      SUBROUTINE DPCMP2(Y1,Y2,Y3,PLABID,NZ,ICASPL,NUMV2,MAXNXT,
     1                  DAT,DX,T,W,
     1                  AMEAN,ASD,N,
     1                  AMEANF,ASDF,NFULL,
     1                  XTEMP1,XTEMP2,XTEMP3,XTEMP4,ITEMP1,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  XPLOT,YPLOT,WTS,NPLOT,
