            WRITE(ICOUT,82)I,Y1(I),Y2(I),Y3(I),PLABID(I)
   82       FORMAT('I,Y1(I),Y2(I),Y3(I),PLABID(I) = ',I8,4G15.7)
   81     CONTINUE
