          ELSEIF(ICMPM2.EQ.'MOM '.AND.IA(1:13).EQ.'Mean of Means')THEN
            METHIN(II)=0
          ELSEIF(ICMPM2.EQ.'MEDM'.AND.IA(1:15).EQ.'Median of Means')THEN
            METHIN(II)=0
          ELSEIF(ICMPM2.EQ.'BOB '.AND.IA(1:3).EQ.'Bob')THEN
            METHIN(II)=0
