 4210 CONTINUE
      CALL DPDT11(ALPHSV,NORVAL,NORVAL,LOWLIM,UPPLIM,
     1            ICASA2,ICAPSW,ICAPTY,NUMDIG,
     1            ISUBRO,IBUGA3,IERROR)
C
C
