      EQUIVALENCE (IGARBG(IIGAR3),ITEMP3(1))
      EQUIVALENCE (IGARBG(IIGAR4),ITEMP4(1))
      EQUIVALENCE (IGARBG(IIGAR5),ITEMP5(1))
      EQUIVALENCE (IGARBG(IIGAR6),ITEMP6(1))
C
      EQUIVALENCE (DGARBG(IDGAR1),DTEMP1(1))
      EQUIVALENCE (DGARBG(IDGAR2),DTEMP2(1))
      EQUIVALENCE (DGARBG(IDGAR3),DTEMP3(1))
C
CCCCC END CHANGE
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOSU.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCODA.INC'
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      IERROR='NO'
      ISUBN1='DPCR'
      ISUBN2='TA  '
C
      IYNAM=' '
      IXNAM=' '
      IXNAM2=' '
      IX1NAM=' '
      IX2NAM=' '
      IX3NAM=' '
      IX4NAM=' '
      IX5NAM=' '
      IX6NAM=' '
      IX7NAM=' '
      IX8NAM=' '
C
      MAXCP1=MAXCOL+1
      MAXCP2=MAXCOL+2
      MAXCP3=MAXCOL+3
      MAXCP4=MAXCOL+4
      MAXCP5=MAXCOL+5
      MAXCP6=MAXCOL+6
C
      MINN2=1
C
C               ******************************************
C               **  TREAT THE CROSS-TABULATION    CASE  **
C               ******************************************
C
      IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'CRTA')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPCRTA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,53)ICASCT,IBUGA2,IBUGA3,IBUGQ
   53   FORMAT('ICASCT,IBUGA2,IBUGA3,IBUGQ = ',3(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ***************************
C               **  STEP 1--             **
C               **  EXTRACT THE COMMAND  **
C               ***************************
C
      ISTEPN='1'
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'CRTA')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C               *******************************************************
C               **  STEP 1.5--                                       **
C               **  SEARCH FOR CROSS-TABULATE CHI-SQUARE             **
C               *******************************************************
C
      ICASCT='CSCT'
      IYVAR='ON'
      IXVAR='OFF'
      IX2VAR='OFF'
      IFLAGL='OFF'
C
      IF(ICOM.EQ.'CROS' .AND. IHARG(1).EQ.'TABU')THEN
        JMIN=2
        IF(IHARG(2).EQ.'LIMI')THEN
          JMIN=3
          IFLAGL='ON'
        ENDIF
      ELSEIF(ICOM.EQ.'TABU')THEN
        JMIN=1
        IF(IHARG(1).EQ.'LIMI')THEN
          JMIN=2
          IFLAGL='ON'
        ENDIF
      ELSE
        IFOUND='NO'
        GOTO9000
      ENDIF
      JMAX=MIN(NUMARG,JMIN+6)
C
CCCCC MARCH 2009: USE "EXTSTA" TO PARSE.  NOTE THAT IF NO
CCCCC             STATISTIC IS GIVEN, WE ASSUME THE "COUNTS"
CCCCC             CASE.
C
      CALL EXTSTA(ICOM,ICOM2,IHARG,IHARG2,IARGT,ARG,NUMARG,
     1            JMIN,JMAX,
     1            ICASCT,ISTANM,ISTANR,ISTADF,ISTARA,
     1            IFOUND,ILOCV,ISUBRO,IBUGA3,IERROR)
C
      IF(IFOUND.EQ.'YES')THEN
        ICTNAM(1:40)=ISTANM(1:40)
        IYVAR='ON'
        IXVAR='OFF'
        IX2VAR='OFF'
        IF(ISTANR.GE.2)IXVAR='ON'
        IF(ISTANR.GE.3)IX2VAR='ON'
        IF(ICASCT.EQ.'NUMB')THEN
          IYVAR='OFF'
          ISTANR=0
        ENDIF
      ELSE
        ICASCT='NUMB'
        ICTNAM='NUMBER'
        IYVAR='OFF'
        IXVAR='OFF'
        IX2VAR='OFF'
        ISTANR=0
        IFOUND='YES'
        ILOCV=JMIN
      ENDIF
C
      ILASTC=ILOCV-1
      IF(ILASTC.GE.1)THEN
        CALL ADJUST(ILASTC,IHARG,IHARG2,IARG,ARG,IARGT,NUMARG)
      ENDIF
C
C               ****************************************
C               **  STEP 2--                          **
C               **  EXTRACT THE VARIABLE LIST         **
C               ****************************************
C
      ISTEPN='2'
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'CRTA')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      INAME='CROSS TABULATE'
      MINNA=1
      MAXNA=100
      MINN2=1
      IFLAGE=1
      IFLAGM=0
      IFLAGP=0
      JMIN=1
      JMAX=NUMARG
      MINNVA=1
      MAXNVA=8 + ISTANR
C
      CALL DPPARS(IHARG,IHARG2,IARGT,ARG,NUMARG,IANS,IWIDTH,
     1            IHNAME,IHNAM2,IUSE,NUMNAM,IN,IVALUE,VALUE,
     1            JMIN,JMAX,
     1            MINN2,MINNA,MAXNA,MAXSPN,IFLAGE,INAME,
     1            IVARN1,IVARN2,IVARTY,PVAR,
     1            ILIS,NRIGHT,ICOLR,ISUB,NQ,ILOCQ,NUMVAR,
     1            MINNVA,MAXNVA,
     1            IFLAGM,IFLAGP,
     1            IBUGA3,IBUGQ,ISUBRO,IFOUND,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'CRTA')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,281)
  281   FORMAT('***** AFTER CALL DPPARS--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,282)NQ,NUMVAR
  282   FORMAT('NQ,NUMVAR = ',2I8)
        CALL DPWRST('XXX','BUG ')
        IF(NUMVAR.GT.0)THEN
          DO285I=1,NUMVAR
            WRITE(ICOUT,287)I,IVARN1(I),IVARN2(I),ILIS(I),NRIGHT(I),
     1                      ICOLR(I)
  287       FORMAT('I,IVARN1(I),IVARN2(I),ILIS(I),NRIGHT(I),',
     1             'ICOLR(I) = ',I8,2X,A4,A4,2X,3I8)
            CALL DPWRST('XXX','BUG ')
  285     CONTINUE
        ENDIF
      ENDIF
C
C               ****************************************
C               **  STEP 3--                          **
C               **  EXTRACT THE DATA                  **
C               ****************************************
C
      NRESP=ISTANR
      NCRTV=NUMVAR-NRESP
      IF(NCRTV.LT.1 .OR. NCRTV.GT.8)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,301)
  301   FORMAT('****** ERROR IN CROSS TABULATE--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,302)
  302   FORMAT('       THE NUMBER OF GROUP-ID VARIABLES IS LESS THAN')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,303)
  303   FORMAT('       OR GREATER THAN EIGHT.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,304)NGROUP
  304   FORMAT('       THE NUMBER OF GROUP-ID VARIABLES   = ',I5)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,305)NRESP
  305   FORMAT('       THE NUMBER OF RESPONSE VARIABLES   = ',I5)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      J=0
      DO2660I=1,NRIGHT(1)
        ICOLH=0
        IF(ISUB(I).EQ.0)GOTO2660
        J=J+1
C
        IF(NRESP.GE.1)THEN
          ICOLH=ICOLH+1
          IJ=MAXN*(ICOLR(ICOLH)-1)+I
          IF(ICOLR(ICOLH).LE.MAXCOL)Y1(J)=V(IJ)
          IF(ICOLR(ICOLH).EQ.MAXCP1)Y1(J)=PRED(I)
          IF(ICOLR(ICOLH).EQ.MAXCP2)Y1(J)=RES(I)
          IF(ICOLR(ICOLH).EQ.MAXCP3)Y1(J)=YPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP4)Y1(J)=XPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP5)Y1(J)=X2PLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP6)Y1(J)=TAGPLO(I)
        ELSE
          Y1(J)=0.0
        ENDIF
C
        IF(NRESP.GE.2)THEN
          ICOLH=ICOLH+1
          IJ=MAXN*(ICOLR(ICOLH)-1)+I
          IF(ICOLR(ICOLH).LE.MAXCOL)Z1(J)=V(IJ)
          IF(ICOLR(ICOLH).EQ.MAXCP1)Z1(J)=PRED(I)
          IF(ICOLR(ICOLH).EQ.MAXCP2)Z1(J)=RES(I)
          IF(ICOLR(ICOLH).EQ.MAXCP3)Z1(J)=YPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP4)Z1(J)=XPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP5)Z1(J)=X2PLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP6)Z1(J)=TAGPLO(I)
        ELSE
          Z1(J)=0.0
        ENDIF
C
        IF(NRESP.GE.3)THEN
          ICOLH=ICOLH+1
          IJ=MAXN*(ICOLR(ICOLH)-1)+I
          IF(ICOLR(ICOLH).LE.MAXCOL)Z2(J)=V(IJ)
          IF(ICOLR(ICOLH).EQ.MAXCP1)Z2(J)=PRED(I)
          IF(ICOLR(ICOLH).EQ.MAXCP2)Z2(J)=RES(I)
          IF(ICOLR(ICOLH).EQ.MAXCP3)Z2(J)=YPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP4)Z2(J)=XPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP5)Z2(J)=X2PLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP6)Z2(J)=TAGPLO(I)
        ELSE
          Z2(J)=0.0
        ENDIF
C
        DO2670K=1,NCRTV
          ICOLH=ICOLH+1
          IJ=MAXN*(ICOLR(ICOLH)-1)+I
          IF(ICOLR(ICOLH).LE.MAXCOL)XDESGN(J,K)=V(IJ)
          IF(ICOLR(ICOLH).EQ.MAXCP1)XDESGN(J,K)=PRED(I)
          IF(ICOLR(ICOLH).EQ.MAXCP2)XDESGN(J,K)=RES(I)
          IF(ICOLR(ICOLH).EQ.MAXCP3)XDESGN(J,K)=YPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP4)XDESGN(J,K)=XPLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP5)XDESGN(J,K)=X2PLOT(I)
          IF(ICOLR(ICOLH).EQ.MAXCP6)XDESGN(J,K)=TAGPLO(I)
 2670   CONTINUE
C
 2660 CONTINUE
      NLOCAL=J
C
      ICNT=0
      IF(NRESP.GE.1)THEN
        ICNT=ICNT+1
        IYNAM(1:4)=IVARN1(ICNT)(1:4)
        IYNAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NRESP.GE.2)THEN
        ICNT=ICNT+1
        IXNAM(1:4)=IVARN1(ICNT)(1:4)
        IXNAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NRESP.GE.3)THEN
        ICNT=ICNT+1
        IXNAM2(1:4)=IVARN1(ICNT)(1:4)
        IXNAM2(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.1)THEN
        ICNT=ICNT+1
        IX1NAM(1:4)=IVARN1(ICNT)(1:4)
        IX1NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.2)THEN
        ICNT=ICNT+1
        IX2NAM(1:4)=IVARN1(ICNT)(1:4)
        IX2NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.3)THEN
        ICNT=ICNT+1
        IX3NAM(1:4)=IVARN1(ICNT)(1:4)
        IX3NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.4)THEN
        ICNT=ICNT+1
        IX4NAM(1:4)=IVARN1(ICNT)(1:4)
        IX4NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.5)THEN
        ICNT=ICNT+1
        IX5NAM(1:4)=IVARN1(ICNT)(1:4)
        IX5NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.6)THEN
        ICNT=ICNT+1
        IX6NAM(1:4)=IVARN1(ICNT)(1:4)
        IX6NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.7)THEN
        ICNT=ICNT+1
        IX7NAM(1:4)=IVARN1(ICNT)(1:4)
        IX7NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
      IF(NCRTV.GE.8)THEN
        ICNT=ICNT+1
        IX8NAM(1:4)=IVARN1(ICNT)(1:4)
        IX8NAM(5:8)=IVARN2(ICNT)(1:4)
      ENDIF
C
      IF(NLOCAL.LE.0)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,301)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2710)
 2710   FORMAT('      AFTER EXTRACTING THE SUBSET, THERE ARE NO')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2712)
 2712   FORMAT('      OBSERVATIONS REMAINING ON WHICH TO PERFORM')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2714)
 2714   FORMAT('      THE RELEVANT CROSS-TABULATION.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
C               *****************************************************
C               **  STEP 8--                                       **
C               **  COMPUTE THE APPROPRIATE CROSS-TABULATION       **
C               **  STATISTIC--                                    **
C               **  (MEAN, STANDARD DEVIATION, RANGE, OR COUNT).   **
C               *****************************************************
C
      ISTEPN='8'
      IF(IBUGA2.EQ.'ON'.OR.ISUBRO.EQ.'CRTA')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL' .OR. ICASCT.EQ.'BRAT')THEN
        IHP='ALPH'
        IHP2='A   '
        IHWUSE='P'
        MESSAG='NO'
        CALL CHECKN(IHP,IHP2,IHWUSE,
     1              IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,
     1              NUMNAM,MAXNAM,
     1              ISUBN1,ISUBN2,MESSAG,IANS,IWIDTH,
     1              ILOCP,IERROR)
        IF(IERROR.EQ.'YES')THEN
          ALPHA=0.95
        ELSE
          ALPHA=VALUE(ILOCP)
          IF(ALPHA.GE.1.0 .AND. ALPHA.LE.100.0)ALPHA=ALPHA/100.
          IF(ALPHA.LE.0.0 .OR. ALPHA.GE.1.0)ALPHA=0.95
          IF(ALPHA.LT.0.5)ALPHA=1.0 - ALPHA
          IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')THEN
            ATEMP=1.0 - ALPHA
            ATEMP=2.0*ATEMP
            ALPHA=1.0 - ATEMP
          ENDIF
        ENDIF
      ELSE
        ALPHA=0.05
      ENDIF
C
      IF(IFLAGL.EQ.'ON')THEN
      ELSE
        CALL DPCRT2(Y1,Z1,Z2,XDESGN,NLOCAL,MAXGR9,
     1              NUMVAR,ICASCT,ICTNAM,ISTARA,
     1              XH1DIS,
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              XNTRIA,XACLOW,XACUPP,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,IQUAME,IQUASE,PSTAMV,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,IX6NAM,
     1              IX7NAM,IX8NAM,
     1              ICAPSW,ICAPTY,IFORSW,NCRTV,MAXOBV,
     1              Y,X,D,DSIZE,DFILL,DCOLOR,DSYMB,
     1              XTEMP6,XTEMP7,NPLOTP,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'CRTA')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRTA--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IFOUND,IERROR,ICASCT
 9012   FORMAT('IFOUND,IERROR,ICASCT = ',2(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NPLOTP,NS,NCRTV
 9013   FORMAT('NPLOTP,NS,ICASCT = ',3I8)
        CALL DPWRST('XXX','BUG ')
        IF(NPLOTP.GT.0)THEN
          DO9015I=1,MIN(200,NPLOTP)
          WRITE(ICOUT,9016)I,Y(I),X(I),D(I)
 9016     FORMAT('I,Y(I),X(I),D(I) = ',I8,3F12.5)
          CALL DPWRST('XXX','BUG ')
 9015     CONTINUE
        ENDIF
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT2(Y,Z,Z2,TAG,N,MAXGRP,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,
     1                  XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,IQUAME,IQUASE,PSTAMV,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,IX6NAM,
     1                  IX7NAM,IX8NAM,
     1                  ICAPSW,ICAPTY,IFORSW,NCRTV,MAXNXT,
     1                  Y2,X2,D2,DSIZE,DFILL,DCOLOR,DSYMB,
     1                  XPLOT,YPLOT,N2,
     1                  ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A PAIR OF COORDINATE VECTORS
C              THAT WILL DEFINE A CROSS-TABULATION
C              OF THE FOLLOWING TYPES--
C                 1) MEAN CROSS-TABULATION;
C                 2) STANDARD DEVIATION CROSS-TABULATION;
C                 3) RANGE CROSS-TABULATION;
C                 4) COUNT CROSS-TABULATION.
C                 5) SUM CROSS-TABULATION`
C                 6) CHI-SQUARE ANALYSIS CROSS_TABULATION
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--82/7
C     ORIGINAL VERSION--JUNE      1978.
C     UPDATED         --OCTOBER   1978.
C     UPDATED         --JANUARY   1981.
C     UPDATED         --DECEMBER  1981.
C     UPDATED         --APRIL     1982.
C     UPDATED         --MAY       1982.
C     UPDATED         --NOVEMBER  1989.  COMMENT OUT CHECK OF NUMSET=N
C     UPDATED         --DECEMBEDR 1989.  FIX CROSS-TAB X1 X2
C     UPDATED         --OCTOBER   1992.  SUPPRESS ERROR MESSAGE FOR
C                                        ZERO COUNT CELLS.
C                                        ADD SUM AND CHI-SQUARE OPTIONS
C     UPDATED         --MARCH     1994.  FIX CROSS TABU SUM CASE
C     UPDATED         --MARCH     1994.  MODIFY CROSS TABU CHI-SQUARE
C                                        OUTPUT
C     UPDATED         --DECEMBER  1998.  WRITE OUTPUT TO FILE
C     UPDATED         --AUGUST    2002.  USE CMPSTA TO COMPUTE THE
C                                        STATISTICS
C     UPDATED         --AUGUST    2002.  GREATLY EXPAND LIST OF
C                                        SUPPORTED STATISICS
C     UPDATED         --AUGUST    2002.  SUPPORT FOR HTML OUTPUT
C     UPDATED         --APRIL     2003.  ADD SN AND QN (AND DIFFERENCE
C                                        OF), REQUIRED ADDITIONAL
C                                        SCRATCH ARAYS
C     UPDATED         --OCTOBER   2003.  SUPPORT FOR LATEX OUTPUT
C     UPDATED         --APRIL     2008.  SUPPORT FOR RTF OUTPUT
C     UPDATED         --APRIL     2008.  SUPPORT FOR 3, 4, 5, OR 6
C                                        CROSS-TABULATION VARIABLES
C     UPDATED         --APRIL     2008.  "BINOMIAL PROBABILITY" OPTION
C                                        (THIS RECIEVES SPECIAL
C                                        HANDLING)
C     UPDATED         --AUGUST    2008.  FIXED SOME BUGS
C     UPDATED         --JUNE      2010.  CALL LIST TO CMPSTA
C     UPDATED         --MAY       2014.  SUPPORT UP TO 8 CROSS-TAB
C                                        VARIABLES
C     UPDATED         --AUGUST    2023.  CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*40 ICTNAM
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 IQUAME
      CHARACTER*4 IQUASE
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
      CHARACTER*8 IX3NAM
      CHARACTER*8 IX4NAM
      CHARACTER*8 IX5NAM
      CHARACTER*8 IX6NAM
      CHARACTER*8 IX7NAM
      CHARACTER*8 IX8NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(MAXNXT,MAXGRP)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
      DIMENSION DSIZE(*)
      DIMENSION DFILL(*)
      DIMENSION DCOLOR(*)
      DIMENSION DSYMB(*)
      DIMENSION XPLOT(*)
      DIMENSION YPLOT(*)
C
      DIMENSION TAG(MAXNXT,MAXGRP)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      INTEGER NUMSET(8)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T2  '
C
      I2=0
C
      AN=0.0
      YUPPER=0.0
      YLOWER=0.0
C
C     CHECK THE INPUT ARGUMENTS FOR ERRORS
C
      IF(N.LT.1)THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,31)
   31   FORMAT('***** ERROR IN CROSS-TABULATE--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,32)
   32   FORMAT('      THE NUMBER OF OBSERVATIONS MUST BE AT LEAST 1.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,34)N
   34   FORMAT('      THE ENTERED NUMBER OF OBSERVATIONS HERE = ',I6)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
CCCCC MAY 2008: DO NOT TREAT FOLLOWING AS AN ERROR.
CCCCC           PRINT A WARNING, BUT CONTINUE TO PROCESS.
CCCCC
CCCCC           WHEN CROSS-TABBING MULTIPLE VARIABLES, CAN
CCCCC           GET A LOT OF ERROR MESSAGES DUE TO CLASSES
CCCCC           WITH SMALL NUMBER OF ELEMENTS.  SO DO NOT
CCCCC           PRINT WARNING MESSAGE.
C
CCCCC IF(IYVAR.EQ.'ON')THEN
CCCCC   HOLD=Y(1)
CCCCC   DO60I=1,N
CCCCC     IF(Y(I).NE.HOLD)GOTO69
CCC60   CONTINUE
CCCCC   WRITE(ICOUT,999)
CCCCC   CALL DPWRST('XXX','BUG ')
CCCCC   WRITE(ICOUT,61)
CCC61   FORMAT('***** WARNING IN CROSS-TABULATE--')
CCCCC   CALL DPWRST('XXX','BUG ')
CCCCC   WRITE(ICOUT,62)
CCC62   FORMAT('      ALL RESPONSE VARIABLE ELEMENTS')
CCCCC   CALL DPWRST('XXX','BUG ')
CCCCC   WRITE(ICOUT,63)HOLD
CCC63   FORMAT('      ARE IDENTICALLY EQUAL TO ',G15.7)
CCCCC   CALL DPWRST('XXX','BUG ')
CCCCC   WRITE(ICOUT,999)
CCCCC   CALL DPWRST('XXX','BUG ')
CCC69   CONTINUE
CCCCC ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT2')THEN
        WRITE(ICOUT,70)
   70   FORMAT('AT THE BEGINNING OF DPCRT2--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,71)N,ICASCT,NUMV2,NCRTV
   71   FORMAT('N,ICASCT,NUMV2,NCRTV = ',I8,2X,A4,I8,2X,I5)
        CALL DPWRST('XXX','BUG ')
        DO72I=1,N
          WRITE(ICOUT,73)I,Y(I),Z(I),Z2(I),(TAG(I,J),J=1,MAXGRP)
   73     FORMAT('I,Y(I),Z(I),Z2(I),TAG1-6(I) = ',I8,12F10.3)
          CALL DPWRST('XXX','BUG ')
   72   CONTINUE
        WRITE(ICOUT,74)IQUAME,IQUASE,PSTAMV
   74   FORMAT('IQUAME,IQUASE,PSTAMV = ',2(A4,2X),G15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************************************************
C               **  STEP 1--                                        **
C               **  DETERMINE THE NUMBER OF DISTINCT VALUES         **
C               **  FOR THE GROUP VARIABLES (TAG1, TAG2)            **
C               **  IF ALL VALUES ARE DISTINCT, THEN THIS           **
C               **  IMPLIES WE HAVE THE NO REPLICATION CASE         **
C               **  WHICH IS AN ERROR CONDITION FOR A PLOT.         **
C               ******************************************************
C
      ISTEPN='1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      DO100J=1,MAXGRP
        NUMSET(J)=0
  100 CONTINUE
C
      DO110J=1,NCRTV
        CALL DISTIN(TAG(1,J),N,IWRITE,XIDTEM(1,J),NUMSET(J),
     1              IBUGA3,IERROR)
        CALL SORT(XIDTEM(1,J),NUMSET(J),TEMP)
        DO120I=1,NUMSET(J)
          XIDTEM(I,J)=TEMP(I)
  120   CONTINUE
        IF(NUMSET(J).LT.1 .OR. NUMSET(J).GT.N)THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,31)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,111)J,NUMSET(J)
  111     FORMAT('      THE NUMBER OF SETS FOR THE GROUP ',I1,
     1           ' VARIABLE, ',I8,',')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,113)
  113     FORMAT('      IS EITHER LESS THAN ONE OR GREATER THAN THE ',
     1           'NUMBER')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,115)N
  115     FORMAT('      OF OBSERVATIONS, ',I8,'.')
          CALL DPWRST('XXX','BUG ')
          IERROR='YES'
          GOTO9000
        ENDIF
  110 CONTINUE
C
      AN=N
      ANUMS1=NUMSET(1)
      ANUMS2=NUMSET(2)
      ANUMS3=NUMSET(3)
      ANUMS4=NUMSET(4)
      ANUMS5=NUMSET(5)
      ANUMS6=NUMSET(6)
      ANUMS7=NUMSET(7)
      ANUMS8=NUMSET(8)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IWRITE='OFF'
C
      IF(NCRTV.EQ.1)THEN
        CALL DPCRT0(Y,Z,Z2,TAG,N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),
     1              NUMSET(1),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,
     1              XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,IX1NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,N2,ISUBRO,IBUGA3,IERROR)
      ELSEIF(NCRTV.EQ.2)THEN
        CALL DPCRT3(Y,Z,Z2,TAG(1,1),TAG(1,2),N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),XIDTEM(1,2),
     1              NUMSET(1),NUMSET(2),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,D2,N2,ISUBRO,IBUGA3,IERROR)
      ELSEIF(NCRTV.EQ.3)THEN
        CALL DPCRT4(Y,Z,Z2,TAG(1,1),TAG(1,2),TAG(1,3),N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),XIDTEM(1,2),XIDTEM(1,3),
     1              NUMSET(1),NUMSET(2),NUMSET(3),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,IX3NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,D2,DSIZE,N2,ISUBRO,IBUGA3,IERROR)
      ELSEIF(NCRTV.EQ.4)THEN
        CALL DPCRT5(Y,Z,Z2,TAG(1,1),TAG(1,2),TAG(1,3),TAG(1,4),N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),XIDTEM(1,2),XIDTEM(1,3),XIDTEM(1,4),
     1              NUMSET(1),NUMSET(2),NUMSET(3),NUMSET(4),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,IX3NAM,IX4NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,D2,DSIZE,DCOLOR,N2,ISUBRO,IBUGA3,IERROR)
      ELSEIF(NCRTV.EQ.5)THEN
        CALL DPCRT6(Y,Z,Z2,TAG(1,1),TAG(1,2),TAG(1,3),
     1              TAG(1,4),TAG(1,5),N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),XIDTEM(1,2),XIDTEM(1,3),
     1              XIDTEM(1,4),XIDTEM(1,5),
     1              NUMSET(1),NUMSET(2),NUMSET(3),
     1              NUMSET(4),NUMSET(5),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,D2,DSIZE,DCOLOR,DFILL,N2,
     1              ISUBRO,IBUGA3,IERROR)
      ELSEIF(NCRTV.EQ.6)THEN
        CALL DPCRT7(Y,Z,Z2,TAG(1,1),TAG(1,2),TAG(1,3),TAG(1,4),
     1              TAG(1,5),TAG(1,6),N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),XIDTEM(1,2),XIDTEM(1,3),
     1              XIDTEM(1,4),XIDTEM(1,5),XIDTEM(1,6),
     1              NUMSET(1),NUMSET(2),NUMSET(3),
     1              NUMSET(4),NUMSET(5),NUMSET(6),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,
     1              IX6NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,D2,DSIZE,DCOLOR,DFILL,DSYMB,N2,
     1              ISUBRO,IBUGA3,IERROR)
      ELSEIF(NCRTV.EQ.7)THEN
        CALL DPCRT8(Y,Z,Z2,TAG(1,1),TAG(1,2),TAG(1,3),TAG(1,4),
     1              TAG(1,5),TAG(1,6),TAG(1,7),N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),XIDTEM(1,2),XIDTEM(1,3),
     1              XIDTEM(1,4),XIDTEM(1,5),XIDTEM(1,6),
     1              XIDTEM(1,7),
     1              NUMSET(1),NUMSET(2),NUMSET(3),NUMSET(4),
     1              NUMSET(5),NUMSET(6),NUMSET(7),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,
     1              IX6NAM,IX7NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,D2,DSIZE,DCOLOR,DFILL,DSYMB,XPLOT,N2,
     1              ISUBRO,IBUGA3,IERROR)
      ELSEIF(NCRTV.EQ.8)THEN
        CALL DPCRT9(Y,Z,Z2,TAG(1,1),TAG(1,2),TAG(1,3),TAG(1,4),
     1              TAG(1,5),TAG(1,6),TAG(1,7),TAG(1,8),N,
     1              NUMV2,ICASCT,ICTNAM,ISTARA,
     1              XIDTEM(1,1),XIDTEM(1,2),XIDTEM(1,3),
     1              XIDTEM(1,4),XIDTEM(1,5),XIDTEM(1,6),
     1              XIDTEM(1,7),XIDTEM(1,8),
     1              NUMSET(1),NUMSET(2),NUMSET(3),NUMSET(4),
     1              NUMSET(5),NUMSET(6),NUMSET(7),NUMSET(8),
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1              ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
     1              ISEED,ALPHA,
     1              IXVAR,IX2VAR,IYVAR,
     1              IYNAM,IXNAM,IXNAM2,
     1              IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,
     1              IX6NAM,IX7NAM,IX8NAM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              MAXNXT,
     1              Y2,X2,D2,DSIZE,DCOLOR,DFILL,DSYMB,XPLOT,YPLOT,N2,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT2')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT2--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,N2,NUMV2,IERROR
 9012   FORMAT('ICASCT,N,N2,NUMV2,IERROR = ',A4,3I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMSET(1),NUMSET(2),N2
 9015   FORMAT('NUMSET(1),NUMSET(2),N2 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2
 9016   FORMAT('ANUMS1,ANUMS2 = ',2E15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I)
 9021     FORMAT('I,Y2(I),X2(I) = ',I8,2E15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT3(Y,Z,Z2,TAG1,TAG2,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,XIDTE2,
     1                  NUMSE1,NUMSE2,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,D2,N2,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A TWO-WAY CROSS-TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/4
C     ORIGINAL VERSION--APRIL     2008. SPLIT OFF FROM DPCRT2 ROUTINE
C     UPDATED         --SEPTEMBER 2008. ACCOMODATE "MISSING" DATA
C     UPDATED         --JANUARY   2010. TREAT BINOMIAL RATIO IN A
C                                       SIMILAR FASHION TO BINOMIAL
C                                       PROBABILITY
C     UPDATED         --JUNE      2010. CALL LIST TO CMPSTA
C     UPDATED         --JUNE      2010. USE DPDTA5 TO PRINT TABLE
C     UPDATED         --AUGUST    2023. CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*40 ICTNAM
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=7)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=30)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 IBFLAG
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION XIDTE2(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
C
      DIMENSION TAG1(*)
      DIMENSION TAG2(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T3  '
C
      I2=0
C
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
      ANUMS2=INT(NUMSE2+0.01)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT3')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
C
      EPS=0.1E-7
      J=0
      NRESP=NUMV2-2
      DO1110ISET1=1,NUMSE1
        DO1120ISET2=1,NUMSE2
C
          K=0
          NTEMP2=0
          DO1130I=1,N
            IF(XIDTEM(ISET1).EQ.TAG1(I).AND.XIDTE2(ISET2).EQ.TAG2(I))
     1        GOTO1131
            GOTO1130
 1131       CONTINUE
C
            NTEMP2=NTEMP2+1
            IF(IYVAR.EQ.'OFF')THEN
              K=K+1
              TEMP(K)=0.0
            ELSE
              IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
                K=K+1
                TEMP(K)=Y(I)
                IF(IXVAR.EQ.'ON')TEMPZ(K)=Z(I)
                IF(IX2VAR.EQ.'ON')TEMPZ2(K)=Z2(I)
              ENDIF
            ENDIF
 1130     CONTINUE
          NTEMP=K
C
C         AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C         NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C         NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
          IF(NTEMP2.EQ.0)GOTO1129
C
          IF(NTEMP.EQ.0)THEN
            IF(ICTAMV.EQ.'ZERO')THEN
              STAT=0.0
              IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1           ICASCT.EQ.'MDCL')THEN
                NTRIAL=0
                ALOWLM=0.0
                AUPPLM=0.0
              ENDIF
            ELSEIF(ICTAMV.EQ.'MV  ')THEN
              STAT=PCTAMV
              IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1           ICASCT.EQ.'MDCL')THEN
                NTRIAL=0
                ALOWLM=PCTAMV
                AUPPLM=PCTAMV
              ENDIF
            ELSE
              GOTO1129
            ENDIF
          ELSE
            CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
            IF(IERROR.EQ.'YES')GOTO9000
            IF(IBFLAG.EQ.'BPRO')THEN
              PTEMP=STAT
              NTRIAL=NTEMP
              IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
              IF(STAT.EQ.PSTAMV)THEN
                ALOWLM=PSTAMV
                AUPPLM=PSTAMV
              ELSE
                ALPHAT=ALPHA
                CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                      ALOWLM,AUPPLM,IBUGA3,IERROR)
              ENDIF
            ELSEIF(ICASCT.EQ.'MECL')THEN
              XMEAN=STAT
              NTRIAL=NTEMP
              IF(STAT.EQ.PSTAMV)THEN
                ALOWLM=PSTAMV
                AUPPLM=PSTAMV
              ELSE
                CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
                ALPHAT=ALPHA
                CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                      ALOWLM,AUPPLM,IBUGA3,IERROR)
              ENDIF
            ELSEIF(ICASCT.EQ.'MDCL')THEN
              XMED=STAT
              NTRIAL=NTEMP
              IF(STAT.EQ.PSTAMV)THEN
                ALOWLM=PSTAMV
                AUPPLM=PSTAMV
              ELSE
                XQ=0.5
                CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                      QUASE,IBUGA3,IERROR)
                ALPHAT=ALPHA
                CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                      ALOWLM,AUPPLM,IBUGA3,IERROR)
              ENDIF
            ENDIF
          ENDIF
C
          J=J+1
          Y2(J)=STAT
          X2(J)=XIDTEM(ISET1)
          D2(J)=XIDTE2(ISET2)
          IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1       ICASCT.EQ.'MDCL')THEN
            XNTRIA(J)=REAL(NTRIAL)
            XACLOW(J)=ALOWLM
            XACUPP(J)=AUPPLM
          ENDIF
C
 1129   CONTINUE
          IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT3')THEN
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,1140)ISET1,ISET2,NTEMP,NTEMP2,STAT
 1140       FORMAT('DPCRT3: ISET1,ISET2,NTEMP,NTEMP2,STAT = ',
     1             2I5,2I8,G15.7)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,1141)XIDTEM(ISET1),XIDTE2(ISET2)
 1141       FORMAT('XIDTEM(ISET1),XIDTE2(ISET2) = ',2G15.7)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,1142)J,Y2(J),X2(J),D2(J)
 1142       FORMAT('J,Y2(J),X2(J),D2(J) = ',I8,3G15.7)
            CALL DPWRST('XXX','BUG ')
          ENDIF
C
 1120   CONTINUE
 1110 CONTINUE
      N2=J
C
      IF(ICASCT.EQ.'BRAT')THEN
        IBFLAG='BRAT'
        ICASCT='BPRO'
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1   GROUP-ID 2           ',A40)
      IF(ICASCT.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),Y2(I),XNTRIA(I),XACLOW(I)
 2171       FORMAT(5E17.9)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),D2(I),Y2(I),XNTRIA(I),
     1                        XACLOW(I),XACUPP(I)
 2173       FORMAT(6E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),D2(I),Y2(I),XNTRIA(I),
     1                      XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),D2(I),Y2(I)
 2161     FORMAT(3E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT3')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)=' '
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)(1:8)=IX2NAM
      NCTIT2(1,2)=8
      ITITL2(1,3)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,3)='  $|$  '
      ENDIF
      NCTIT2(1,3)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=7
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=6
        ITITL2(1,4)='P'
        NCTIT2(1,4)=1
        ITITL2(1,5)='N'
        NCTIT2(1,5)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,6)(1:40)='Lower AC'
          NCTIT2(1,6)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,6)(1:40)='Upper AC'
          NCTIT2(1,6)=8
        ELSE
          ITITL2(1,6)(1:40)='Lower AC'
          NCTIT2(1,6)=8
          ITITL2(1,7)(1:40)='Upper AC'
          NCTIT2(1,7)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=7
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,4)='Mean'
          NCTIT2(1,4)=4
        ELSE
          ITITL2(1,4)='Median'
          NCTIT2(1,4)=6
        ENDIF
        ITITL2(1,5)='N'
        NCTIT2(1,5)=1
        ITITL2(1,6)='Lower Limit'
        ITITL2(1,7)='Upper Limit'
        NCTIT2(1,6)=11
        NCTIT2(1,7)=11
      ELSE
        NUMCOL=4
        ITITL2(1,4)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,4)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,4)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.5)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.3)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
        ENDIF
 4210 CONTINUE
C
      IWHTML(1)=125
      IWHTML(2)=125
      IWHTML(3)=25
      IWHTML(4)=125
      IWHTML(5)=75
      IWHTML(6)=125
      IWHTML(7)=125
      IJUNK=1400
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+IJUNK
      IWRTF(3)=IWRTF(2)+200
      IWRTF(4)=IWRTF(3)+IJUNK
      IWRTF(5)=IWRTF(4)+800
      IWRTF(6)=IWRTF(5)+IJUNK
      IWRTF(7)=IWRTF(6)+IJUNK
      IFRST=.TRUE.
      IFLAGS=.TRUE.
      ILAST=.TRUE.
      IFLAGE=.FALSE.
C
      ICALL=0
      ICNT=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=D2(I)
        AMAT(ICNT,3)=0.0
        AMAT(ICNT,4)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,5)=XNTRIA(I)
          AMAT(ICNT,6)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,5)=XNTRIA(I)
          AMAT(ICNT,6)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,5)=XNTRIA(I)
          AMAT(ICNT,6)=XACLOW(I)
          AMAT(ICNT,7)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,5)=XNTRIA(I)
          AMAT(ICNT,6)=XACLOW(I)
          AMAT(ICNT,7)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.3)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
C
      IF(IBFLAG.EQ.'BRAT')THEN
        ICASCT='BRAT'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT3')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT3--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,NUMSE1,N2,IERROR
 9012   FORMAT('ICASCT,N,NUMSE1,N2,IERROR = ',A4,3I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMV2,NUMSE1,NUMSE2,N2
 9015   FORMAT('NUMV2,NUMSE1,NUMSE2,N2 = ',4I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2
 9016   FORMAT('ANUMS1,ANUMS2 = ',2E15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I),D2(I)
 9021     FORMAT('I,Y2(I),X2(I),D2(I) = ',I8,3G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT4(Y,Z,Z2,TAG1,TAG2,TAG3,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,XIDTE2,XIDTE3,
     1                  NUMSE1,NUMSE2,NUMSE3,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,IX3NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,D2,DSIZE,N2,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A THREE-WAY CROSS-TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/4
C     ORIGINAL VERSION--APRIL     2008.
C     UPDATED         --SEPTEMBER 2008. ACCOMODATE "MISSING" DATA
C     UPDATED         --JANUARY   2010. TREAT BINOMIAL RATIO IN A
C                                       SIMILAR FASHION TO BINOMIAL
C     UPDATED         --JUNE      2010. CALL LIST TO CMPSTA
C     UPDATED         --JUNE      2010. PRINT TABLES USING DPDTA5
C     UPDATED         --AUGUST    2023. CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*40 ICTNAM
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=8)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=30)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
      CHARACTER*8 IX3NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IBFLAG
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION XIDTE2(*)
      DIMENSION XIDTE3(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
      DIMENSION DSIZE(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      DIMENSION TAG1(*)
      DIMENSION TAG2(*)
      DIMENSION TAG3(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T4  '
C
      I2=0
C
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
      ANUMS2=INT(NUMSE2+0.01)
      ANUMS3=INT(NUMSE3+0.01)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT4')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
C
      EPS=0.1E-7
      J=0
      NRESP=NUMV2-3
      DO1110ISET1=1,NUMSE1
        DO1120ISET2=1,NUMSE2
          DO1130ISET3=1,NUMSE3
C
            K=0
            NTEMP2=0
            DO1140I=1,N
              IF(XIDTEM(ISET1).EQ.TAG1(I).AND.XIDTE2(ISET2).EQ.TAG2(I)
     1           .AND.XIDTE3(ISET3).EQ.TAG3(I))
     1           GOTO1141
              GOTO1140
 1141         CONTINUE
C
              NTEMP2=NTEMP2+1
              IF(IYVAR.EQ.'OFF')THEN
                K=K+1
                TEMP(K)=0.0
              ELSE
                IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
                  K=K+1
                  TEMP(K)=Y(I)
                  IF(IXVAR.EQ.'ON')TEMPZ(K)=Z(I)
                  IF(IX2VAR.EQ.'ON')TEMPZ2(K)=Z2(I)
                ENDIF
              ENDIF
 1140       CONTINUE
            NTEMP=K
C
C           AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C         NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C         NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
          IF(NTEMP2.EQ.0)GOTO1130
C
            IF(NTEMP.EQ.0)THEN
              IF(ICTAMV.EQ.'ZERO')THEN
                STAT=0.0
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=0.0
                  AUPPLM=0.0
                ENDIF
              ELSEIF(ICTAMV.EQ.'MV  ')THEN
                STAT=PCTAMV
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=PCTAMV
                  AUPPLM=PCTAMV
                ENDIF
              ELSE
                GOTO1130
              ENDIF
            ELSE
              CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
              IF(IERROR.EQ.'YES')GOTO9000
              IF(IBFLAG.EQ.'BPRO')THEN
                PTEMP=STAT
                NTRIAL=NTEMP
                IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  ALPHAT=ALPHA
                  CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MECL')THEN
                XMEAN=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MDCL')THEN
                XMED=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  XQ=0.5
                  CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                        QUASE,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ENDIF
            ENDIF
C
            J=J+1
            Y2(J)=STAT
            X2(J)=XIDTEM(ISET1)
            D2(J)=XIDTE2(ISET2)
            DSIZE(J)=XIDTE3(ISET3)
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              XNTRIA(J)=REAL(NTRIAL)
              XACLOW(J)=ALOWLM
              XACUPP(J)=AUPPLM
            ENDIF
C
 1130     CONTINUE
 1120   CONTINUE
 1110 CONTINUE
      N2=J
C
      IF(ICASCT.EQ.'BRAT')THEN
        IBFLAG='BRAT'
        ICASCT='BPRO'
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1   GROUP-ID 2   GROUP-ID 3           ',A40)
      IF(ICASCT.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),
     1                        Y2(I),XNTRIA(I),XACLOW(I)
 2171       FORMAT(6E17.8)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),
     1                        Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),
     1                        Y2(I),XNTRIA(I),
     1                        XACLOW(I),XACUPP(I)
 2173       FORMAT(7E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),Y2(I),XNTRIA(I),
     1                      XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),D2(I),DSIZE(I),Y2(I)
 2161     FORMAT(4E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT4')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)=' '
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)(1:8)=IX2NAM
      NCTIT2(1,2)=8
      ITITL2(1,3)(1:8)=IX3NAM
      NCTIT2(1,3)=8
      ITITL2(1,4)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,4)='  $|$  '
      ENDIF
      NCTIT2(1,4)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=8
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=7
        ITITL2(1,5)='P'
        NCTIT2(1,5)=1
        ITITL2(1,6)='N'
        NCTIT2(1,6)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,7)(1:40)='Lower AC'
          NCTIT2(1,7)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,7)(1:40)='Upper AC'
          NCTIT2(1,7)=8
        ELSE
          ITITL2(1,7)(1:40)='Lower AC'
          NCTIT2(1,7)=8
          ITITL2(1,8)(1:40)='Upper AC'
          NCTIT2(1,8)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=8
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,5)='Mean'
          NCTIT2(1,5)=4
        ELSE
          ITITL2(1,5)='Median'
          NCTIT2(1,5)=6
        ENDIF
        ITITL2(1,6)='N'
        NCTIT2(1,6)=1
        ITITL2(1,7)='Lower Limit'
        ITITL2(1,7)='Upper Limit'
        NCTIT2(1,8)=11
        NCTIT2(1,8)=11
      ELSE
        NUMCOL=5
        ITITL2(1,5)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,5)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,5)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.6)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.4)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
CCCCC   ELSEIF(I.EQ.5)THEN
CCCCC     IF(ICASCT.NE.'BPRO' .AND. ICASCT.NE.'MECL' .AND.
CCCCC1       ICASCT.NE.'MDCL')THEN
CCCCC        ALIGN(I)='l'
CCCCC     ENDIF
        ENDIF
 4210 CONTINUE
C
      IWHTML(1)=125
      IWHTML(2)=125
      IWHTML(3)=125
      IWHTML(4)=25
      IWHTML(5)=125
      IWHTML(6)=75
      IWHTML(7)=125
      IWHTML(8)=125
      IJUNK=1400
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+IJUNK
      IWRTF(3)=IWRTF(2)+IJUNK
      IWRTF(4)=IWRTF(3)+200
      IWRTF(5)=IWRTF(4)+IJUNK
      IWRTF(6)=IWRTF(5)+800
      IWRTF(7)=IWRTF(6)+IJUNK
      IWRTF(8)=IWRTF(7)+IJUNK
      IFRST=.TRUE.
      ILAST=.TRUE.
      IFLAGS=.TRUE.
      IFLAGE=.FALSE.
C
      ICNT=0
      ICALL=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=D2(I)
        AMAT(ICNT,3)=DSIZE(I)
        AMAT(ICNT,4)=0.0
        AMAT(ICNT,5)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,6)=XNTRIA(I)
          AMAT(ICNT,7)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,6)=XNTRIA(I)
          AMAT(ICNT,7)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,6)=XNTRIA(I)
          AMAT(ICNT,7)=XACLOW(I)
          AMAT(ICNT,8)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,6)=XNTRIA(I)
          AMAT(ICNT,7)=XACLOW(I)
          AMAT(ICNT,8)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.4)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
C
      IF(IBFLAG.EQ.'BRAT')THEN
        ICASCT='BRAT'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT4')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT4--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,N2,IERROR
 9012   FORMAT('ICASCT,N,N2,IERROR = ',A4,2I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMV2
 9013   FORMAT('NUMV2 = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMSE1,NUMSE2,NUMSE3
 9015   FORMAT('NUMSE1,NUMSE2,NUMSE3 = ',3I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2,ANUMS3
 9016   FORMAT('ANUMS1,ANUMS2,ANUMS3 = ',3E15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I),DSIZE(I),D2(I)
 9021     FORMAT('I,Y2(I),X2(I),DSIZE(I),D2(I) = ',I8,4G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT5(Y,Z,Z2,TAG1,TAG2,TAG3,TAG4,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,XIDTE2,XIDTE3,XIDTE4,
     1                  NUMSE1,NUMSE2,NUMSE3,NUMSE4,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,IX3NAM,IX4NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,D2,DSIZE,DCOLOR,N2,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A FOUR-WAY CROSS-TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/4
C     ORIGINAL VERSION--APRIL     2008.
C     UPDATED         --SEPTEMBER 2008. ACCOMODATE "MISSING" DATA
C     UPDATED         --JANUARY   2010. TREAT BINOMIAL RATIO IN A
C                                       SIMILAR FASHION TO BINOMIAL
C     UPDATED         --JUNE      2010. CALL LIST TO CMPSTA
C     UPDATED         --JUNE      2010. USE DPDTA5 TO PRINT TABLE
C     UPDATED         --AUGUST    2023. CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*40 ICTNAM
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=9)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=30)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
      CHARACTER*8 IX3NAM
      CHARACTER*8 IX4NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IBFLAG
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION XIDTE2(*)
      DIMENSION XIDTE3(*)
      DIMENSION XIDTE4(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
      DIMENSION DSIZE(*)
      DIMENSION DCOLOR(*)
C
      DIMENSION TAG1(*)
      DIMENSION TAG2(*)
      DIMENSION TAG3(*)
      DIMENSION TAG4(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T5  '
C
      I2=0
C
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
      ANUMS2=INT(NUMSE2+0.01)
      ANUMS3=INT(NUMSE3+0.01)
      ANUMS4=INT(NUMSE4+0.01)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT5')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
C
      EPS=0.1E-7
      J=0
      NRESP=NUMV2-4
      DO1110ISET1=1,NUMSE1
        DO1120ISET2=1,NUMSE2
          DO1130ISET3=1,NUMSE3
          DO1140ISET4=1,NUMSE4
C
            K=0
            NTEMP2=0
            DO1180I=1,N
              IF(XIDTEM(ISET1).EQ.TAG1(I) .AND.
     1           XIDTE2(ISET2).EQ.TAG2(I) .AND.
     1           XIDTE3(ISET3).EQ.TAG3(I) .AND.
     1           XIDTE4(ISET4).EQ.TAG4(I))
     1           GOTO1181
              GOTO1180
 1181         CONTINUE
C
              NTEMP2=NTEMP2+1
              IF(IYVAR.EQ.'OFF')THEN
                K=K+1
                TEMP(K)=0.0
              ELSE
                IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
                  K=K+1
                  TEMP(K)=Y(I)
                  IF(IXVAR.EQ.'ON')TEMPZ(K)=Z(I)
                  IF(IX2VAR.EQ.'ON')TEMPZ2(K)=Z2(I)
                ENDIF
              ENDIF
 1180       CONTINUE
            NTEMP=K
C
C           AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C           NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C           NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
            IF(NTEMP2.EQ.0)GOTO1140
C
            IF(NTEMP.EQ.0)THEN
              IF(ICTAMV.EQ.'ZERO')THEN
                STAT=0.0
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=0.0
                  AUPPLM=0.0
                ENDIF
              ELSEIF(ICTAMV.EQ.'MV  ')THEN
                STAT=PCTAMV
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=PCTAMV
                  AUPPLM=PCTAMV
                ENDIF
              ELSE
                GOTO1140
              ENDIF
            ELSE
              CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
              IF(IERROR.EQ.'YES')GOTO9000
              IF(IBFLAG.EQ.'BPRO')THEN
                PTEMP=STAT
                NTRIAL=NTEMP
                IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  ALPHAT=ALPHA
                  CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MECL')THEN
                XMEAN=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MDCL')THEN
                XMED=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  XQ=0.5
                  CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                        QUASE,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ENDIF
            ENDIF
C
            J=J+1
            Y2(J)=STAT
            X2(J)=XIDTEM(ISET1)
            D2(J)=XIDTE2(ISET2)
            DSIZE(J)=XIDTE3(ISET3)
            DCOLOR(J)=XIDTE4(ISET4)
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              XNTRIA(J)=REAL(NTRIAL)
              XACLOW(J)=ALOWLM
              XACUPP(J)=AUPPLM
            ENDIF
C
 1140     CONTINUE
 1130     CONTINUE
 1120   CONTINUE
 1110 CONTINUE
      N2=J
C
      IF(ICASCT.EQ.'BRAT')THEN
        IBFLAG='BRAT'
        ICASCT='BPRO'
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1   GROUP-ID 2   GROUP-ID 3   GROUP-ID 4',
     1       '           ',A40)
      IF(ICASCT.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),
     1                        Y2(I),XNTRIA(I),XACLOW(I)
 2171       FORMAT(7E17.9)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),
     1                        Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),
     1                        Y2(I),XNTRIA(I),
     1                        XACLOW(I),XACUPP(I)
 2173       FORMAT(8E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),
     1                      Y2(I),XNTRIA(I),XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),D2(I),DSIZE(I),DCOLOR(I),Y2(I)
 2161     FORMAT(5E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT5')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)='-'
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)(1:8)=IX2NAM
      NCTIT2(1,2)=8
      ITITL2(1,3)(1:8)=IX3NAM
      NCTIT2(1,3)=8
      ITITL2(1,4)(1:8)=IX4NAM
      NCTIT2(1,4)=8
      ITITL2(1,5)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,5)='  $|$  '
      ENDIF
      NCTIT2(1,5)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=9
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=8
        ITITL2(1,6)='P'
        NCTIT2(1,6)=1
        ITITL2(1,7)='N'
        NCTIT2(1,7)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,8)(1:40)='Lower AC'
          NCTIT2(1,8)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,8)(1:40)='Upper AC'
          NCTIT2(1,8)=8
        ELSE
          ITITL2(1,8)(1:40)='Lower AC'
          NCTIT2(1,8)=8
          ITITL2(1,9)(1:40)='Upper AC'
          NCTIT2(1,9)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=9
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,6)='Mean'
          NCTIT2(1,6)=4
        ELSE
          ITITL2(1,6)='Median'
          NCTIT2(1,6)=6
        ENDIF
        ITITL2(1,7)='N'
        NCTIT2(1,7)=1
        ITITL2(1,8)='Lower Limit'
        ITITL2(1,8)='Upper Limit'
        NCTIT2(1,9)=11
        NCTIT2(1,9)=11
      ELSE
        NUMCOL=6
        ITITL2(1,6)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,6)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,6)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.7)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.5)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
CCCCC   ELSEIF(I.EQ.6)THEN
CCCCC     IF(ICASCT.NE.'BPRO' .AND. ICASCT.NE.'MECL' .AND.
CCCCC1       ICASCT.NE.'MDCL')THEN
CCCCC        ALIGN(I)='l'
CCCCC     ENDIF
        ENDIF
 4210 CONTINUE
C
      IWHTML(1)=125
      IWHTML(2)=125
      IWHTML(3)=125
      IWHTML(4)=125
      IWHTML(5)=25
      IWHTML(6)=125
      IWHTML(7)=75
      IWHTML(8)=125
      IWHTML(9)=125
      IJUNK=1300
      IF(ICASCT.EQ.'BPRO')IJUNK=1100
      IF(ICASCT.EQ.'MECL')IJUNK=1100
      IF(ICASCT.EQ.'MDCL')IJUNK=1100
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+IJUNK
      IWRTF(3)=IWRTF(2)+IJUNK
      IWRTF(4)=IWRTF(3)+IJUNK
      IWRTF(5)=IWRTF(4)+200
      IWRTF(6)=IWRTF(5)+IJUNK
      IWRTF(7)=IWRTF(6)+800
      IWRTF(8)=IWRTF(7)+IJUNK
      IWRTF(9)=IWRTF(8)+IJUNK
      IFRST=.TRUE.
      ILAST=.TRUE.
      IFLAGS=.TRUE.
      IFLAGE=.FALSE.
      ICALL=0
C
      ICNT=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=D2(I)
        AMAT(ICNT,3)=DSIZE(I)
        AMAT(ICNT,4)=DCOLOR(I)
        AMAT(ICNT,5)=0.0
        AMAT(ICNT,6)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,7)=XNTRIA(I)
          AMAT(ICNT,8)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,7)=XNTRIA(I)
          AMAT(ICNT,8)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,7)=XNTRIA(I)
          AMAT(ICNT,8)=XACLOW(I)
          AMAT(ICNT,9)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,7)=XNTRIA(I)
          AMAT(ICNT,8)=XACLOW(I)
          AMAT(ICNT,9)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.5)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
C
      IF(IBFLAG.EQ.'BRAT')THEN
        ICASCT='BRAT'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT5')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT5--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,N2,IERROR
 9012   FORMAT('ICASCT,N,N2,IERROR = ',A4,2I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMV2
 9013   FORMAT('NUMV2 = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMSE1,NUMSE2,NUMSE3,NUMSE4
 9015   FORMAT('NUMSE1,NUMSE2,NUMSE3,NUMSE4 = ',4I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2,ANUMS3,ANUMS4
 9016   FORMAT('ANUMS1,ANUMS2,ANUMS3,ANUMS4 = ',4G15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I),DSIZE(I),D2(I)
 9021     FORMAT('I,Y2(I),X2(I),DSIZE(I),D2(I) = ',I8,4G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT6(Y,Z,Z2,TAG1,TAG2,TAG3,TAG4,TAG5,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,XIDTE2,XIDTE3,XIDTE4,XIDTE5,
     1                  NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,D2,DSIZE,DCOLOR,DFILL,N2,
     1                  ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A FIVE-WAY CROSS-TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/4
C     ORIGINAL VERSION--APRIL     2008.
C     UPDATED         --SEPTEMBER 2008. ACCOMODATE "MISSING" DATA
C     UPDATED         --JANUARY   2010. TREAT BINOMIAL RATIO IN A
C                                       SIMILAR FASHION TO BINOMIAL
C     UPDATED         --JUNE      2010. CALL LIST TO CMPSTA
C     UPDATED         --JUNE      2010. USE DPDTA5 TO PRINT TABLE
C     UPDATED         --AUGUST    2023. CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*40 ICTNAM
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=10)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=30)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
      CHARACTER*1 IBASLC
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
      CHARACTER*8 IX3NAM
      CHARACTER*8 IX4NAM
      CHARACTER*8 IX5NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IBFLAG
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION XIDTE2(*)
      DIMENSION XIDTE3(*)
      DIMENSION XIDTE4(*)
      DIMENSION XIDTE5(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
      DIMENSION DSIZE(*)
      DIMENSION DCOLOR(*)
      DIMENSION DFILL(*)
C
      DIMENSION TAG1(*)
      DIMENSION TAG2(*)
      DIMENSION TAG3(*)
      DIMENSION TAG4(*)
      DIMENSION TAG5(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T6  '
C
      I2=0
      IPTSAV=IRTFPS
C
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
      ANUMS2=INT(NUMSE2+0.01)
      ANUMS3=INT(NUMSE3+0.01)
      ANUMS4=INT(NUMSE4+0.01)
      ANUMS5=INT(NUMSE5+0.01)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT6')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
C
      EPS=0.1E-7
      J=0
      NRESP=NUMV2-5
      DO1110ISET1=1,NUMSE1
        DO1120ISET2=1,NUMSE2
          DO1130ISET3=1,NUMSE3
          DO1140ISET4=1,NUMSE4
          DO1150ISET5=1,NUMSE5
C
            K=0
            NTEMP2=0
            DO1180I=1,N
              IF(XIDTEM(ISET1).EQ.TAG1(I) .AND.
     1           XIDTE2(ISET2).EQ.TAG2(I) .AND.
     1           XIDTE3(ISET3).EQ.TAG3(I) .AND.
     1           XIDTE4(ISET4).EQ.TAG4(I) .AND.
     1           XIDTE5(ISET5).EQ.TAG5(I))
     1           GOTO1181
              GOTO1180
 1181         CONTINUE
C
              NTEMP2=NTEMP2+1
              IF(IYVAR.EQ.'OFF')THEN
                K=K+1
                TEMP(K)=0.0
              ELSE
                IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
                  K=K+1
                  TEMP(K)=Y(I)
                  IF(IXVAR.EQ.'ON')TEMPZ(K)=Z(I)
                  IF(IX2VAR.EQ.'ON')TEMPZ2(K)=Z2(I)
                ENDIF
              ENDIF
 1180       CONTINUE
            NTEMP=K
C
C           AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C           NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C           NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
            IF(NTEMP2.EQ.0)GOTO1150
C
            IF(NTEMP.EQ.0)THEN
              IF(ICTAMV.EQ.'ZERO')THEN
                STAT=0.0
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=0.0
                  AUPPLM=0.0
                ENDIF
              ELSEIF(ICTAMV.EQ.'MV  ')THEN
                STAT=PCTAMV
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=PCTAMV
                  AUPPLM=PCTAMV
                ENDIF
              ELSE
                GOTO1150
              ENDIF
            ELSE
              CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
              IF(IERROR.EQ.'YES')GOTO9000
              IF(IBFLAG.EQ.'BPRO')THEN
                PTEMP=STAT
                NTRIAL=NTEMP
                IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  ALPHAT=ALPHA
                  CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MECL')THEN
                XMEAN=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MDCL')THEN
                XMED=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  XQ=0.5
                  CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                        QUASE,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ENDIF
            ENDIF
C
            J=J+1
            Y2(J)=STAT
            X2(J)=XIDTEM(ISET1)
            D2(J)=XIDTE2(ISET2)
            DSIZE(J)=XIDTE3(ISET3)
            DCOLOR(J)=XIDTE4(ISET4)
            DFILL(J)=XIDTE5(ISET5)
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              XNTRIA(J)=REAL(NTRIAL)
              XACLOW(J)=ALOWLM
              XACUPP(J)=AUPPLM
            ENDIF
C
 1150     CONTINUE
 1140     CONTINUE
 1130     CONTINUE
 1120   CONTINUE
 1110 CONTINUE
      N2=J
C
      IF(ICASCT.EQ.'BRAT')THEN
        IBFLAG='BRAT'
        ICASCT='BPRO'
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1   GROUP-ID 2   GROUP-ID 3   GROUP-ID 4',
     1       '   GROUP-ID 5           ',A40)
C
      IF(ICASCT.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        Y2(I),XNTRIA(I),XACLOW(I)
 2171       FORMAT(8E17.9)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        Y2(I),XNTRIA(I),
     1                        XACLOW(I),XACUPP(I)
 2173       FORMAT(9E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      Y2(I),XNTRIA(I),XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      Y2(I)
 2161     FORMAT(6E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT6')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)=' '
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)(1:8)=IX2NAM
      NCTIT2(1,2)=8
      ITITL2(1,3)(1:8)=IX3NAM
      NCTIT2(1,3)=8
      ITITL2(1,4)(1:8)=IX4NAM
      NCTIT2(1,4)=8
      ITITL2(1,5)(1:8)=IX5NAM
      NCTIT2(1,5)=8
      ITITL2(1,6)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,6)='  $|$  '
      ENDIF
      NCTIT2(1,6)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=10
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=9
        ITITL2(1,7)='P'
        NCTIT2(1,7)=1
        ITITL2(1,8)='N'
        NCTIT2(1,8)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,9)(1:40)='Lower AC'
          NCTIT2(1,9)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,9)(1:40)='Upper AC'
          NCTIT2(1,9)=8
        ELSE
          ITITL2(1,9)(1:40)='Lower AC'
          NCTIT2(1,9)=8
          ITITL2(1,10)(1:40)='Upper AC'
          NCTIT2(1,10)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=10
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,7)='Mean'
          NCTIT2(1,7)=4
        ELSE
          ITITL2(1,7)='Median'
          NCTIT2(1,7)=6
        ENDIF
        ITITL2(1,8)='N'
        NCTIT2(1,8)=1
        ITITL2(1,9)='Lower Limit'
        ITITL2(1,9)='Upper Limit'
        NCTIT2(1,10)=11
        NCTIT2(1,10)=11
      ELSE
        NUMCOL=7
        ITITL2(1,7)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,7)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,7)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.8)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.6)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
CCCCC   ELSEIF(I.EQ.6)THEN
CCCCC     IF(ICASCT.NE.'BPRO' .AND. ICASCT.NE.'MECL' .AND.
CCCCC1       ICASCT.NE.'MDCL')THEN
CCCCC        ALIGN(I)='l'
CCCCC     ENDIF
        ENDIF
 4210 CONTINUE
C
      IWHTML(1)=100
      IWHTML(2)=100
      IWHTML(3)=100
      IWHTML(4)=100
      IWHTML(5)=100
      IWHTML(6)=25
      IWHTML(7)=100
      IWHTML(8)=75
      IWHTML(9)=100
      IWHTML(10)=100
      IJUNK=1300
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IJUNK=1000
        IPTSAV=IRTFPS
        IRTFPS=16
        CALL DPCONA(92,IBASLC)
        WRITE(ICOUT,7003)IBASLC,IRTFPS
 7003   FORMAT(A1,'fs',I2)
        CALL DPWRST('XXX','WRIT')
      ENDIF
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+IJUNK
      IWRTF(3)=IWRTF(2)+IJUNK
      IWRTF(4)=IWRTF(3)+IJUNK
      IWRTF(5)=IWRTF(4)+IJUNK
      IWRTF(6)=IWRTF(5)+200
      IWRTF(7)=IWRTF(6)+IJUNK
      IWRTF(8)=IWRTF(7)+800
      IWRTF(9)=IWRTF(8)+IJUNK
      IWRTF(10)=IWRTF(9)+IJUNK
      IFRST=.TRUE.
      ILAST=.TRUE.
      IFLAGS=.TRUE.
      IFLAGE=.FALSE.
      ICALL=0
C
      ICNT=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=D2(I)
        AMAT(ICNT,3)=DSIZE(I)
        AMAT(ICNT,4)=DCOLOR(I)
        AMAT(ICNT,5)=DFILL(I)
        AMAT(ICNT,6)=0.0
        AMAT(ICNT,7)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,8)=XNTRIA(I)
          AMAT(ICNT,9)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,8)=XNTRIA(I)
          AMAT(ICNT,9)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,8)=XNTRIA(I)
          AMAT(ICNT,9)=XACLOW(I)
          AMAT(ICNT,10)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,8)=XNTRIA(I)
          AMAT(ICNT,9)=XACLOW(I)
          AMAT(ICNT,10)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.6)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IJUNK=1000
        IRTFPS=IPTSAV
        WRITE(ICOUT,7003)IBASLC,IRTFPS
        CALL DPWRST('XXX','WRIT')
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
C
      IF(IBFLAG.EQ.'BRAT')THEN
        ICASCT='BRAT'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT6')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT6--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,N2,IERROR
 9012   FORMAT('ICASCT,N,N2,IERROR = ',A4,2I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMV2
 9013   FORMAT('NUMV2 = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5
 9015   FORMAT('NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5 = ',5I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5
 9016   FORMAT('ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5 = ',5G15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I),DSIZE(I),D2(I)
 9021     FORMAT('I,Y2(I),X2(I),DSIZE(I),D2(I) = ',I8,4G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT7(Y,Z,Z2,TAG1,TAG2,TAG3,TAG4,TAG5,TAG6,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,XIDTE2,XIDTE3,XIDTE4,XIDTE5,XIDTE6,
     1                  NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,NUMSE6,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,IX6NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,D2,DSIZE,DCOLOR,DFILL,DSYMB,N2,
     1                  ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A SIX-WAY CROSS-TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/4
C     ORIGINAL VERSION--APRIL     2008.
C     UPDATED         --SEPTEMBER 2008. ACCOMODATE "MISSING" DATA
C     UPDATED         --JANUARY   2010. TREAT BINOMIAL RATIO IN A
C                                       SIMILAR FASHION TO BINOMIAL
C     UPDATED         --JUNE      2010. CALL LIST TO CMPSTA
C     UPDATED         --JUNE      2010. USE DPDTA5 TO PRINT TABLE
C     UPDATED         --AUGUST    2023. CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*40 ICTNAM
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=11)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=30)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
      CHARACTER*1 IBASLC
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
      CHARACTER*8 IX3NAM
      CHARACTER*8 IX4NAM
      CHARACTER*8 IX5NAM
      CHARACTER*8 IX6NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IBFLAG
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION XIDTE2(*)
      DIMENSION XIDTE3(*)
      DIMENSION XIDTE4(*)
      DIMENSION XIDTE5(*)
      DIMENSION XIDTE6(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
      DIMENSION DSIZE(*)
      DIMENSION DCOLOR(*)
      DIMENSION DFILL(*)
      DIMENSION DSYMB(*)
C
      DIMENSION TAG1(*)
      DIMENSION TAG2(*)
      DIMENSION TAG3(*)
      DIMENSION TAG4(*)
      DIMENSION TAG5(*)
      DIMENSION TAG6(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T7  '
C
      I2=0
      IPTSAV=IRTFPS
C
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
      ANUMS2=INT(NUMSE2+0.01)
      ANUMS3=INT(NUMSE3+0.01)
      ANUMS4=INT(NUMSE4+0.01)
      ANUMS5=INT(NUMSE5+0.01)
      ANUMS6=INT(NUMSE6+0.01)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT7')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
C
      EPS=0.1E-7
      J=0
      NRESP=NUMV2-6
      DO1110ISET1=1,NUMSE1
        DO1120ISET2=1,NUMSE2
          DO1130ISET3=1,NUMSE3
          DO1140ISET4=1,NUMSE4
          DO1150ISET5=1,NUMSE5
          DO1160ISET6=1,NUMSE6
C
            K=0
            NTEMP2=0
            DO1180I=1,N
              IF(XIDTEM(ISET1).EQ.TAG1(I) .AND.
     1           XIDTE2(ISET2).EQ.TAG2(I) .AND.
     1           XIDTE3(ISET3).EQ.TAG3(I) .AND.
     1           XIDTE4(ISET4).EQ.TAG4(I) .AND.
     1           XIDTE5(ISET5).EQ.TAG5(I) .AND.
     1           XIDTE6(ISET6).EQ.TAG6(I))
     1           GOTO1181
              GOTO1180
 1181         CONTINUE
C
              NTEMP2=NTEMP2+1
              IF(IYVAR.EQ.'OFF')THEN
                K=K+1
                TEMP(K)=0.0
              ELSE
                IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
                  K=K+1
                  TEMP(K)=Y(I)
                  IF(IXVAR.EQ.'ON')TEMPZ(K)=Z(I)
                  IF(IX2VAR.EQ.'ON')TEMPZ2(K)=Z2(I)
                ENDIF
              ENDIF
 1180       CONTINUE
            NTEMP=K
C
C           AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C           NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C           NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
            IF(NTEMP2.EQ.0)GOTO1160
C
            IF(NTEMP.EQ.0)THEN
              IF(ICTAMV.EQ.'ZERO')THEN
                STAT=0.0
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=0.0
                  AUPPLM=0.0
                ENDIF
              ELSEIF(ICTAMV.EQ.'MV  ')THEN
                STAT=PCTAMV
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=PCTAMV
                  AUPPLM=PCTAMV
                ENDIF
              ELSE
                GOTO1160
              ENDIF
            ELSE
              CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
              IF(IERROR.EQ.'YES')GOTO9000
              IF(IBFLAG.EQ.'BPRO')THEN
                PTEMP=STAT
                NTRIAL=NTEMP
                IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  ALPHAT=ALPHA
                  CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MECL')THEN
                XMEAN=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MDCL')THEN
                XMED=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  XQ=0.5
                  CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                        QUASE,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ENDIF
            ENDIF
C
            J=J+1
            Y2(J)=STAT
            X2(J)=XIDTEM(ISET1)
            D2(J)=XIDTE2(ISET2)
            DSIZE(J)=XIDTE3(ISET3)
            DCOLOR(J)=XIDTE4(ISET4)
            DFILL(J)=XIDTE5(ISET5)
            DSYMB(J)=XIDTE6(ISET6)
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              XNTRIA(J)=REAL(NTRIAL)
              XACLOW(J)=ALOWLM
              XACUPP(J)=AUPPLM
            ENDIF
C
 1160     CONTINUE
 1150     CONTINUE
 1140     CONTINUE
 1130     CONTINUE
 1120   CONTINUE
 1110 CONTINUE
      N2=J
C
      IF(ICASCT.EQ.'BRAT')THEN
        IBFLAG='BRAT'
        ICASCT='BPRO'
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1   GROUP-ID 2   GROUP-ID 3   GROUP-ID 4',
     1       '   GROUP-ID 5   GROUP-ID 6           ',A40)
C
      IF(ICASCT.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),Y2(I),XNTRIA(I),XACLOW(I)

 2171       FORMAT(9E17.9)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),Y2(I),XNTRIA(I),
     1                        XACLOW(I),XACUPP(I)
 2173       FORMAT(10E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      DSYMB(I),Y2(I),XNTRIA(I),XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      DSYMB(I),Y2(I)
 2161     FORMAT(7E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT7')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)=' '
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)(1:8)=IX2NAM
      NCTIT2(1,2)=8
      ITITL2(1,3)(1:8)=IX3NAM
      NCTIT2(1,3)=8
      ITITL2(1,4)(1:8)=IX4NAM
      NCTIT2(1,4)=8
      ITITL2(1,5)(1:8)=IX5NAM
      NCTIT2(1,5)=8
      ITITL2(1,6)(1:8)=IX6NAM
      NCTIT2(1,6)=8
      ITITL2(1,7)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,7)='  $|$  '
      ENDIF
      NCTIT2(1,7)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=11
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=10
        ITITL2(1,8)='P'
        NCTIT2(1,8)=1
        ITITL2(1,9)='N'
        NCTIT2(1,9)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,10)(1:40)='Lower AC'
          NCTIT2(1,10)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,10)(1:40)='Upper AC'
          NCTIT2(1,10)=8
        ELSE
          ITITL2(1,10)(1:40)='Lower AC'
          NCTIT2(1,10)=8
          ITITL2(1,11)(1:40)='Upper AC'
          NCTIT2(1,11)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=11
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,8)='Mean'
          NCTIT2(1,8)=4
        ELSE
          ITITL2(1,8)='Median'
          NCTIT2(1,8)=6
        ENDIF
        ITITL2(1,9)='N'
        NCTIT2(1,9)=1
        ITITL2(1,10)='Lower Limit'
        ITITL2(1,10)='Upper Limit'
        NCTIT2(1,11)=11
        NCTIT2(1,11)=11
      ELSE
        NUMCOL=8
        ITITL2(1,8)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,8)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,8)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.9)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.7)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
CCCCC   ELSEIF(I.EQ.6)THEN
CCCCC     IF(ICASCT.NE.'BPRO' .AND. ICASCT.NE.'MECL' .AND.
CCCCC1       ICASCT.NE.'MDCL')THEN
CCCCC        ALIGN(I)='l'
CCCCC     ENDIF
        ENDIF
 4210 CONTINUE
C
      IWHTML(1)=100
      IWHTML(2)=100
      IWHTML(3)=100
      IWHTML(4)=100
      IWHTML(5)=100
      IWHTML(6)=100
      IWHTML(7)=25
      IWHTML(8)=100
      IWHTML(9)=75
      IWHTML(10)=100
      IWHTML(11)=100
      IJUNK=1300
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IJUNK=900
        IPTSAV=IRTFPS
        IRTFPS=14
        CALL DPCONA(92,IBASLC)
        WRITE(ICOUT,7003)IBASLC,IRTFPS
 7003   FORMAT(A1,'fs',I2)
        CALL DPWRST('XXX','WRIT')
      ENDIF
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+IJUNK
      IWRTF(3)=IWRTF(2)+IJUNK
      IWRTF(4)=IWRTF(3)+IJUNK
      IWRTF(5)=IWRTF(4)+IJUNK
      IWRTF(6)=IWRTF(5)+IJUNK
      IWRTF(7)=IWRTF(6)+200
      IWRTF(8)=IWRTF(7)+IJUNK
      IWRTF(9)=IWRTF(8)+800
      IWRTF(10)=IWRTF(9)+IJUNK
      IWRTF(11)=IWRTF(10)+IJUNK
      IFRST=.TRUE.
      ILAST=.TRUE.
      IFLAGS=.TRUE.
      IFLAGE=.FALSE.
      ICALL=0
C
      ICNT=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=D2(I)
        AMAT(ICNT,3)=DSIZE(I)
        AMAT(ICNT,4)=DCOLOR(I)
        AMAT(ICNT,5)=DFILL(I)
        AMAT(ICNT,6)=DSYMB(I)
        AMAT(ICNT,7)=0.0
        AMAT(ICNT,8)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,9)=XNTRIA(I)
          AMAT(ICNT,10)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,9)=XNTRIA(I)
          AMAT(ICNT,10)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,9)=XNTRIA(I)
          AMAT(ICNT,10)=XACLOW(I)
          AMAT(ICNT,11)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,9)=XNTRIA(I)
          AMAT(ICNT,10)=XACLOW(I)
          AMAT(ICNT,11)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.7)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IRTFPS=IPTSAV
        WRITE(ICOUT,7003)IBASLC,IRTFPS
        CALL DPWRST('XXX','WRIT')
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
C
      IF(IBFLAG.EQ.'BRAT')THEN
        ICASCT='BRAT'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT7')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT7--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,N2,IERROR
 9012   FORMAT('ICASCT,N,N2,IERROR = ',A4,2I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMV2
 9013   FORMAT('NUMV2 = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,NUMSE6
 9015   FORMAT('NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,NUMSE6 = ',6I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5,ANUMSE6
 9016   FORMAT('ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5,ANUMSE6 = ',6G15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I),DSIZE(I),D2(I)
 9021     FORMAT('I,Y2(I),X2(I),DSIZE(I),D2(I) = ',I8,4G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT8(Y,Z,Z2,TAG1,TAG2,TAG3,TAG4,TAG5,TAG6,TAG7,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,XIDTE2,XIDTE3,XIDTE4,
     1                  XIDTE5,XIDTE6,XIDTE7,
     1                  NUMSE1,NUMSE2,NUMSE3,NUMSE4,
     1                  NUMSE5,NUMSE6,NUMSE7,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,IX6NAM,
     1                  IX7NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,D2,DSIZE,DCOLOR,DFILL,DSYMB,XPLOT,N2,
     1                  ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A SEVEN-WAY CROSS-TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2014/5
C     ORIGINAL VERSION--MAY       2014.
C     UPDATED         --AUGUST    2023. CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*40 ICTNAM
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=12)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=30)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
      CHARACTER*1 IBASLC
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
      CHARACTER*8 IX3NAM
      CHARACTER*8 IX4NAM
      CHARACTER*8 IX5NAM
      CHARACTER*8 IX6NAM
      CHARACTER*8 IX7NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IBFLAG
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION XIDTE2(*)
      DIMENSION XIDTE3(*)
      DIMENSION XIDTE4(*)
      DIMENSION XIDTE5(*)
      DIMENSION XIDTE6(*)
      DIMENSION XIDTE7(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
      DIMENSION DSIZE(*)
      DIMENSION DCOLOR(*)
      DIMENSION DFILL(*)
      DIMENSION DSYMB(*)
      DIMENSION XPLOT(*)
C
      DIMENSION TAG1(*)
      DIMENSION TAG2(*)
      DIMENSION TAG3(*)
      DIMENSION TAG4(*)
      DIMENSION TAG5(*)
      DIMENSION TAG6(*)
      DIMENSION TAG7(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T8  '
C
      I2=0
      IPTSAV=IRTFPS
C
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
      ANUMS2=INT(NUMSE2+0.01)
      ANUMS3=INT(NUMSE3+0.01)
      ANUMS4=INT(NUMSE4+0.01)
      ANUMS5=INT(NUMSE5+0.01)
      ANUMS6=INT(NUMSE6+0.01)
      ANUMS7=INT(NUMSE7+0.01)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT8')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
C
      EPS=0.1E-7
      J=0
      NRESP=NUMV2-6
      DO1110ISET1=1,NUMSE1
        DO1120ISET2=1,NUMSE2
          DO1130ISET3=1,NUMSE3
          DO1140ISET4=1,NUMSE4
          DO1150ISET5=1,NUMSE5
          DO1160ISET6=1,NUMSE6
          DO1170ISET7=1,NUMSE7
C
            K=0
            NTEMP2=0
            DO1180I=1,N
              IF(XIDTEM(ISET1).EQ.TAG1(I) .AND.
     1           XIDTE2(ISET2).EQ.TAG2(I) .AND.
     1           XIDTE3(ISET3).EQ.TAG3(I) .AND.
     1           XIDTE4(ISET4).EQ.TAG4(I) .AND.
     1           XIDTE5(ISET5).EQ.TAG5(I) .AND.
     1           XIDTE6(ISET6).EQ.TAG6(I) .AND.
     1           XIDTE7(ISET7).EQ.TAG7(I))
     1           GOTO1181
              GOTO1180
 1181         CONTINUE
C
              NTEMP2=NTEMP2+1
              IF(IYVAR.EQ.'OFF')THEN
                K=K+1
                TEMP(K)=0.0
              ELSE
                IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
                  K=K+1
                  TEMP(K)=Y(I)
                  IF(IXVAR.EQ.'ON')TEMPZ(K)=Z(I)
                  IF(IX2VAR.EQ.'ON')TEMPZ2(K)=Z2(I)
                ENDIF
              ENDIF
 1180       CONTINUE
            NTEMP=K
C
C           AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C           NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C           NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
            IF(NTEMP2.EQ.0)GOTO1170
C
            IF(NTEMP.EQ.0)THEN
              IF(ICTAMV.EQ.'ZERO')THEN
                STAT=0.0
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=0.0
                  AUPPLM=0.0
                ENDIF
              ELSEIF(ICTAMV.EQ.'MV  ')THEN
                STAT=PCTAMV
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=PCTAMV
                  AUPPLM=PCTAMV
                ENDIF
              ELSE
                GOTO1160
              ENDIF
            ELSE
              CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
              IF(IERROR.EQ.'YES')GOTO9000
              IF(IBFLAG.EQ.'BPRO')THEN
                PTEMP=STAT
                NTRIAL=NTEMP
                IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  ALPHAT=ALPHA
                  CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MECL')THEN
                XMEAN=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MDCL')THEN
                XMED=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  XQ=0.5
                  CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                        QUASE,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ENDIF
            ENDIF
C
            J=J+1
            Y2(J)=STAT
            X2(J)=XIDTEM(ISET1)
            D2(J)=XIDTE2(ISET2)
            DSIZE(J)=XIDTE3(ISET3)
            DCOLOR(J)=XIDTE4(ISET4)
            DFILL(J)=XIDTE5(ISET5)
            DSYMB(J)=XIDTE6(ISET6)
            XPLOT(J)=XIDTE7(ISET7)
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              XNTRIA(J)=REAL(NTRIAL)
              XACLOW(J)=ALOWLM
              XACUPP(J)=AUPPLM
            ENDIF
C
 1170     CONTINUE
 1160     CONTINUE
 1150     CONTINUE
 1140     CONTINUE
 1130     CONTINUE
 1120   CONTINUE
 1110 CONTINUE
      N2=J
C
      IF(ICASCT.EQ.'BRAT')THEN
        IBFLAG='BRAT'
        ICASCT='BPRO'
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1   GROUP-ID 2   GROUP-ID 3   GROUP-ID 4',
     1       '   GROUP-ID 5   GROUP-ID 6  GROUP-ID 7         ',A40)
C
      IF(ICASCT.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),XPLOT(I),
     1                        Y2(I),XNTRIA(I),XACLOW(I)

 2171       FORMAT(10E17.9)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),XPLOT(I),
     1                        Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),XPLOT(I),Y2(I),XNTRIA(I),
     1                        XACLOW(I),XACUPP(I)
 2173       FORMAT(11E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      DSYMB(I),XPLOT(I),
     1                      Y2(I),XNTRIA(I),XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      DSYMB(I),XPLOT(I),Y2(I)
 2161     FORMAT(8E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT8')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)=' '
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)(1:8)=IX2NAM
      NCTIT2(1,2)=8
      ITITL2(1,3)(1:8)=IX3NAM
      NCTIT2(1,3)=8
      ITITL2(1,4)(1:8)=IX4NAM
      NCTIT2(1,4)=8
      ITITL2(1,5)(1:8)=IX5NAM
      NCTIT2(1,5)=8
      ITITL2(1,6)(1:8)=IX6NAM
      NCTIT2(1,6)=8
      ITITL2(1,7)(1:8)=IX7NAM
      NCTIT2(1,7)=8
      ITITL2(1,8)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,8)='  $|$  '
      ENDIF
      NCTIT2(1,8)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=12
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=11
        ITITL2(1,9)='P'
        NCTIT2(1,9)=1
        ITITL2(1,10)='N'
        NCTIT2(1,10)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,11)(1:40)='Lower AC'
          NCTIT2(1,11)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,11)(1:40)='Upper AC'
          NCTIT2(1,11)=8
        ELSE
          ITITL2(1,11)(1:40)='Lower AC'
          NCTIT2(1,11)=8
          ITITL2(1,12)(1:40)='Upper AC'
          NCTIT2(1,12)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=12
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,9)='Mean'
          NCTIT2(1,9)=4
        ELSE
          ITITL2(1,9)='Median'
          NCTIT2(1,9)=6
        ENDIF
        ITITL2(1,10)='N'
        NCTIT2(1,10)=1
        ITITL2(1,11)='Lower Limit'
        ITITL2(1,11)='Upper Limit'
        NCTIT2(1,12)=11
        NCTIT2(1,12)=11
      ELSE
        NUMCOL=9
        ITITL2(1,9)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,9)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,9)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.10)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.8)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
CCCCC   ELSEIF(I.EQ.6)THEN
CCCCC     IF(ICASCT.NE.'BPRO' .AND. ICASCT.NE.'MECL' .AND.
CCCCC1       ICASCT.NE.'MDCL')THEN
CCCCC        ALIGN(I)='l'
CCCCC     ENDIF
        ENDIF
        NMAX=NMAX+NTOT(I)
 4210 CONTINUE
C
      IWHTML(1)=100
      IWHTML(2)=100
      IWHTML(3)=100
      IWHTML(4)=100
      IWHTML(5)=100
      IWHTML(6)=100
      IWHTML(7)=100
      IWHTML(8)=25
      IWHTML(9)=100
      IWHTML(10)=75
      IWHTML(11)=100
      IWHTML(12)=100
      IJUNK=1300
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IJUNK=900
        IPTSAV=IRTFPS
        IRTFPS=14
        CALL DPCONA(92,IBASLC)
        WRITE(ICOUT,7003)IBASLC,IRTFPS
 7003   FORMAT(A1,'fs',I2)
        CALL DPWRST('XXX','WRIT')
      ENDIF
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+IJUNK
      IWRTF(3)=IWRTF(2)+IJUNK
      IWRTF(4)=IWRTF(3)+IJUNK
      IWRTF(5)=IWRTF(4)+IJUNK
      IWRTF(6)=IWRTF(5)+IJUNK
      IWRTF(7)=IWRTF(6)+IJUNK
      IWRTF(8)=IWRTF(7)+200
      IWRTF(9)=IWRTF(8)+IJUNK
      IWRTF(10)=IWRTF(9)+800
      IWRTF(11)=IWRTF(10)+IJUNK
      IWRTF(12)=IWRTF(11)+IJUNK
      IFRST=.TRUE.
      ILAST=.TRUE.
      IFLAGS=.TRUE.
      IFLAGE=.FALSE.
      ICALL=0
C
      ICNT=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=D2(I)
        AMAT(ICNT,3)=DSIZE(I)
        AMAT(ICNT,4)=DCOLOR(I)
        AMAT(ICNT,5)=DFILL(I)
        AMAT(ICNT,6)=DSYMB(I)
        AMAT(ICNT,7)=XPLOT(I)
        AMAT(ICNT,8)=0.0
        AMAT(ICNT,9)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,10)=XNTRIA(I)
          AMAT(ICNT,11)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,10)=XNTRIA(I)
          AMAT(ICNT,11)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,10)=XNTRIA(I)
          AMAT(ICNT,11)=XACLOW(I)
          AMAT(ICNT,12)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,10)=XNTRIA(I)
          AMAT(ICNT,11)=XACLOW(I)
          AMAT(ICNT,12)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.8)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IRTFPS=IPTSAV
        WRITE(ICOUT,7003)IBASLC,IRTFPS
        CALL DPWRST('XXX','WRIT')
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
C
      IF(IBFLAG.EQ.'BRAT')THEN
        ICASCT='BRAT'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT8')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT8--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,N2,IERROR
 9012   FORMAT('ICASCT,N,N2,IERROR = ',A4,2I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMV2
 9013   FORMAT('NUMV2 = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,
     1                   NUMSE6,NUSE7
 9015   FORMAT('NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,NUMSE6,NUMSE7 = ',
     1         7I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5,
     1                   ANUMSE6,ANUMSE7
 9016   FORMAT('ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5,ANUMSE6,ANUMSE7 = ',
     1         7G15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I),DSIZE(I),D2(I)
 9021     FORMAT('I,Y2(I),X2(I),DSIZE(I),D2(I) = ',I8,4G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT9(Y,Z,Z2,TAG1,TAG2,TAG3,TAG4,TAG5,TAG6,
     1                  TAG7,TAG8,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,XIDTE2,XIDTE3,XIDTE4,
     1                  XIDTE5,XIDTE6,XIDTE7,XIDTE8,
     1                  NUMSE1,NUMSE2,NUMSE3,NUMSE4,
     1                  NUMSE5,NUMSE6,NUMSE7,NUMSE8,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,
     1                  IX1NAM,IX2NAM,IX3NAM,IX4NAM,IX5NAM,IX6NAM,
     1                  IX7NAM,IX8NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,D2,DSIZE,DCOLOR,DFILL,DSYMB,
     1                  XPLOT,YPLOT,N2,
     1                  ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE AN EIGHT-WAY CROSS-TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2014/5
C     ORIGINAL VERSION--MAY       2014.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*4 ISTARA
      CHARACTER*40 ICTNAM
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=13)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=50)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
      CHARACTER*1 IBASLC
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
      CHARACTER*8 IX2NAM
      CHARACTER*8 IX3NAM
      CHARACTER*8 IX4NAM
      CHARACTER*8 IX5NAM
      CHARACTER*8 IX6NAM
      CHARACTER*8 IX7NAM
      CHARACTER*8 IX8NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IBFLAG
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION XIDTE2(*)
      DIMENSION XIDTE3(*)
      DIMENSION XIDTE4(*)
      DIMENSION XIDTE5(*)
      DIMENSION XIDTE6(*)
      DIMENSION XIDTE7(*)
      DIMENSION XIDTE8(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
      DIMENSION D2(*)
      DIMENSION DSIZE(*)
      DIMENSION DCOLOR(*)
      DIMENSION DFILL(*)
      DIMENSION DSYMB(*)
      DIMENSION XPLOT(*)
      DIMENSION YPLOT(*)
C
      DIMENSION TAG1(*)
      DIMENSION TAG2(*)
      DIMENSION TAG3(*)
      DIMENSION TAG4(*)
      DIMENSION TAG5(*)
      DIMENSION TAG6(*)
      DIMENSION TAG7(*)
      DIMENSION TAG8(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT9')THEN
        WRITE(ICOUT,51)
   51   FORMAT('AT THE BEGINNING OF DPCRT9')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,55)NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,
     1                   NUMSE6,NUSE7,NUMSE8
   55   FORMAT('NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,NUMSE6,',
     1         'NUMSE7,NUMSE8 = ',8I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      ISUBN1='DPCR'
      ISUBN2='T9  '
C
      I2=0
      IPTSAV=IRTFPS
C
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
      ANUMS2=INT(NUMSE2+0.01)
      ANUMS3=INT(NUMSE3+0.01)
      ANUMS4=INT(NUMSE4+0.01)
      ANUMS5=INT(NUMSE5+0.01)
      ANUMS6=INT(NUMSE6+0.01)
      ANUMS7=INT(NUMSE7+0.01)
      ANUMS8=INT(NUMSE8+0.01)
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT9')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
C
      EPS=0.1E-7
      J=0
      NRESP=NUMV2-6
      DO1110ISET1=1,NUMSE1
        DO1120ISET2=1,NUMSE2
          DO1130ISET3=1,NUMSE3
          DO1140ISET4=1,NUMSE4
          DO1150ISET5=1,NUMSE5
          DO1160ISET6=1,NUMSE6
          DO1170ISET7=1,NUMSE7
          DO1180ISET8=1,NUMSE8
C
            K=0
            NTEMP2=0
            DO1190I=1,N
              IF(XIDTEM(ISET1).EQ.TAG1(I) .AND.
     1           XIDTE2(ISET2).EQ.TAG2(I) .AND.
     1           XIDTE3(ISET3).EQ.TAG3(I) .AND.
     1           XIDTE4(ISET4).EQ.TAG4(I) .AND.
     1           XIDTE5(ISET5).EQ.TAG5(I) .AND.
     1           XIDTE6(ISET6).EQ.TAG6(I) .AND.
     1           XIDTE7(ISET7).EQ.TAG7(I) .AND.
     1           XIDTE8(ISET8).EQ.TAG8(I))
     1           GOTO1191
              GOTO1190
 1191         CONTINUE
C
              NTEMP2=NTEMP2+1
              IF(IYVAR.EQ.'OFF')THEN
                K=K+1
                TEMP(K)=0.0
              ELSE
                IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
                  K=K+1
                  TEMP(K)=Y(I)
                  IF(IXVAR.EQ.'ON')TEMPZ(K)=Z(I)
                  IF(IX2VAR.EQ.'ON')TEMPZ2(K)=Z2(I)
                ENDIF
              ENDIF
 1190       CONTINUE
            NTEMP=K
C
C           AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C           NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C           NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
            IF(NTEMP2.EQ.0)GOTO1180
C
            IF(NTEMP.EQ.0)THEN
              IF(ICTAMV.EQ.'ZERO')THEN
                STAT=0.0
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=0.0
                  AUPPLM=0.0
                ENDIF
              ELSEIF(ICTAMV.EQ.'MV  ')THEN
                STAT=PCTAMV
                IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1             ICASCT.EQ.'MDCL')THEN
                  NTRIAL=0
                  ALOWLM=PCTAMV
                  AUPPLM=PCTAMV
                ENDIF
              ELSE
                GOTO1180
              ENDIF
            ELSE
              CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
              IF(IERROR.EQ.'YES')GOTO9000
              IF(IBFLAG.EQ.'BPRO')THEN
                PTEMP=STAT
                NTRIAL=NTEMP
                IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  ALPHAT=ALPHA
                  CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MECL')THEN
                XMEAN=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ELSEIF(ICASCT.EQ.'MDCL')THEN
                XMED=STAT
                NTRIAL=NTEMP
                IF(STAT.EQ.PSTAMV)THEN
                  ALOWLM=PSTAMV
                  AUPPLM=PSTAMV
                ELSE
                  XQ=0.5
                  CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                        QUASE,IBUGA3,IERROR)
                  ALPHAT=ALPHA
                  CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                        ALOWLM,AUPPLM,IBUGA3,IERROR)
                ENDIF
              ENDIF
            ENDIF
C
            J=J+1
            Y2(J)=STAT
            X2(J)=XIDTEM(ISET1)
            D2(J)=XIDTE2(ISET2)
            DSIZE(J)=XIDTE3(ISET3)
            DCOLOR(J)=XIDTE4(ISET4)
            DFILL(J)=XIDTE5(ISET5)
            DSYMB(J)=XIDTE6(ISET6)
            XPLOT(J)=XIDTE7(ISET7)
            YPLOT(J)=XIDTE8(ISET8)
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              XNTRIA(J)=REAL(NTRIAL)
              XACLOW(J)=ALOWLM
              XACUPP(J)=AUPPLM
            ENDIF
C
 1180     CONTINUE
 1170     CONTINUE
 1160     CONTINUE
 1150     CONTINUE
 1140     CONTINUE
 1130     CONTINUE
 1120   CONTINUE
 1110 CONTINUE
      N2=J
C
      IF(ICASCT.EQ.'BRAT')THEN
        IBFLAG='BRAT'
        ICASCT='BPRO'
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1   GROUP-ID 2   GROUP-ID 3   GROUP-ID 4',
     1       '   GROUP-ID 5   GROUP-ID 6  GROUP-ID 7   GROUP-ID 8',
     1       '      ',A40)
C
      IF(ICASCT.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),XPLOT(I),YPLOT(I),
     1                        Y2(I),XNTRIA(I),XACLOW(I)

 2171       FORMAT(11E17.9)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),XPLOT(I),YPLOT(I),
     1                        Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                        DSYMB(I),XPLOT(I),YPLOT(I),
     1                        Y2(I),XNTRIA(I),XACLOW(I),XACUPP(I)
 2173       FORMAT(12E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      DSYMB(I),XPLOT(I),YPLOT(I),
     1                      Y2(I),XNTRIA(I),XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),D2(I),DSIZE(I),DCOLOR(I),DFILL(I),
     1                      DSYMB(I),XPLOT(I),YPLOT(I),Y2(I)
 2161     FORMAT(9E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT9')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)=' '
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)(1:8)=IX2NAM
      NCTIT2(1,2)=8
      ITITL2(1,3)(1:8)=IX3NAM
      NCTIT2(1,3)=8
      ITITL2(1,4)(1:8)=IX4NAM
      NCTIT2(1,4)=8
      ITITL2(1,5)(1:8)=IX5NAM
      NCTIT2(1,5)=8
      ITITL2(1,6)(1:8)=IX6NAM
      NCTIT2(1,6)=8
      ITITL2(1,7)(1:8)=IX7NAM
      NCTIT2(1,7)=8
      ITITL2(1,8)(1:8)=IX8NAM
      NCTIT2(1,8)=8
      ITITL2(1,9)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,9)='  $|$  '
      ENDIF
      NCTIT2(1,9)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=13
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=12
        ITITL2(1,10)='P'
        NCTIT2(1,10)=1
        ITITL2(1,11)='N'
        NCTIT2(1,11)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,12)(1:40)='Lower AC'
          NCTIT2(1,12)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,12)(1:40)='Upper AC'
          NCTIT2(1,12)=8
        ELSE
          ITITL2(1,12)(1:40)='Lower AC'
          NCTIT2(1,12)=8
          ITITL2(1,13)(1:40)='Upper AC'
          NCTIT2(1,13)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=13
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,10)='Mean'
          NCTIT2(1,10)=4
        ELSE
          ITITL2(1,10)='Median'
          NCTIT2(1,10)=6
        ENDIF
        ITITL2(1,11)='N'
        NCTIT2(1,11)=1
        ITITL2(1,12)='Lower Limit'
        ITITL2(1,12)='Upper Limit'
        NCTIT2(1,13)=11
        NCTIT2(1,13)=11
      ELSE
        NUMCOL=10
        ITITL2(1,10)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,9)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,10)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.11)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.9)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
CCCCC   ELSEIF(I.EQ.6)THEN
CCCCC     IF(ICASCT.NE.'BPRO' .AND. ICASCT.NE.'MECL' .AND.
CCCCC1       ICASCT.NE.'MDCL')THEN
CCCCC        ALIGN(I)='l'
CCCCC     ENDIF
        ENDIF
        NMAX=NMAX+NTOT(I)
 4210 CONTINUE
C
      IWHTML(1)=100
      IWHTML(2)=100
      IWHTML(3)=100
      IWHTML(4)=100
      IWHTML(5)=100
      IWHTML(6)=100
      IWHTML(7)=100
      IWHTML(8)=100
      IWHTML(9)=25
      IWHTML(10)=100
      IWHTML(11)=75
      IWHTML(12)=100
      IWHTML(13)=100
      IJUNK=1300
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IJUNK=900
        IPTSAV=IRTFPS
        IRTFPS=14
        CALL DPCONA(92,IBASLC)
        WRITE(ICOUT,7003)IBASLC,IRTFPS
 7003   FORMAT(A1,'fs',I2)
        CALL DPWRST('XXX','WRIT')
      ENDIF
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+IJUNK
      IWRTF(3)=IWRTF(2)+IJUNK
      IWRTF(4)=IWRTF(3)+IJUNK
      IWRTF(5)=IWRTF(4)+IJUNK
      IWRTF(6)=IWRTF(5)+IJUNK
      IWRTF(7)=IWRTF(6)+IJUNK
      IWRTF(8)=IWRTF(7)+IJUNK
      IWRTF(9)=IWRTF(8)+200
      IWRTF(10)=IWRTF(9)+IJUNK
      IWRTF(11)=IWRTF(10)+800
      IWRTF(12)=IWRTF(11)+IJUNK
      IWRTF(13)=IWRTF(10)+IJUNK
      IFRST=.TRUE.
      ILAST=.TRUE.
      IFLAGS=.TRUE.
      IFLAGE=.FALSE.
      ICALL=0
C
      ICNT=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=D2(I)
        AMAT(ICNT,3)=DSIZE(I)
        AMAT(ICNT,4)=DCOLOR(I)
        AMAT(ICNT,5)=DFILL(I)
        AMAT(ICNT,6)=DSYMB(I)
        AMAT(ICNT,7)=XPLOT(I)
        AMAT(ICNT,8)=YPLOT(I)
        AMAT(ICNT,9)=0.0
        AMAT(ICNT,10)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,11)=XNTRIA(I)
          AMAT(ICNT,12)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,11)=XNTRIA(I)
          AMAT(ICNT,12)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,11)=XNTRIA(I)
          AMAT(ICNT,12)=XACLOW(I)
          AMAT(ICNT,13)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,11)=XNTRIA(I)
          AMAT(ICNT,12)=XACLOW(I)
          AMAT(ICNT,13)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.9)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
      IF(ICASCT.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1   ICASCT.EQ.'MDCL')THEN
        IRTFPS=IPTSAV
        WRITE(ICOUT,7003)IBASLC,IRTFPS
        CALL DPWRST('XXX','WRIT')
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
C
      IF(IBFLAG.EQ.'BRAT')THEN
        ICASCT='BRAT'
      ENDIF
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT9')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT9--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,N2,IERROR
 9012   FORMAT('ICASCT,N,N2,IERROR = ',A4,2I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)NUMV2
 9013   FORMAT('NUMV2 = ',I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9015)NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,
     1                   NUMSE6,NUSE7,NUMSE8
 9015   FORMAT('NUMSE1,NUMSE2,NUMSE3,NUMSE4,NUMSE5,NUMSE6,',
     1         'NUMSE7,NUMSE8 = ',8I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5,
     1                   ANUMSE6,ANUMSE7,ANUMSE8
 9016   FORMAT('ANUMS1,ANUMS2,ANUMS3,ANUMS4,ANUMSE5,ANUMSE6,',
     1         'ANUMSE7,ANUMSE8 = ',8G15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I),DSIZE(I),D2(I)
 9021     FORMAT('I,Y2(I),X2(I),DSIZE(I),D2(I) = ',I8,4G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCRT0(Y,Z,Z2,TAG1,N,
     1                  NUMV2,ICASCT,ICTNAM,ISTARA,
     1                  XIDTEM,
     1                  NUMSE1,
     1                  TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1                  XNTRIA,XACLOW,XACUPP,XTEMP8,XTEMP9,
     1                  ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1                  DTEMP1,DTEMP2,DTEMP3,
     1                  ISEED,ALPHA,
     1                  IXVAR,IX2VAR,IYVAR,
     1                  IYNAM,IXNAM,IXNAM2,IX1NAM,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  MAXNXT,
     1                  Y2,X2,N2,ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--GENERATE A ONE-WAY TABULATION AND
C              OPTIONALLY PRINT THE RESULTS TO AN ASCII,
C              HTML, LATEX, OR RTF TABLE.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     REFERENCE--ASTM MANUAL STP-15D, PAGES 78-84, 100-105
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2008/6
C     ORIGINAL VERSION--JUNE      2008. SPLIT OFF FROM DPTAB2 ROUTINE
C     UPDATED         --SEPTEMBER 2008. ACCOMODATE "MISSING" DATA
C     UPDATED         --JANUARY   2010. "BINOMIAL RATIO" HANDLED
C                                       SAME AS "BINOMIAL PROB"
C     UPDATED         --JUNE      2010. CALL LIST TO CMPSTA
C     UPDATED         --JUNE      2010. USE DPDTA5 TO PRINT TABLE
C     UPDATED         --AUGUST    2023. CALL LIST TO CMPSTA
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASCT
      CHARACTER*40 ICTNAM
      CHARACTER*4 ISTARA
      CHARACTER*4 IXVAR
      CHARACTER*4 IX2VAR
      CHARACTER*4 IYVAR
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 IBUGA3
      CHARACTER*4 IERROR
C
      PARAMETER(NUMCLI=6)
      PARAMETER(MAXLIN=2)
      PARAMETER (MAXROW=30)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITL9
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
C
      CHARACTER*8 IYNAM
      CHARACTER*8 IXNAM
      CHARACTER*8 IXNAM2
      CHARACTER*8 IX1NAM
C
      CHARACTER*4 ISUBRO
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IBFLAG
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION Z(*)
      DIMENSION Z2(*)
      DIMENSION XIDTEM(*)
      DIMENSION Y2(*)
      DIMENSION X2(*)
C
      DIMENSION TAG1(*)
      DIMENSION TEMP(*)
      DIMENSION TEMPZ(*)
      DIMENSION TEMPZ2(*)
      DIMENSION XTEMP1(*)
      DIMENSION XTEMP2(*)
      DIMENSION XTEMP3(*)
      DIMENSION XNTRIA(*)
      DIMENSION XACLOW(*)
      DIMENSION XACUPP(*)
      DIMENSION XTEMP8(*)
      DIMENSION XTEMP9(*)
C
      INTEGER ITEMP1(*)
      INTEGER ITEMP2(*)
      INTEGER ITEMP3(*)
      INTEGER ITEMP4(*)
      INTEGER ITEMP5(*)
      INTEGER ITEMP6(*)
C
      DOUBLE PRECISION DTEMP1(*)
      DOUBLE PRECISION DTEMP2(*)
      DOUBLE PRECISION DTEMP3(*)
C
      CHARACTER*4 IOP
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOST.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCR'
      ISUBN2='T0  '
C
      IBFLAG=ICASCT
      IF(IBFLAG.EQ.'BRAT')IBFLAG='BPRO'
      I2=0
      AN=INT(N+0.01)
      ANUMS1=INT(NUMSE1+0.01)
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT0')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,11)
   11   FORMAT('***** AT THE BEGINNING OF DPCRT0--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,12)ICASCT,N,NUMSE1
   12   FORMAT('ICASCT,N,NUMSE1 = ',A4,2I8)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,13)IYVAR,IXVAR,IX2VAR
   13   FORMAT('IYVAR,IXVAR,IX2VAR = ',A4,2X,A4,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,14)IYNAM,IXNAM,IXNAM2
   14   FORMAT('IYNAM,IXNAM,IXNAM2 = ',A8,2X,A8,2X,A8)
        CALL DPWRST('XXX','BUG ')
        DO20I=1,N
          WRITE(ICOUT,16)I,Y(I),Z(I),Z2(I),TAG1(I)
   16     FORMAT('I,Y(I),Z(I),Z2(I),TAG1(I) = ',I8,4G15.7)
          CALL DPWRST('XXX','BUG ')
   20   CONTINUE
      ENDIF
C
C               ***********************************************
C               **  STEP 5--                                 **
C               **  COMPUTE THE VARIOUS CROSS-TAB STATISTICS **
C               ***********************************************
C
      ISTEPN='5.1'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT0')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
C     ALLOW THE   SET WRITE DECIMALS COMMAND    TO BE USED TO
C     DETERMINE HOW MANY DIGITS PRINTED IN TABLE.
C
      NUMDIG=-7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=10
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
C     NOTE 9/2008: IN FOLLOWING, WE NEED TO DISTINGUISH BETWEEN
C                  EMPTY SETS DUE TO NO POINTS FALLING IN THE
C                  PARTICULAR CROSS TABULATION (IN WHICH CASE,
C                  WE WANT TO SKIP IN THE OUTPUT FILE) AND EMPTY
C                  SETS DUE TO ALL POINTS IN THE PARTICULAR
C                  CROSS TABULATION BEING MISSING (IN WHICH CASE,
C                  WE MAY WANT TO WRITE A 0 OR A MISSING VALUE).
C
C                  THERE ARE 2 MISSING VALUES:
C
C                  PSTAMV   - SPECIFIES WHETHER THE INPUT DATA
C                             VALUE IS TO BE INCLUDED IN THE
C                             COMPUTATION OF THE STATISTIC
C
C                  PCTAMV    - IF ALL THE DATA IS MISSING, THIS
C                              IS THE VALUE TO USE IN WRITING THE
C                              CROSS TABULATE OUTPUT.
C
      IWRITE='OFF'
C
      EPS=0.1E-7
      J=0
      K=0
      NRESP=NUMV2-1
C
      ISTEPN='5.2'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT0')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      DO1110ISET1=1,NUMSE1
C
        K=0
        NTEMP2=0
        NTEMP=0
C
        ISTEPN='5.2'
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT0')
     1    CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
        DO1130I=1,N
          IF(XIDTEM(ISET1).EQ.TAG1(I))GOTO1131
          GOTO1130
 1131     CONTINUE
C
          NTEMP2=NTEMP2+1
          IF(IYVAR.EQ.'OFF')THEN
            NTEMP=NTEMP+1
            TEMP(NTEMP)=0.0
          ELSE
            IF(ABS(Y(I)-PSTAMV).GT.EPS)THEN
              NTEMP=NTEMP+1
              TEMP(NTEMP)=Y(I)
              IF(IXVAR.EQ.'ON')TEMPZ(NTEMP)=Z(I)
              IF(IX2VAR.EQ.'ON')TEMPZ2(NTEMP)=Z2(I)
            ENDIF
          ENDIF
 1130   CONTINUE
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT0')THEN
          ISTEPN='5.3'
          CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1133)NTEMP,NTEMP2
 1133     FORMAT('DPCRT0: NTEMP,NTEMP2=',2I8)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
C       AUGUST 2002.  CALL CMPSTA (EXCEPT FOR CHI-SQUARE CASE)
C
C       NTEMP2 IS THE NUMBER OF POINTS IN THE SUBSET AND
C       NTEMP IS THE NUMBER OF NON-MISSING POINTS IN THE SUBSET.
C
        IF(NTEMP2.EQ.0)GOTO1110
C
        IF(NTEMP.EQ.0)THEN
          IF(ICTAMV.EQ.'ZERO')THEN
            STAT=0.0
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              NTRIAL=0
              ALOWLM=0.0
              AUPPLM=0.0
            ENDIF
          ELSEIF(ICTAMV.EQ.'MV  ')THEN
            STAT=PCTAMV
            IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1         ICASCT.EQ.'MDCL')THEN
              NTRIAL=0
              ALOWLM=PCTAMV
              AUPPLM=PCTAMV
            ENDIF
          ELSE
            GOTO1110
          ENDIF
        ELSE
          CALL CMPSTA(
     1              TEMP,TEMPZ,TEMPZ2,XTEMP1,XTEMP2,XTEMP3,
     1              XTEMP8,XTEMP9,
     1              MAXNXT,NTEMP,NTEMP,NTEMP,
     1              NRESP,ICASCT,ISTARA,
     1              ISEED,ITEMP1,ITEMP2,ITEMP3,ITEMP4,ITEMP5,ITEMP6,
     1              DTEMP1,DTEMP2,DTEMP3,
CCCCC1              IQUAME,IQUASE,PSTAMV,
     1              STAT,
     1              ISUBRO,IBUGA3,IERROR)
          IF(IERROR.EQ.'YES')GOTO9000
          IF(IBFLAG.EQ.'BPRO')THEN
            PTEMP=STAT
            NTRIAL=NTEMP
            IF(ICASCT.EQ.'BRAT')NTRIAL=ITEMP1(1)
            IF(STAT.EQ.PSTAMV)THEN
              ALOWLM=PSTAMV
              AUPPLM=PSTAMV
            ELSE
              ALPHAT=ALPHA
              CALL DPAGCO(PTEMP,NTRIAL,ALPHAT,IWRITE,
     1                    ALOWLM,AUPPLM,IBUGA3,IERROR)
            ENDIF
          ELSEIF(ICASCT.EQ.'MECL')THEN
            XMEAN=STAT
            NTRIAL=NTEMP
            IF(STAT.EQ.PSTAMV)THEN
              ALOWLM=PSTAMV
              AUPPLM=PSTAMV
            ELSE
              CALL SD(TEMP,NTEMP,IWRITE,XSD,IBUGA3,IERROR)
              ALPHAT=ALPHA
              CALL DPMECL(XMEAN,XSD,NTEMP,ALPHAT,IWRITE,
     1                    ALOWLM,AUPPLM,IBUGA3,IERROR)
            ENDIF
          ELSEIF(ICASCT.EQ.'MDCL')THEN
            XMED=STAT
            NTRIAL=NTEMP
            IF(STAT.EQ.PSTAMV)THEN
              ALOWLM=PSTAMV
              AUPPLM=PSTAMV
            ELSE
              XQ=0.5
              CALL QUANSE(XQ,TEMP,NTEMP,IWRITE,XTEMP1,MAXNXT,IQUASE,
     1                    QUASE,IBUGA3,IERROR)
              ALPHAT=ALPHA
              CALL DPMECL(XMED,QUASE,NTEMP,ALPHAT,IWRITE,
     1                    ALOWLM,AUPPLM,IBUGA3,IERROR)
            ENDIF
          ENDIF
        ENDIF
C
        J=J+1
        Y2(J)=STAT
        X2(J)=XIDTEM(ISET1)
C
        IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT0')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1136)J
 1136     FORMAT('DPCRT0: J=',I8)
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
        IF(IBFLAG.EQ.'BPRO' .OR. ICASCT.EQ.'MECL' .OR.
     1     ICASCT.EQ.'MDCL')THEN
          XNTRIA(J)=REAL(NTRIAL)
          XACLOW(J)=ALOWLM
          XACUPP(J)=AUPPLM
        ENDIF
C
 1110 CONTINUE
C
      N2=J
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT0')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,1191)J,N2
 1191   FORMAT('DPCRT0: J,N2=',2I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IOP='OPEN'
      IFLG11=1
      IFLG21=0
      IFLG31=0
      IFLAG4=0
      IFLAG5=0
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
CCCCC WRITE OUT TO DPST1F.DAT
C
      WRITE(IOUNI1,2111)ICTNAM
 2111 FORMAT(' GROUP-ID 1          ',A40)
      IF(IBFLAG.EQ.'BPRO')THEN
        DO2170I=1,N2
          IF(IBINTA.EQ.'LOWE')THEN
            WRITE(IOUNI1,2171)X2(I),Y2(I),XNTRIA(I),XACLOW(I)
 2171       FORMAT(4E17.9)
          ELSEIF(IBINTA.EQ.'UPPE')THEN
            WRITE(IOUNI1,2171)X2(I),Y2(I),XNTRIA(I),XACUPP(I)
          ELSE
            WRITE(IOUNI1,2173)X2(I),Y2(I),XNTRIA(I),
     1                        XACLOW(I),XACUPP(I)
 2173       FORMAT(5E17.9)
          ENDIF
 2170   CONTINUE
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        DO2175I=1,N2
          WRITE(IOUNI1,2173)X2(I),Y2(I),XNTRIA(I),
     1                      XACLOW(I),XACUPP(I)
 2175   CONTINUE
      ELSE
        DO2160I=1,N2
          WRITE(IOUNI1,2161)X2(I),Y2(I)
 2161     FORMAT(2E17.9)
 2160   CONTINUE
      ENDIF
C
      IOP='CLOS'
      CALL DPAUFI(IOP,IFLG11,IFLG21,IFLG31,IFLAG4,IFLAG5,
     1            IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1            IBUGA3,ISUBRO,IERROR)
      IF(IERROR.EQ.'YES')GOTO9000
C
C               *****************************
C               **   STEP 6--              **
C               **   WRITE OUT THE TABLE   **
C               *****************************
C
      ISTEPN='6'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CRT0')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      WRITE(ICOUT,999)
  999 FORMAT(1X)
      CALL DPWRST('XXX','BUG ')
C
      IF(IPRINT.EQ.'OFF')GOTO8000
C
      ITITLE(1:15)='Cross Tabulate '
      IF(ICASCT.EQ.'BPRO')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Binomial Probabilities'
      ELSEIF(ICASCT.EQ.'MECL')THEN
        NCTITL=37
        ITITLE(16:NCTITL)='Mean Confidence Limits'
      ELSEIF(ICASCT.EQ.'MDCL')THEN
        NCTITL=39
        ITITLE(16:NCTITL)='Median Confidence Limits'
      ELSE
        ITITLE(16:55)=ICTNAM(1:40)
        NCTITL=55
        DO4010I=55,1,-1
          IF(ITITLE(I:I).NE.' ')THEN
            NCTITL=I
            GOTO4019
          ENDIF
 4010   CONTINUE
 4019   CONTINUE
      ENDIF
C
      IF(IYVAR.EQ.'ON')THEN
        ITITL9(1:21)='(Response Variables: '
        NTEMP=21
        ITITL9(22:30)=IYNAM(1:8)
        NTEMP=30
        IF(IXVAR.EQ.'ON')THEN
          ITITL9(30:30)=' '
          ITITL9(31:38)=IXNAM(1:8)
          NTEMP=38
        ENDIF
        IF(IX2VAR.EQ.'ON')THEN
          ITITL9(39:39)=' '
          ITITL9(40:47)=IXNAM2(1:8)
          NTEMP=47
        ENDIF
        NTEMP=NTEMP+1
        ITITL9(NTEMP:NTEMP)=')'
        NCTIT9=NTEMP
      ELSE
        ITITL9=' '
        NCTIT9=0
      ENDIF
C
      ITITL2(1,1)(1:8)=IX1NAM
      NCTIT2(1,1)=8
      ITITL2(1,2)='   |   '
      IF(ICAPTY.EQ.'LATE')THEN
        ITITL2(1,2)='  $|$  '
      ENDIF
      NCTIT2(1,2)=7
C
      NUMLIN=1
      IF(ICASCT.EQ.'BPRO')THEN
        NUMCOL=6
        IF(IBINTA.EQ.'LOWE' .OR. IBINTA.EQ.'UPPE')NUMCOL=5
        ITITL2(1,3)='P'
        NCTIT2(1,3)=1
        ITITL2(1,4)='N'
        NCTIT2(1,4)=1
        IF(IBINTA.EQ.'LOWE')THEN
          ITITL2(1,5)(1:40)='Lower AC'
          NCTIT2(1,5)=8
        ELSEIF(IBINTA.EQ.'UPPE')THEN
          ITITL2(1,5)(1:40)='Upper AC'
          NCTIT2(1,5)=8
        ELSE
          ITITL2(1,5)(1:40)='Lower AC'
          NCTIT2(1,5)=8
          ITITL2(1,6)(1:40)='Upper AC'
          NCTIT2(1,6)=8
        ENDIF
      ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
        NUMCOL=6
        IF(ICASCT.EQ.'MECL')THEN
          ITITL2(1,3)='Mean'
          NCTIT2(1,3)=4
        ELSE
          ITITL2(1,3)='Median'
          NCTIT2(1,3)=6
        ENDIF
        ITITL2(1,4)='N'
        NCTIT2(1,4)=1
        ITITL2(1,5)='Lower Limit'
        ITITL2(1,6)='Upper Limit'
        NCTIT2(1,5)=11
        NCTIT2(1,6)=11
      ELSE
        NUMCOL=3
        ITITL2(1,3)(1:15)=ICTNAM(1:15)
        NTEMP=15
        DO4070I=15,1,-1
          IF(ITITL2(1,3)(I:I).NE.' ')THEN
            NTEMP=I
            GOTO4079
          ENDIF
 4070   CONTINUE
 4079   CONTINUE
        NCTIT2(1,3)=NTEMP
      ENDIF
C
      NMAX=0
      DO4210I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(NCTIT2(1,I).GT.NTOT(I))NTOT(I)=NCTIT2(1,I)
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
        IF(I.EQ.4)THEN
          NTOT(I)=8
          IDIGIT(I)=0
        ELSEIF(I.EQ.2)THEN
          ITYPCO(I)='ALPH'
          NTOT(I)=7
          IDIGIT(I)=-1
        ENDIF
 4210 CONTINUE
C
      IWHTML(1)=125
      IWHTML(2)=25
      IWHTML(3)=125
      IWHTML(4)=75
      IWHTML(5)=125
      IWHTML(6)=125
      IJUNK=1400
      IWRTF(1)=IJUNK
      IWRTF(2)=IWRTF(1)+200
      IWRTF(3)=IWRTF(2)+IJUNK
      IWRTF(4)=IWRTF(3)+800
      IWRTF(5)=IWRTF(4)+IJUNK
      IWRTF(6)=IWRTF(5)+IJUNK
      IFRST=.TRUE.
      IFLAGS=.TRUE.
      ILAST=.TRUE.
      IFLAGE=.FALSE.
C
      ICALL=0
      ICNT=0
      DO4310I=1,N2
        IF(ICNT.GE.30)THEN
          IF(I.EQ.N2)IFLAGE=.TRUE.
          CALL DPDTA5(ITITLE,NCTITL,
     1                ITITL9,NCTIT9,ITITL2,NCTIT2,
     1                MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1                IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1                IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1                ICAPSW,ICAPTY,IFRST,ILAST,
     1                IFLAGS,IFLAGE,
     1                ISUBRO,IBUGA3,IERROR)
          IFRST=.FALSE.
          IFLAGS=.FALSE.
          ICALL=1
          ICNT=0
        ENDIF
        ICNT=ICNT+1
        NCTEXT(ICNT)=0
        AMAT(ICNT,1)=X2(I)
        AMAT(ICNT,2)=0.0
        AMAT(ICNT,3)=Y2(I)
        IF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'LOWE')THEN
          AMAT(ICNT,4)=XNTRIA(I)
          AMAT(ICNT,5)=XACLOW(I)
        ELSEIF(ICASCT.EQ.'BPRO' .AND. IBINTA.EQ.'UPPE')THEN
          AMAT(ICNT,4)=XNTRIA(I)
          AMAT(ICNT,5)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'BPRO')THEN
          AMAT(ICNT,4)=XNTRIA(I)
          AMAT(ICNT,5)=XACLOW(I)
          AMAT(ICNT,6)=XACUPP(I)
        ELSEIF(ICASCT.EQ.'MECL' .OR. ICASCT.EQ.'MDCL')THEN
          AMAT(ICNT,4)=XNTRIA(I)
          AMAT(ICNT,5)=XACLOW(I)
          AMAT(ICNT,6)=XACUPP(I)
        ENDIF
        DO4320J=1,NUMCOL
          IF(J.EQ.2)THEN
            NCVALU(ICNT,J)=7
            IVALUE(ICNT,J)='   |   '
            IF(ICAPTY.EQ.'LATE')THEN
              IVALUE(ICNT,J)='  $|$  '
            ENDIF
          ELSE
            NCVALU(ICNT,J)=0
            IVALUE(ICNT,J)=' '
          ENDIF
 4320   CONTINUE
 4310 CONTINUE
C
      IF(ICNT.GE.1)THEN
        IFLAGE=.TRUE.
        ILAST=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
 8000 CONTINUE
      IF(IFEEDB.EQ.'ON')THEN
        WRITE(ICOUT,9212)
 9212   FORMAT(6X,'GROUP-IDs AND STATISTIC WRITTEN TO FILE ',
     1         'DPST1F.DAT')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C               ******************
C               **   STEP 90--  **
C               **   EXIT       **
C               ******************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'CRT0')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPCRT0--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)ICASCT,N,NUMV2,NUMSE1,N2,IERROR
 9012   FORMAT('ICASCT,N,NUMV2,NUMSE1,N2,IERROR = ',A4,4I8,2X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9016)ANUMS1
 9016   FORMAT('ANUMS1 = ',E15.7)
        CALL DPWRST('XXX','BUG ')
        DO9020I=1,N2
          WRITE(ICOUT,9021)I,Y2(I),X2(I)
 9021     FORMAT('I,Y2(I),X2(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','BUG ')
 9020   CONTINUE
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPCSR2(Y,X,N,ICASAN,MAXNXT,TEMP1,TEMP2,
     1                  IVARID,IVARI2,IVARI3,IVARI4,
     1                  XMIN,XMAX,YMIN,YMAX,
     1                  ICAPSW,ICAPTY,IFORSW,
     1                  STATV1,STATV2,STATC2,PVAL2,
     1                  CV01,CV02,CV05,CV10,CV15,CV25,CV50,
     1                  CV75,CV85,CV90,CV95,CV98,CV99,
     1                  CUTL90,CUTU90,CUTL95,CUTU95,CUTL99,CUTU99,
     1                  ISUBRO,IBUGA3,IERROR)
C
C     PURPOSE--THIS ROUTINE CARRIES OUT SEVERAL TESTS FOR COMPLETE
C              SPATIAL RANDOMNESS:
C
C                 1. BIVARAITE CRAMER VON-MISES TEST (I.E., BIVARIATE
C                    UNIFORMITY.
C
C                 2. MEAN NEAREST NEIGHBORS DISTANCE TEST.
C
C                 3. POLLARD'S STATISTIC (FOR DISTANCE INDEX
C                    1, 2, 3, 4, 5)
C
C     EXAMPLE--COMPLETE SPATIAL RANDOMNESS Y X
C     REFERENCES--DALE ZIMMERMAN (1993), "A BIVARIATE CRAMER-VON MISES
C                 TYPE OF TEST FOR SPATIAL RANDONNESS", JOURNAL OF
C                 THE ROYAL STATISTICAL SOCIETY, SERIES C (APPLIED
C                 STATISTICS, VOL. 42, NO. 1, PP. 43-54.
C                 LONDON.
C               --CLARK AND EVANS (1954), "DISTANCE TO NEAREST
C                 NEIGHBOR AS A MEASURE OF SPATIAL RELATIONSHIPS
C                 IN POPULATIONS", ECOLOGY, 35, PP. 23-30.
C               --DONNELLY (1978), "SIMULATIONS TO DETERMINE THE
C                 VARIANCE AND EDGE-EFFECT OF TOTAL NEAREST-NEIGHBOR
C                 DISTANCE", IN SIMULATION STUDIES IN ARCHAEOLOGY
C                 (ED. HODDER), PP. 91-95, LONDON: CAMBRIDGE UNIVERSITY
C                 PRESS.
C               --FORTIN AND DALE (2005), "SPATIAL ANALYSIS: A GUIDE
C                 FOR ECOLOGISTS", CAMBRIDGE UNIVERSITY PRESS, PP. 34-35.
C               --POLLARD (1971), "ON DISTANCE ESTIMATORS OF DENSITY
C                 IN RANDOMLY DISTRIBUTED FORESTS", BIOMETRICS, 27,
C                 991-1002.
C               --LIU (2001), "A COMPARISON OF FIVE DISTANCE-BASED
C                 METHODS FOR PATTERN ANALYSIS", JOURNAL OF VEGETATION
C                 SCIENCE, 12, 411-416.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2014/1
C     ORIGINAL VERSION--JANUARY   2014.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 ICASAN
      CHARACTER*4 IERROR
      CHARACTER*4 IVARID
      CHARACTER*4 IVARI2
      CHARACTER*4 IVARI3
      CHARACTER*4 IVARI4
      CHARACTER*4 ICAPSW
      CHARACTER*4 ICAPTY
      CHARACTER*4 IFORSW
      CHARACTER*4 ISUBRO
      CHARACTER*4 IBUGA3
C
      CHARACTER*4 IWRITE
C
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
      PARAMETER (NUMALP=7)
      REAL ALPHA(NUMALP)
      REAL ALOWER(NUMALP)
      REAL AUPPER(NUMALP)
C
      PARAMETER(NUMCLI=5)
      PARAMETER(MAXLIN=3)
      PARAMETER (MAXROW=50)
      CHARACTER*60 ITITLE
      CHARACTER*60 ITITLZ
      CHARACTER*40 ITITL9
      CHARACTER*60 ITEXT(MAXROW)
      CHARACTER*4  ALIGN(NUMCLI)
      CHARACTER*4  VALIGN(NUMCLI)
      REAL         AVALUE(MAXROW)
      INTEGER      NCTEXT(MAXROW)
      INTEGER      IDIGIT(MAXROW)
      INTEGER      NTOT(MAXROW)
      CHARACTER*60 ITITL2(MAXLIN,NUMCLI)
      CHARACTER*15 IVALUE(MAXROW,NUMCLI)
      CHARACTER*4  ITYPCO(NUMCLI)
      INTEGER      NCTIT2(MAXLIN,NUMCLI)
      INTEGER      NCVALU(MAXROW,NUMCLI)
      INTEGER      IWHTML(NUMCLI)
      INTEGER      IWRTF(NUMCLI)
      REAL         AMAT(MAXROW,NUMCLI)
      LOGICAL IFRST
      LOGICAL ILAST
      LOGICAL IFLAGS
      LOGICAL IFLAGE
C
C---------------------------------------------------------------------
C
      DIMENSION Y(*)
      DIMENSION X(*)
      DIMENSION TEMP1(*)
      DIMENSION TEMP2(*)
C
      DIMENSION STATV3(5)
      DIMENSION STATV4(5)
      DIMENSION STATC3(5)
      DIMENSION STATP3(5)
      DIMENSION STATN3(5)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
      DATA ALPHA/
     1 50.0, 75.0, 80.0, 90.0, 95.0, 99.0, 99.9/
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPCS'
      ISUBN2='R2  '
      IERROR='NO'
      STATV1=CPUMIN
      STATV2=CPUMIN
      STATC2=CPUMIN
      PVAL2=CPUMIN
      CUTL90=CPUMIN
      CUTU90=CPUMIN
      CUTL95=CPUMIN
      CUTU95=CPUMIN
      CUTL99=CPUMIN
      CUTU99=CPUMIN
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,51)
   51   FORMAT('**** AT THE BEGINNING OF DPCSR2--')
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,52)ISUBRO,IBUGA3,ICASAN,N,MAXNXT
   52   FORMAT('ISUBRO,IBUGA3,ICASAN,N,MAXNXT = ',3(A4,2X),2I8)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,54)XMIN,XMAX,YMIN,YMAX
   54   FORMAT('XMIN,XMAX,YMIN,YMAX = ',4G15.7)
        CALL DPWRST('XXX','WRIT')
        DO56I=1,N
          WRITE(ICOUT,57)I,Y(I),X(I)
   57     FORMAT('I,Y(I),X(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','WRIT')
   56   CONTINUE
      ENDIF
C
C               ********************************************
C               **  STEP 11--                             **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      ISTEPN='11'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(N.LT.5)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,1111)
 1111   FORMAT('***** ERROR IN COMPLETE SPATIAL RANDOMNESS TEST--')
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,1113)
 1113   FORMAT('      THE NUMBER OF OBSERVATIONS IS LESS THAN 5.')
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,1114)N
 1114   FORMAT('SAMPLE SIZE = ',I8)
        CALL DPWRST('XXX','WRIT')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      HOLD=Y(1)
      DO1135I=2,N
        IF(Y(I).NE.HOLD)GOTO1139
 1135 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','WRIT')
      WRITE(ICOUT,1111)
      CALL DPWRST('XXX','WRIT')
      WRITE(ICOUT,1131)HOLD
 1131 FORMAT('      THE RESPONSE VARIABLE HAS ALL ELEMENTS = ',G15.7)
      CALL DPWRST('XXX','WRIT')
      IERROR='YES'
      GOTO9000
 1139 CONTINUE
C
C               ******************************
C               **  STEP 21--               **
C               **  CARRY OUT CALCULATIONS  **
C               ******************************
C
      ISTEPN='41'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      XMINZ=XMIN
      XMAXZ=XMAX
      YMINZ=YMIN
      YMAXZ=YMAX
      IWRITE='OFF'
      CALL MINIM(X,N,IWRITE,XMIND,IBUGA3,IERROR)
      CALL MAXIM(X,N,IWRITE,XMAXD,IBUGA3,IERROR)
      CALL MINIM(Y,N,IWRITE,YMIND,IBUGA3,IERROR)
      CALL MAXIM(Y,N,IWRITE,YMAXD,IBUGA3,IERROR)
C
      CALL DPCSR3(X,Y,N,TEMP1,TEMP2,
     1            XMIN,XMAX,YMIN,YMAX,
     1            STATV1,
     1            CV01,CV02,CV05,CV10,CV15,CV25,CV50,
     1            CV75,CV85,CV90,CV95,CV98,CV99,
     1            ISUBRO,IBUGA3,IERROR)
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')THEN
        WRITE(ICOUT,2111)STATV1,CV05,CV10,CV90,CV95
 2111   FORMAT('STATV1,CV05,CV10,CV90,CV95 = ',5G15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      CALL DPCSR4(X,Y,N,TEMP1,TEMP2,
     1            STATV2,STATC2,PVAL2,
     1            ISUBRO,IBUGA3,IERROR)
C
      DO2140I=1,NUMALP
        PCONF=ALPHA(I)/100.0
        CDF=PCONF
        CDF=0.5+(PCONF/2.0)
        CALL NORPPF(CDF,PPF)
        ALOWER(I)=-PPF
        AUPPER(I)=PPF
 2140 CONTINUE
      CUTL50=ALOWER(1)
      CUTU50=AUPPER(1)
      CUTL75=ALOWER(2)
      CUTU75=AUPPER(2)
      CUTL80=ALOWER(3)
      CUTU80=AUPPER(3)
      CUTL90=ALOWER(4)
      CUTU90=AUPPER(4)
      CUTL95=ALOWER(5)
      CUTU95=AUPPER(5)
      CUTL99=ALOWER(6)
      CUTU99=AUPPER(6)
      CTL999=ALOWER(7)
      CTU999=AUPPER(7)
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')THEN
        WRITE(ICOUT,2141)STATV2,STATC2,PVAL2
 2141   FORMAT('STATV2,STATC2,PVAL2 = ',3G15.7)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,2143)CUTL90,CUTU90,CUTL95,CUTU95,CUTL99,CUTU99
 2143   FORMAT('CUTL90,CUTU90,CUTL95,CUTU95,CUTL99,CUTU99=',6G15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
C
C               ***************************************
C               **   STEP 42--                       **
C               **   WRITE OUT EVERYTHING FOR        **
C               **   BIVARIATE CRAMER VON-MISES TEST **
C               ***************************************
C
      ISTEPN='42'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(IPRINT.EQ.'OFF')GOTO9000
C
      NUMDIG=7
      IF(IFORSW.EQ.'1')NUMDIG=1
      IF(IFORSW.EQ.'2')NUMDIG=2
      IF(IFORSW.EQ.'3')NUMDIG=3
      IF(IFORSW.EQ.'4')NUMDIG=4
      IF(IFORSW.EQ.'5')NUMDIG=5
      IF(IFORSW.EQ.'6')NUMDIG=6
      IF(IFORSW.EQ.'7')NUMDIG=7
      IF(IFORSW.EQ.'8')NUMDIG=8
      IF(IFORSW.EQ.'9')NUMDIG=9
      IF(IFORSW.EQ.'0')NUMDIG=0
      IF(IFORSW.EQ.'E')NUMDIG=-2
      IF(IFORSW.EQ.'-2')NUMDIG=-2
      IF(IFORSW.EQ.'-3')NUMDIG=-3
      IF(IFORSW.EQ.'-4')NUMDIG=-4
      IF(IFORSW.EQ.'-5')NUMDIG=-5
      IF(IFORSW.EQ.'-6')NUMDIG=-6
      IF(IFORSW.EQ.'-7')NUMDIG=-7
      IF(IFORSW.EQ.'-8')NUMDIG=-8
      IF(IFORSW.EQ.'-9')NUMDIG=-9
C
      ITITLE='Bivariate Cramer Von-Mises Test'
      NCTITL=31
      ITITLZ='for Complete Spatial Randomness'
      NCTITZ=31
C
      ICNT=1
      ITEXT(ICNT)=' '
      NCTEXT(ICNT)=0
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
      ICNT=ICNT+1
      ITEXT(ICNT)='First Response Variable: '
      WRITE(ITEXT(ICNT)(26:29),'(A4)')IVARID(1:4)
      WRITE(ITEXT(ICNT)(30:35),'(A4)')IVARI2(1:4)
      NCTEXT(ICNT)=35
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
      ICNT=ICNT+1
      ITEXT(ICNT)='Second Response Variable: '
      WRITE(ITEXT(ICNT)(27:30),'(A4)')IVARI3(1:4)
      WRITE(ITEXT(ICNT)(31:36),'(A4)')IVARI4(1:4)
      NCTEXT(ICNT)=36
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
C
      ICNT=ICNT+1
      ITEXT(ICNT)=' '
      NCTEXT(ICNT)=1
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
C
      ICNT=ICNT+1
      ITEXT(ICNT)='H0: Complete Spatial Randomness'
      NCTEXT(ICNT)=31
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
      ICNT=ICNT+1
      ITEXT(ICNT)='Ha: Not Complete Spatial Randomness'
      NCTEXT(ICNT)=35
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
C
      ICNT=ICNT+1
      ITEXT(ICNT)=' '
      NCTEXT(ICNT)=1
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
      ICNT=ICNT+1
      ITEXT(ICNT)='Number of Observations:'
      NCTEXT(ICNT)=23
      AVALUE(ICNT)=REAL(N)
      IDIGIT(ICNT)=0
      ICNT=ICNT+1
      ITEXT(ICNT)='Data Minimum for X:'
      NCTEXT(ICNT)=19
      AVALUE(ICNT)=XMIND
      IDIGIT(ICNT)=NUMDIG
      ICNT=ICNT+1
      ITEXT(ICNT)='Data Maximum for X:'
      NCTEXT(ICNT)=19
      AVALUE(ICNT)=XMAXD
      IDIGIT(ICNT)=NUMDIG
      ICNT=ICNT+1
      ITEXT(ICNT)='Data Minimum for Y:'
      NCTEXT(ICNT)=19
      AVALUE(ICNT)=YMIND
      IDIGIT(ICNT)=NUMDIG
      ICNT=ICNT+1
      ITEXT(ICNT)='Data Maximum for Y:'
      NCTEXT(ICNT)=19
      AVALUE(ICNT)=YMAXD
      IDIGIT(ICNT)=NUMDIG
C
      IFLAGT=1
      IF(XMINZ.EQ.CPUMIN)IFLAGT=0
      IF(XMAXZ.EQ.CPUMIN)IFLAGT=0
      IF(YMINZ.EQ.CPUMIN)IFLAGT=0
      IF(YMAXZ.EQ.CPUMIN)IFLAGT=0
      IF(IFLAGT.EQ.1)THEN
        ICNT=ICNT+1
        ITEXT(ICNT)='User Specified Minimum for X:'
        NCTEXT(ICNT)=29
        AVALUE(ICNT)=XMINZ
        IDIGIT(ICNT)=NUMDIG
        ICNT=ICNT+1
        ITEXT(ICNT)='User Specified Maximum for X:'
        NCTEXT(ICNT)=29
        AVALUE(ICNT)=XMAXZ
        IDIGIT(ICNT)=NUMDIG
        ICNT=ICNT+1
        ITEXT(ICNT)='User Specified Minimum for Y:'
        NCTEXT(ICNT)=29
        AVALUE(ICNT)=YMINZ
        IDIGIT(ICNT)=NUMDIG
        ICNT=ICNT+1
        ITEXT(ICNT)='User Specified Maximum for Y:'
        NCTEXT(ICNT)=29
        AVALUE(ICNT)=YMAXZ
        IDIGIT(ICNT)=NUMDIG
      ENDIF
C
      ICNT=ICNT+1
      ITEXT(ICNT)=' '
      NCTEXT(ICNT)=1
      AVALUE(ICNT)=0.0
      IDIGIT(ICNT)=-1
      ICNT=ICNT+1
      ITEXT(ICNT)='Test Statistic Value:'
      NCTEXT(ICNT)=21
      AVALUE(ICNT)=STATV1
      IDIGIT(ICNT)=NUMDIG
C
      NUMROW=ICNT
      DO4210I=1,NUMROW
        NTOT(I)=15
 4210 CONTINUE
C
      IFRST=.TRUE.
      ILAST=.TRUE.
C
      ISTEPN='42A'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL DPDTA1(ITITLE,NCTITL,ITITLZ,NCTITZ,ITEXT,NCTEXT,
     1            AVALUE,IDIGIT,
     1            NTOT,NUMROW,
     1            ICAPSW,ICAPTY,ILAST,IFRST,
     1            ISUBRO,IBUGA3,IERROR)
C
      ISTEPN='42B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      ITITLE=' '
      NCTITL=0
C
      ITITL9=' '
      NCTIT9=0
      ITITLE(1:44)='Percent Points of the Reference Distribution'
      NCTITL=44
      NUMLIN=1
      NUMROW=13
      NUMCOL=3
      ITITL2(1,1)='Percent Point'
      ITITL2(1,2)=' '
      ITITL2(1,3)='Value'
      NCTIT2(1,1)=13
      NCTIT2(1,2)=1
      NCTIT2(1,3)=5
C
      NMAX=0
      DO4221I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(I.EQ.2)NTOT(I)=5
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=NUMDIG
        ITYPCO(I)='NUME'
 4221 CONTINUE
      ITYPCO(2)='ALPH'
      IDIGIT(1)=2
      IDIGIT(3)=3
      DO4223I=1,NUMROW
        DO4225J=1,NUMCOL
          NCVALU(I,J)=0
          IVALUE(I,J)=' '
          NCVALU(I,J)=0
          AMAT(I,J)=0.0
          IF(J.EQ.1)THEN
            IF(I.EQ.1)THEN
              AMAT(I,1)=0.01
            ELSEIF(I.EQ.2)THEN
              AMAT(I,1)=0.02
            ELSEIF(I.EQ.3)THEN
              AMAT(I,1)=0.05
            ELSEIF(I.EQ.4)THEN
              AMAT(I,1)=0.10
            ELSEIF(I.EQ.5)THEN
              AMAT(I,1)=0.15
            ELSEIF(I.EQ.6)THEN
              AMAT(I,1)=0.25
            ELSEIF(I.EQ.7)THEN
              AMAT(I,1)=0.50
            ELSEIF(I.EQ.8)THEN
              AMAT(I,1)=0.75
            ELSEIF(I.EQ.9)THEN
              AMAT(I,1)=0.85
            ELSEIF(I.EQ.10)THEN
              AMAT(I,1)=0.90
            ELSEIF(I.EQ.11)THEN
              AMAT(I,1)=0.95
            ELSEIF(I.EQ.12)THEN
              AMAT(I,1)=0.98
            ELSEIF(I.EQ.13)THEN
              AMAT(I,1)=0.99
            ENDIF
          ELSEIF(J.EQ.2)THEN
            IVALUE(I,J)='='
            NCVALU(I,J)=1
          ELSEIF(J.EQ.3)THEN
            IF(I.EQ.1)THEN
              AMAT(I,J)=CV01
            ELSEIF(I.EQ.2)THEN
              AMAT(I,J)=CV02
            ELSEIF(I.EQ.3)THEN
              AMAT(I,J)=CV05
            ELSEIF(I.EQ.4)THEN
              AMAT(I,J)=CV10
            ELSEIF(I.EQ.5)THEN
              AMAT(I,J)=CV15
            ELSEIF(I.EQ.6)THEN
              AMAT(I,J)=CV25
            ELSEIF(I.EQ.7)THEN
              AMAT(I,J)=CV50
            ELSEIF(I.EQ.8)THEN
              AMAT(I,J)=CV75
            ELSEIF(I.EQ.9)THEN
              AMAT(I,J)=CV85
            ELSEIF(I.EQ.10)THEN
              AMAT(I,J)=CV90
            ELSEIF(I.EQ.11)THEN
              AMAT(I,J)=CV95
            ELSEIF(I.EQ.12)THEN
              AMAT(I,J)=CV98
            ELSEIF(I.EQ.13)THEN
              AMAT(I,J)=CV99
            ENDIF
          ENDIF
 4225   CONTINUE
 4223 CONTINUE
C
      IWHTML(1)=150
      IWHTML(2)=50
      IWHTML(3)=150
      IWRTF(1)=2000
      IWRTF(2)=IWRTF(1)+500
      IWRTF(3)=IWRTF(2)+2000
      IFRST=.TRUE.
      ILAST=.FALSE.
C
      ISTEPN='42C'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      CALL DPDTA4(ITITL9,NCTIT9,
     1            ITITLE,NCTITL,ITITL2,NCTIT2,
     1            MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1            IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,NUMROW,
     1            IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1            ICAPSW,ICAPTY,IFRST,ILAST,
     1            ISUBRO,IBUGA3,IERROR)
C
      ISTEPN='42D'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'CSR2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      ITITL9=' '
      NCTIT9=0
      ITITLE='Conclusions (Two-Tailed Test)'
      NCTITL=29
      NUMLIN=2
      NUMROW=4
      NUMCOL=4
      ITITL2(1,1)=' '
      ITITL2(2,1)='Alpha'
      NCTIT2(1,1)=0
      NCTIT2(2,1)=5
      ITITL2(1,2)='Lower'
      ITITL2(2,2)='Critical Value'
      NCTIT2(1,2)=5
      NCTIT2(2,2)=14
      ITITL2(1,3)='Upper'
      ITITL2(2,3)='Critical Value'
      NCTIT2(1,3)=5
      NCTIT2(2,3)=14
      ITITL2(1,4)=' '
      ITITL2(2,4)='Conclusion'
      NCTIT2(1,4)=0
      NCTIT2(2,4)=10
C
      NMAX=0
      DO4321I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        IF(I.EQ.1)NTOT(I)=7
        IF(I.EQ.2 .OR. I.EQ.3)NTOT(I)=17
        NMAX=NMAX+NTOT(I)
        IDIGIT(I)=3
        ITYPCO(I)='ALPH'
        IF(I.EQ.2 .OR. I.EQ.3)ITYPCO(I)='NUME'
 4321 CONTINUE
      IDIGIT(1)=0
      IDIGIT(4)=0
      DO4323I=1,NUMROW
        DO4325J=1,NUMCOL
          NCVALU(I,J)=0
          IVALUE(I,J)=' '
          NCVALU(I,J)=0
          AMAT(I,J)=0.0
 4325   CONTINUE
 4323 CONTINUE
      IVALUE(1,1)='20%'
      IVALUE(2,1)='10%'
      IVALUE(3,1)='4%'
      IVALUE(4,1)='2%'
      NCVALU(1,1)=3
      NCVALU(2,1)=3
      NCVALU(3,1)=2
      NCVALU(4,1)=2
      IVALUE(1,4)='Accept H0'
      IVALUE(2,4)='Accept H0'
      IVALUE(3,4)='Accept H0'
      IVALUE(4,4)='Accept H0'
      NCVALU(1,4)=9
      NCVALU(2,4)=9
      NCVALU(3,4)=9
      NCVALU(4,4)=9
      IF(STATV1.LT.CV10 .OR. STATV1.GT.CV90)IVALUE(1,4)='Reject H0'
