      IFOUND='YES'
      IERROR='NO'
C
      ICASA2='BOTH'
      IF(ICASAN.EQ.'CSLT')THEN
        ICASAN='CSTE'
        ICASA2='LOWE'
      ELSEIF(ICASAN.EQ.'CSUT')THEN
        ICASAN='CSTE'
        ICASA2='UPPE'
      ELSEIF(ICASAN.EQ.'CS2T')THEN
        ICASAN='CSTE'
        ICASA2='TWOT'
      ENDIF
C
C               ***************************************
C               **  TREAT THE CHI-SQUARED TEST CASE  **
C               ***************************************
C
      IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'CSTE')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
