            IVALUE(J,2)(1:5)='75.0%'
            IF(PVAL2.GE.0.25)IVALUE(J,4)(1:6)='ACCEPT'
          ELSEIF(J.EQ.3)THEN
            IVALUE(J,2)(1:5)='90.0%'
