        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPGCI--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,52)NPTS,NLAB
   52   FORMAT('NPTS,NLAB = ',2I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IFLAG=0
      DO100I=1,NLAB
        IINDX1=I
        IINDX2=I+NLAB
        DTEMP1(IINDX1)=DBLE(AMEAN(I))
        DTEMP1(IINDX2)=DBLE(ASD(I))**2
        NITEMP=ABS(N(I))
        IF(NITEMP.LE.1)IFLAG=1
  100 CONTINUE
      IINDX1=1
      IINDX2=1+NLAB
      IINDX3=1+2*NLAB
      IINDX4=1+3*NLAB
C
      DALPHA=0.95
      NRUN=10000
      IERROR='NO'
C
      IF(NLAB.GT.1.AND.IFLAG.EQ.0)THEN
        CALL GCI1(NLAB,N,DTEMP1(IINDX1),DTEMP1(IINDX2),
     1            DALPHA,NRUN,DTERM1,
     1            DLOWGC,DHIGGC,DTERM2,
     1            DTEMP1(IINDX3),DTEMP1(IINDX4),DTEMP2,
     1            IERROR)
        SEGCI=REAL(DTERM2)
        IF(IERROR.EQ.'YES')THEN
           XGCI=0.0
           SEGCI=0.0
           DLOWGC=0.0D0
           DHIGGC=0.0D0
           GOTO9000
        ELSE
           XGCI=REAL(DTERM1)
        ENDIF
      ELSE
        XGCI=0.0
        SEGCI=0.0
        DLOWGC=0.0D0
        DHIGGC=0.0D0
        GOTO9000
      ENDIF
