 2331     FORMAT('I,STATVA,CUT99,CUT975,CUT95 = ',I8,4G15.7)
          CALL DPWRST('XXX','WRIT')
          WRITE(ICOUT,2333)CUT90,CUT75,CUT50
 2333     FORMAT('CUT90,CUT75,CUT50 = ',5G15.7)
          CALL DPWRST('XXX','WRIT')
