        NCTITZ=0
C
        ICNT=1
        ITEXT(ICNT)=' '
        NCTEXT(ICNT)=1
        AVALUE(ICNT)=0.0
        IDIGIT(ICNT)=-1
        ICNT=ICNT+1
        ITEXT(ICNT)='H0: There are no outliers'
        NCTEXT(ICNT)=25
        AVALUE(ICNT)=0.0
        IDIGIT(ICNT)=-1
        ICNT=ICNT+1
C
        IF(IRT.EQ.1)THEN
          ITEXT(ICNT)(1:21)='Ha: There is exactly '
          WRITE(ITEXT(ICNT)(22:26),'(I5)')IRT
          ITEXT(ICNT)(27:34)=' outlier'
          NCTEXT(ICNT)=34
        ELSE
          ITEXT(ICNT)(1:22)='Ha: There are exactly '
          WRITE(ITEXT(ICNT)(23:27),'(I5)')IRT
          ITEXT(ICNT)(28:36)=' outliers'
          NCTEXT(ICNT)=36
        ENDIF
        AVALUE(ICNT)=0.0
        IDIGIT(ICNT)=-1
C
        ICNT=ICNT+1
        ITEXT(ICNT)='Potential Outlier Value Tested at This Step:      '
        NCTEXT(ICNT)=50
        AVALUE(ICNT)=Y(ITEMP2(I))
        IDIGIT(ICNT)=NUMDIG
C
        ICNT=ICNT+1
        ITEXT(ICNT)=' '
        NCTEXT(ICNT)=1
        AVALUE(ICNT)=0.0
        IDIGIT(ICNT)=-1
        ICNT=ICNT+1
        ITEXT(ICNT)='Extreme Studentized Deviate Test Statistic Value:'
        NCTEXT(ICNT)=50
        AVALUE(ICNT)=STATVA
        IDIGIT(ICNT)=NUMDIG
C
        IF(N.LT.NCUT)THEN
          ICNT=ICNT+1
          ITEXT(ICNT)='CDF Value:'
          NCTEXT(ICNT)=10
          AVALUE(ICNT)=STATCD
          IDIGIT(ICNT)=NUMDIG
          ICNT=ICNT+1
          ITEXT(ICNT)='P-Value:'
          NCTEXT(ICNT)=7
          AVALUE(ICNT)=PVAL
          IDIGIT(ICNT)=NUMDIG
          ICNT=ICNT+1
          ITEXT(ICNT)=' '
          NCTEXT(ICNT)=1
          AVALUE(ICNT)=0.0
          IDIGIT(ICNT)=-1
        ENDIF
C
        NUMROW=ICNT
        DO4210II=1,NUMROW
          NTOT(II)=15
 4210   CONTINUE
C
        IFRST=.TRUE.
        ILAST=.FALSE.
