        ITITL2(1,3)='Critical Value'
        ITITL2(1,4)='Conclusion'
        NCTIT2(1,1)=5
        NCTIT2(1,2)=3
        NCTIT2(1,3)=14
        NCTIT2(1,4)=10
C
        NMAX=0
        DO4321II=1,NUMCOL
          ALIGN(II)='r'
          NTOT(II)=15
          IF(II.EQ.1 .OR. II.EQ.2)NTOT(II)=7
          IF(II.EQ.3)NTOT(II)=17
          NMAX=NMAX+NTOT(II)
