 9101   FORMAT('ICALL(81:160) = ',A80)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9103)ICALL(161:240)
 9103   FORMAT('ICALL(161:240) = ',A80)
        CALL DPWRST('XXX','BUG ')
