      SUBROUTINE DPLACL(ICOM,IARG,IHARG,NUMARG,IDEFCO,
     1                  IX1LCO,IX2LCO,IX3LCO,IY1LCO,IY2LCO,
     1                  IX1LC2,IX2LC2,IX3LC2,IY1LC2,IY2LC2,IRGBMX,
