     1       IHARG(2).EQ.'AUTO' .OR. IHARG(2).EQ.'DEFA')THEN
            PX1LXC=CPUMIN
          ELSEIF(IARGT(2).EQ.'NUMB')THEN
            PX1LXC=ARG(2)
          ELSE
            IERROR='YES'
