          ENDIF
        ENDIF
C
        IF(NUMARG.GE.3)THEN
          IF(IHARG(3).EQ.'ON'   .OR. IHARG(3).EQ.'OFF'  .OR.
     1       IHARG(3).EQ.'AUTO' .OR. IHARG(3).EQ.'DEFA')THEN
