 1481     FORMAT('THE LABEL COORDINATES (FOR THE LEFT   ',
     1           'VERTICAL LABEL) ARE:')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1182)PY1LXC
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1183)PY1LYC
          CALL DPWRST('XXX','BUG ')
        ENDIF
C
C               **************************************************************
C               **  TREAT THE CASE WHEN                                     **
C               **  ONLY THE RIGHT  VERTICAL   LABEL IS      TO BE CHANGED  **
