            PY1LYC=ARG(3)
            PY2LYC=ARG(3)
          ELSE
            IERROR='YES'
            GOTO9000
          ENDIF
        ENDIF
C
        IFOUND='YES'
C
        IF(IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1781)
 1781     FORMAT('THE LABEL COORDINATES FOR ALL LABELS--')
          CALL DPWRST('XXX','BUG ')
