 1281     FORMAT('THE LABEL SIZE (FOR THE SECOND HORIZONTAL LABEL)')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1182)HOLD1
          CALL DPWRST('XXX','BUG ')
        ENDIF
        GOTO1900
C
C               **************************************************************
C               **  TREAT THE CASE WHEN                                     **
C               **  ONLY THE THIRD  HORIZONTAL LABEL IS      TO BE CHANGED  **
C               **************************************************************
C
      ELSEIF(ICOM.EQ.'X3LA')THEN
