        PY1LWI=HOLD2
C
        IF(IFEEDB.EQ.'ON')THEN
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1481)
 1481     FORMAT('THE LABEL SIZE (FOR THE LEFT   VERTICAL   LABEL)')
          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1182)HOLD1
          CALL DPWRST('XXX','BUG ')
        ENDIF
        GOTO1900
C
C               **************************************************************
