          CALL DPWRST('XXX','BUG ')
          WRITE(ICOUT,1182)HOLD1
          CALL DPWRST('XXX','BUG ')
        ENDIF
        GOTO1900
C
C               **************************************************************
C               **  TREAT THE CASE WHEN                                     **
C               **  BOTH VERTICAL AXIS LABELS ARE TO BE CHANGED             **
C               **************************************************************
C
      ELSEIF(ICOM.EQ.'YLAB')THEN
