        IF(NCHAR2.GT.0)THEN
          IFORMT=' '
          IFORMT(1:11)='(A  ,A1,A1)'
          WRITE(IFORMT(3:4),'(I2)')NCHAR2
          WRITE(ICOUT,IFORMT)IVAL2(1:NCHAR2),IBASLC,IBASLC
          CALL DPWRST('XXX','WRIT')
        ENDIF
