        CALL DPWRST('XXX','BUG ')
        DO9075I=1,10
          WRITE(ICOUT,76)I,ILEGCO(I),ILEGC2(I,1),ILEGC2(I,2),ILEGC2(I,3)
          CALL DPWRST('XXX','BUG ')
