      ELSEIF(NUMARG.GE.2.AND.IHARG(2).EQ.'UNIT')THEN
        IF(IARGT(1).NE.'NUMB')THEN
          IERROR='YES'
          WRITE(ICOUT,999)
          CALL DPWRST('XXX','BUG ')
