      IF(IPOIN2.LE.0)GOTO1290
C
CCCCC THE FOLLOWING 4 LINES WERE ADDED JULY 1993
      IF(NUMLPR.GE.IHELMX)
     1CALL DPMORE(NUMLPR,NCPREH,ICPREH,IRESP,IBUGS2,IERROR)
      IF(NUMLPR.GE.IHELMX)NUMLPR=0
      IF(IRESP.EQ.'NO')GOTO1290
