      DO1210I=1,MAXSTR
      IC1=IANSSV(J,I)
      ISTRIN(I:I)=IC1
 1210 CONTINUE
      NMAX=MAXSTR
      CALL DPDB80(ISTRIN,IMAX,NMAX,IBUGS2,ISUBRO,IERROR)
CCCCC THE FOLLOWING SECTION HAD CHANGES MADE   SEPTEMBER 1993
      IF(J.NE.JMAX)THEN
         WRITE(ICOUT,1215)IPOIN2,(ISTRIN(I:I),I=1,MIN(255,IMAX))
 1215    FORMAT(4X,I4,'--',255A1)
