      NUMLPR=NUMLPR+1
 1200 CONTINUE
 1290 CONTINUE
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT.      **
C               *****************
C
      IF(IBUGS2.EQ.'ON'.OR.ISUBRO.EQ.'LICO')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('AT THE END       OF DPLICO--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9029)IFOUND,IERROR,IRESP
 9029   FORMAT('IFOUND,IERROR,IRESP = ',2(A4,2X),A4)
        CALL DPWRST('XXX','BUG ')
