      ALIGN(NUMCOL+1)='r'
      IDIGIT(2)=0
      IDIGIT(3)=0
      DO4020I=1,NDIST
        NCTEXT(I)=0
        ITEXT(I)=' '
        AMAT(I,1)=XIDTEM(I)
        AMAT(I,2)=REAL(MU(I))
        AMAT(I,3)=REAL(NN(I))
        AMAT(I,4)=YM(I)
        AMAT(I,5)=YVAR(I)
 4020 CONTINUE
      IWRTF(1)=1400
      IWRTF(2)=IWRTF(1)+1600
      IWRTF(3)=IWRTF(2)+1600
      IWRTF(4)=IWRTF(3)+1600
      IWRTF(5)=IWRTF(4)+1600
      IFRST=.TRUE.
      ILAST=.TRUE.
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'LOD3')THEN
        ISTEPN='4.4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4111)
 4111   FORMAT('BEFORE PRINT SUMMARY TABLE')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4112)NCTIT9,NCTITL,MAXLIN,NUMLIN,NUMCOL,MAXROW
 4112   FORMAT('NCTIT9,NCTITL,MAXLIN,NUMLIN,NUMCOL,MAXROW = ',6I6)
        CALL DPWRST('XXX','BUG ')
        DO4120I=1,NUMLIN
          DO4130J=1,NUMCOL
            WRITE(ICOUT,4122)I,J,ITITL2(I,J),NCTIT2(I,J),AMAT(I,J)
 4122       FORMAT('I,J,ITITL2(I,J),NCTIT2(I,J),AMAT(I,J) = ',
     1             2I6,A40,I8,G15.7)
            CALL DPWRST('XXX','BUG ')
 4130     CONTINUE
 4120   CONTINUE
      ENDIF
C
      CALL DPDTA2(ITITL9,NCTIT9,
     1            ITITLE,NCTITL,ITITL2,NCTIT2,
     1            MAXLIN,NUMLIN,MAXCOL,NUMCOL,
     1            ITEXT,NCTEXT,AMAT,MAXROW,NDIST,
     1            IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1            ICAPSW,ICAPTY,IFRST,ILAST,
     1            ISUBRO,IBUGA3,IERROR)
C
 4199 CONTINUE
C
      IF(ILODTA.EQ.'OFF' .OR. IPRINT.EQ.'OFF')GOTO5199
C
      NCTIT9=0
      ITITL9(1:NCTIT9)=' '
      NCTITL=9
      ITITLE(1:NCTITL)='LOD Table'
      NUMLIN=4
      NUMCOL=7
C
      ITITL2(1,1)=' '
      ITITL2(2,1)=' '
      ITITL2(3,1)='Mass'
      ITITL2(4,1)='Values'
      NCTIT2(1,1)=0
      NCTIT2(2,1)=0
      NCTIT2(3,1)=4
      NCTIT2(4,1)=6
C
      ITITL2(1,2)='Linear'
      ITITL2(2,2)='Least Squares'
      ITITL2(3,2)='Slope'
      ITITL2(4,2)='Estimates'
      NCTIT2(1,2)=6
      NCTIT2(2,2)=13
      NCTIT2(3,2)=5
      NCTIT2(4,2)=9
C
      ITITL2(1,3)='StdDev of'
      ITITL2(2,3)='Errors From'
      ITITL2(3,3)='Linear'
      ITITL2(4,3)='Model'
      NCTIT2(1,3)=9
      NCTIT2(2,3)=11
      NCTIT2(3,3)=6
      NCTIT2(4,3)=5
C
      ITITL2(1,4)='LOD Estimate'
      ITITL2(2,4)='Based On'
      ITITL2(3,4)='Cumulated'
      ITITL2(4,4)='Sample'
      NCTIT2(1,4)=12
      NCTIT2(2,4)=8
      NCTIT2(3,4)=9
      NCTIT2(4,4)=6
C
      ITITL2(1,5)='1-Sigma'
      ITITL2(2,5)='Uncertainty'
      ITITL2(3,5)='on LOD'
      ITITL2(4,5)='Estimate'
      NCTIT2(1,5)=7
      NCTIT2(2,5)=11
      NCTIT2(3,5)=6
      NCTIT2(4,5)=8
C
      IF(GAMMA.EQ.0.05)THEN
        ITITL2(2,6)='95% Upper'
        NCTIT2(2,6)=9
      ELSEIF(GAMMA.EQ.0.10)THEN
        ITITL2(2,6)='90% Upper'
        NCTIT2(2,6)=9
      ELSEIF(GAMMA.EQ.0.01)THEN
        ITITL2(2,6)='99% Upper'
        NCTIT2(2,6)=9
      ELSE
        ATEMP=100.*(1.0 - GAMMA)
        ITITL2(2,6)='xx.x% Upper'
        WRITE(ITITL2(2,6)(1:4),'(F4.1)')ATEMP
        NCTIT2(2,6)=11
      ENDIF
      ITITL2(1,6)=' '
      ITITL2(3,6)='Confidence'
      ITITL2(4,6)='Limit on LOD'
      NCTIT2(1,6)=0
      NCTIT2(3,6)=10
      NCTIT2(4,6)=12
C
      IF(GAMMA.EQ.0.05.AND.PA.EQ.0.05)THEN
        ITITL2(1,7)='95%-95%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.10.AND.PA.EQ.0.05)THEN
        ITITL2(1,7)='90%-95%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.01.AND.PA.EQ.0.05)THEN
        ITITL2(1,7)='99%-95%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.10.AND.PA.EQ.0.10)THEN
        ITITL2(1,7)='90%-90%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.05.AND.PA.EQ.0.10)THEN
        ITITL2(1,7)='95%-90%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.01.AND.PA.EQ.0.10)THEN
        ITITL2(1,7)='99%-90%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.10.AND.PA.EQ.0.01)THEN
        ITITL2(1,7)='90%-99%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.05.AND.PA.EQ.0.01)THEN
        ITITL2(1,7)='95%-99%'
        NCTIT2(1,7)=7
      ELSEIF(GAMMA.EQ.0.01.AND.PA.EQ.0.01)THEN
        ITITL2(1,7)='99%-99%'
        NCTIT2(1,7)=7
      ELSE
        ITITL2(1,7)='xx.x%-xx.x%'
        ATEMP=100.*(1.0 - GAMMA)
        WRITE(ITITL2(1,7)(1:4),'(F4.1)')ATEMP
        ATEMP=100.*(1.0 - PA)
        WRITE(ITITL2(1,7)(7:10),'(F4.1)')ATEMP
        NCTIT2(1,7)=11
      ENDIF
      ITITL2(2,7)='Upper'
      ITITL2(3,7)='Tolerance'
      ITITL2(4,7)='Limit on LOD'
      NCTIT2(2,7)=5
      NCTIT2(3,7)=9
      NCTIT2(4,7)=12
C
      NMAX=0
      DO5010J=1,NUMCOL
        NTOT(J)=15
        VALIGN(J)='b'
        ALIGN(J)='r'
        NMAX=NMAX+NTOT(J)
        IDIGIT(J)=NUMDIG
        IWHTML(J)=150
 5010 CONTINUE
      IWHTML(1)=125
      IWHTML(2)=175
      IWHTML(4)=175
      IWHTML(NUMCOL+1)=150
      VALIGN(NUMCOL+1)='b'
      ALIGN(NUMCOL+1)='r'
      DO5020I=1,NDIST
        NCTEXT(I)=0
        AMAT(I,1)=XIDTEM(I)
CCCCC   IF(I.EQ.1)THEN
        IF(I.EQ.1 .OR. I.LT.ISTRT)THEN
          AMAT(I,2)=CPUMIN
          AMAT(I,3)=CPUMIN
          AMAT(I,4)=CPUMIN
          AMAT(I,5)=CPUMIN
          AMAT(I,6)=CPUMIN
          AMAT(I,7)=CPUMIN
        ELSE
          AMAT(I,2)=BA(I)
          AMAT(I,3)=SI(I)
          AMAT(I,4)=LO(I)
          AMAT(I,5)=UN(I)
          AMAT(I,6)=LU(I)
          AMAT(I,7)=TO(I)
        ENDIF
 5020 CONTINUE
      IWRTF(1)=900
      IWRTF(2)=IWRTF(1)+1150
      IWRTF(3)=IWRTF(2)+1150
      IWRTF(4)=IWRTF(3)+1150
      IWRTF(5)=IWRTF(4)+1150
      IWRTF(6)=IWRTF(5)+1150
      IWRTF(7)=IWRTF(6)+1150
      IF(ICAPSW.EQ.'ON' .AND. ICAPTY.EQ.'RTF ')THEN
        IPTSZ=14
        CALL DPCONA(92,IBASLC)
        WRITE(ICOUT,8199)IBASLC,IPTSZ
 8199   FORMAT(A1,'fs',I2)
        CALL DPWRST(ICOUT,'WRIT')
      ENDIF
      IFRST=.TRUE.
      ILAST=.TRUE.
C
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'LOD3')THEN
        ISTEPN='4.4'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,5111)
 5111   FORMAT('BEFORE PRINT LOD TABLE')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,4112)NCTIT9,NCTITL,MAXLIN,NUMLIN,NUMCOL,MAXROW
        CALL DPWRST('XXX','BUG ')
        DO5120I=1,NUMLIN
          DO5130J=1,NUMCOL
            WRITE(ICOUT,4122)I,J,ITITL2(I,J),NCTIT2(I,J),AMAT(I,J)
            CALL DPWRST('XXX','BUG ')
 5130     CONTINUE
 5120   CONTINUE
      ENDIF
C
      CALL DPDTA2(ITITL9,NCTIT9,
     1            ITITLE,NCTITL,ITITL2,NCTIT2,
     1            MAXLIN,NUMLIN,MAXCOL,NUMCOL,
     1            ITEXT,NCTEXT,AMAT,MAXROW,NDIST,
     1            IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1            ICAPSW,ICAPTY,IFRST,ILAST,
     1            ISUBRO,IBUGA3,IERROR)
      IF(ICAPSW.EQ.'ON' .AND. ICAPTY.EQ.'RTF ')THEN
        IPTSZ=IRTFPS
        WRITE(ICOUT,8199)IBASLC,IPTSZ
        CALL DPWRST(ICOUT,'WRIT')
      ENDIF
C
 5199 CONTINUE
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'LOD3')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPLOD3--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9012)IERROR,IBUGA3
 9012   FORMAT('IERROR,IBUGA3 = ',A4,1X,A4)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9013)N
 9013   FORMAT('N = ',2I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPLOD5(ALOD,ALODSE,CV,ALODLU,
     1                  IFLAGU,IFRST,ILAST,ICASPL,
     1                  IBUGA2,IBUGA3,ISUBRO,IERROR)
C
C     PURPOSE--UTILITY ROUTINE USED BY DPLODE.  THIS ROUTINE
C              UPDATES THE PARAMETERS "LOD", LODSE", "LODCV", AND
C              "LODUCL" AFTER PERFORMING A LIMITS OF DETECTION
C              COMMAND.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORAOTRY
C                 NATIONAL INSTITUTE OF STANDARDS OF TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2899
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS OF TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2018/10
C     ORIGINAL VERSION--OCTOBER   2018.
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IFLAGU
      CHARACTER*4 ICASPL
      CHARACTER*4 IBUGA2
      CHARACTER*4 IBUGA3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      LOGICAL IFRST
      LOGICAL ILAST
C
      CHARACTER*4 IH
      CHARACTER*4 IH2
      CHARACTER*4 ISUBN0
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
      CHARACTER*4 IOP
C
      SAVE IOUNI1
C
C
C-----COMMON VARIABLES (GENERAL)--------------------------------------
C
      INCLUDE 'DPCOPA.INC'
      INCLUDE 'DPCOHK.INC'
      INCLUDE 'DPCOHO.INC'
      INCLUDE 'DPCOF2.INC'
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'LOD5')THEN
        ISTEPN='1'
        CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('***** AT THE BEGINNING OF DPLOD5--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,54)ICASPL,ALOD,ALODSE,CV,ALODLU
   54   FORMAT('ICASPL,ALOD,ALODSE,CV,ALODLU = ',A4,2X,4G15.7)
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      IF(IFLAGU.EQ.'FILE')THEN
        IF(IFRST)THEN
          IOP='OPEN'
          IFLAG1=1
          IFLAG2=0
          IFLAG3=0
          IFLAG4=0
          IFLAG5=0
          CALL DPAUFI(IOP,IFLAG1,IFLAG2,IFLAG3,IFLAG4,IFLAG5,
     1                IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1                IBUGA3,ISUBRO,IERROR)
          IF(IERROR.EQ.'YES')GOTO9000
C
          IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'LOD5')THEN
            ISTEPN='2A'
            CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,201)IOUNI1
  201       FORMAT('AFTER CALL DPOPFI, IOUNI1 = ',I8)
            CALL DPWRST('XXX','BUG ')
          ENDIF
C
          WRITE(IOUNI1,295)
  295     FORMAT(12X,'LOD',10X,'LODSE',10X,'LODCV',9X,'LODUCL')
        ENDIF
        WRITE(IOUNI1,299)ALOD,ALODSE,CV,ALODLU
  299   FORMAT(4E15.7)
      ELSEIF(IFLAGU.EQ.'ON')THEN
C
        IF(ALOD.NE.CPUMIN)THEN
          IH='LOD '
          IH2='    '
          VALUE0=ALOD
          CALL DPADDP(IH,IH2,VALUE0,IHOST1,ISUBN0,
     1                IHNAME,IHNAM2,IUSE,VALUE,IVALUE,NUMNAM,MAXNAM,
     1                IANS,IWIDTH,IBUGA3,IERROR)
        ENDIF
C
        IF(ALODSE.NE.CPUMIN)THEN
          IH='LODS'
          IH2='E   '
          VALUE0=ALODSE
          CALL DPADDP(IH,IH2,VALUE0,IHOST1,ISUBN0,
     1                IHNAME,IHNAM2,IUSE,VALUE,IVALUE,NUMNAM,MAXNAM,
     1                IANS,IWIDTH,IBUGA3,IERROR)
        ENDIF
C
        IF(CV.NE.CPUMIN)THEN
          IH='LODC'
          IH2='V   '
          VALUE0=CV
          CALL DPADDP(IH,IH2,VALUE0,IHOST1,ISUBN0,
     1                IHNAME,IHNAM2,IUSE,VALUE,IVALUE,NUMNAM,MAXNAM,
     1                IANS,IWIDTH,IBUGA3,IERROR)
        ENDIF
C
        IF(ALODLU.NE.CPUMIN)THEN
          IH='LODU'
          IH2='CL  '
          VALUE0=ALODLU
          CALL DPADDP(IH,IH2,VALUE0,IHOST1,ISUBN0,
     1                IHNAME,IHNAM2,IUSE,VALUE,IVALUE,NUMNAM,MAXNAM,
     1                IANS,IWIDTH,IBUGA3,IERROR)
        ENDIF
C
      ENDIF
C
      IF(IFLAGU.EQ.'FILE')THEN
        IF(ILAST)THEN
          IOP='CLOS'
          IFLAG1=1
          IFLAG2=0
          IFLAG3=0
          IFLAG4=0
          IFLAG5=0
          CALL DPAUFI(IOP,IFLAG1,IFLAG2,IFLAG3,IFLAG4,IFLAG5,
     1                IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1                IBUGA3,ISUBRO,IERROR)
          IF(IERROR.EQ.'YES')GOTO9000
C
          IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'LOD5')THEN
            ISTEPN='3A'
            CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
            WRITE(ICOUT,999)
            CALL DPWRST('XXX','BUG ')
            WRITE(ICOUT,301)IERROR,IOUNI1
  301       FORMAT('AFTER CALL DPCLFI: IERROR,IOUNI1 = ',A4,2X,I5)
            CALL DPWRST('XXX','BUG ')
          ENDIF
C
        ENDIF
      ENDIF
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
C
      IF(IBUGA2.EQ.'ON' .OR. ISUBRO.EQ.'LOD5')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END OF DPLOD5--')
        CALL DPWRST('XXX','BUG ')
      ENDIF
C
      RETURN
      END
