      SUBROUTINE DPLRD4(STATVA,STATCD,PVAL,CV90,CV95,CV99,
     1                  APOW90,APOW95,APOW99,
     1                  IFLAGU,IFRST,ILAST,ICASPL,
     1                  IBUGA2,IBUGA3,ISUBRO,IERROR)
