          ELSE
            IST1CS='OPEN'
          ENDIF
C
          WRITE(IOUNI1,295)
  295     FORMAT(11X,'STATVAL',8X,'STATCDF',8X,'PVALUE',
     1           7X,'CV90',11X,'CV95',11X,'CV99',11X,
     1           'POW90',10X,'POW95',10X,'POW99')
