      INCLUDE 'DPCOP2.INC'
C
C-----START POINT------------------------------------------------
C
      IERROR='NO'
      IPTEMP=IPRINT
      IFLAG9=.TRUE.
      IUNCFL='OFF'
      IDFFL='OFF'
      ISUBN1='DPMA'
      ISUBN2='N2  '
C
      IBOBSV=IBOBCM
      IBCPSV=IBCPCM
C
      XMPS=CPUMIN
      XMMPS=CPUMIN
