        WRITE(IOUNI5,1122)
 1122   FORMAT('WEIGHTS FOR MANDEL-PAULE')
        DO1123I=1,NLAB
          DTERM1=DBLE(ASD(I))**2/DBLE(N(I))
          WITEMP=1.0D0/(S2BMPS + DTERM1)
          WRITE(IOUNI5,'(E15.7)')WITEMP
 1123   CONTINUE
