     1              X,T,N,
     1              XMMPS,S2BMMP,SEMMP,SEMMP1,SEMMP2,
     1              DLOWMM,DHIGMM,
     1              IWRITE,
     1              ICAPSW,ICAPTY,NUMDIG,
     1              ISUBRO,IBUGA3,IERROR)
        IF(IFLAGZ.EQ.1)THEN
          IPRINT=IPRISV
        ENDIF
