        AMAT(ICNT,ISTRT+5)=AK
        IMETH='Vangel-Rukhin Bootstrap'
        WRITE(IOUNI2,4001)XMLS,DLOWM2,DHIGM2,AK,IMETH
      ENDIF
C
      IF(IDSLCM.EQ.'ON' .AND. XDL.NE.CPUMIN)THEN
        ICNT=ICNT+1
        ITEXT(ICNT,1)=' 4a. DerSimonian-Laird (original)'
        NCTEXT(ICNT,1)=33
