      CALL DPDTA1(ITITLE,NCTITL,ITITLZ,NCTITZ,ITEXT,NCTEXT,
     1            AVALUE,IDIGIT,
     1            NTOT,NUMROW,
     1            ICAPSW,ICAPTY,ILAST,IFRST,
     1            ISUBRO,IBUGA3,IERROR)
C
      ISTEPN='21B'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'MNN2')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(ITAB.EQ.0)THEN
        ITITLE='Two-Tailed Test: Normal Approximation'
        NCTITL=37
      ELSE
        ITITLE='Two-Tailed Test: Exact - Small Sample, No Ties'
        NCTITL=46
      ENDIF
      ITITL9='H0: F(x) = G(x); Ha: F(x) <> G(x)  for some x'
      NCTIT9=45
C
      DO2130J=1,NUMCLI
        DO2140I=1,MAXLIN
          ITITL2(I,J)=' '
          NCTIT2(I,J)=0
 2140   CONTINUE
 2130 CONTINUE
C
      IF(ITAB.EQ.0)THEN
        NUMCOL=4
        ITITL2(2,1)='Significance'
        NCTIT2(2,1)=12
        ITITL2(3,1)='Level'
        NCTIT2(3,1)=5
C
        ITITL2(2,2)='Test '
        NCTIT2(2,2)=4
        ITITL2(3,2)='Statistic'
        NCTIT2(3,2)=9
C
        ITITL2(2,3)='Critical'
        NCTIT2(2,3)=8
        ITITL2(3,3)='Value (+/-)'
        NCTIT2(3,3)=11
C
        ITITL2(1,4)='Null'
        NCTIT2(1,4)=4
        ITITL2(2,4)='Hypothesis'
        NCTIT2(2,4)=10
        ITITL2(3,4)='Conclusion'
        NCTIT2(3,4)=10
C
      ELSE
        NUMCOL=5
        ITITL2(2,1)='Significance'
        NCTIT2(2,1)=12
        ITITL2(3,1)='Level'
        NCTIT2(3,1)=5
C
        ITITL2(2,2)='Test '
        NCTIT2(2,2)=4
        ITITL2(3,2)='Statistic'
        NCTIT2(3,2)=9
C
        ITITL2(1,3)='Lower'
        NCTIT2(1,3)=5
        ITITL2(2,3)='Critical'
        NCTIT2(2,3)=8
        ITITL2(3,3)='Value (<)'
        NCTIT2(3,3)=9
C
        ITITL2(1,4)='Upper'
        NCTIT2(1,4)=5
        ITITL2(2,4)='Critical'
        NCTIT2(2,4)=8
        ITITL2(3,4)='Value (>)'
        NCTIT2(3,4)=9
C
        ITITL2(1,5)='Null'
        NCTIT2(1,5)=4
        ITITL2(2,5)='Hypothesis'
        NCTIT2(2,5)=10
        ITITL2(3,5)='Conclusion'
        NCTIT2(3,5)=10
C
      ENDIF
C
      NMAX=0
      DO2150I=1,NUMCOL
        VALIGN(I)='b'
        ALIGN(I)='r'
        NTOT(I)=15
        NMAX=NMAX+NTOT(I)
        ITYPCO(I)='NUME'
        IDIGIT(I)=NUMDIG
        IF(ITAB.EQ.0)THEN
          IF(I.EQ.1 .OR. I.EQ.4)THEN
            ITYPCO(I)='ALPH'
          ENDIF
        ELSE
          IF(I.EQ.1 .OR. I.EQ.5)THEN
            ITYPCO(I)='ALPH'
          ENDIF
        ENDIF
 2150 CONTINUE
C
      IWHTML(1)=125
      IWHTML(2)=175
      IWHTML(3)=175
      IWHTML(4)=175
      IWHTML(5)=175
      IINC=1800
      IINC2=1400
      IWRTF(1)=IINC
      IWRTF(2)=IWRTF(1)+IINC
      IWRTF(3)=IWRTF(2)+IINC
      IWRTF(4)=IWRTF(3)+IINC
      IWRTF(5)=IWRTF(4)+IINC
C
      IF(ITAB.EQ.0)THEN
        ICNT=NUMAL2
        DO2160J=1,NUMAL2
C
          AMAT(J,2)=STATVA
          ALPHAT=ALPHA2(J)
          ATEMP=(1.0 - ALPHAT)/2.0
          ATEMP=1.0 - ATEMP
          CALL NORPPF(ATEMP,CUTTMP)
          AMAT(J,3)=CUTTMP
          IVALUE(J,4)(1:6)='REJECT'
          IF(ABS(STATVA).LT.AMAT(J,3))THEN
            IVALUE(J,4)(1:6)='ACCEPT'
          ENDIF
          NCVALU(J,4)=6
C
          WRITE(IVALUE(J,1)(1:4),'(F4.1)')100.0*ALPHAT
          IVALUE(J,1)(5:5)='%'
          NCVALU(J,1)=5
 2160   CONTINUE
      ELSE
        ICNT=NUMAL2
        DO3160J=1,ICNT
C
          AMAT(J,2)=STATVA
          ALPHAT=ALPHA2(J)
          IF(J.EQ.1)THEN
            AMAT(J,3)=CVL100
            AMAT(J,4)=CVU900
          ELSEIF(J.EQ.2)THEN
            AMAT(J,3)=CVL050
            AMAT(J,4)=CVU950
          ELSEIF(J.EQ.3)THEN
            AMAT(J,3)=CVL025
            AMAT(J,4)=CVU975
          ELSEIF(J.EQ.4)THEN
            AMAT(J,3)=CVL005
            AMAT(J,4)=CVU995
          ENDIF
          IVALUE(J,5)(1:6)='ACCEPT'
          IF(STATVA.LT.AMAT(J,3))THEN
            IVALUE(J,5)(1:6)='REJECT'
          ELSEIF(STATVA.GT.AMAT(J,4))THEN
            IVALUE(J,5)(1:6)='REJECT'
          ENDIF
          NCVALU(J,5)=6
C
          WRITE(IVALUE(J,1)(1:4),'(F4.1)')100.0*ALPHAT
          IVALUE(J,1)(5:5)='%'
          NCVALU(J,1)=5
 3160   CONTINUE
      ENDIF
C
      NUMLIN=3
      IFRST=.TRUE.
      ILAST=.TRUE.
      IFLAGS=.TRUE.
      IFLAGE=.TRUE.
C
      IF(ICASAN.EQ.'TWOT')THEN
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
      IF(ICASAN.EQ.'LOWE')THEN
C
        IF(ITAB.EQ.0)THEN
          ITITLE='Lower-Tailed Test: Normal Approximation'
          NCTITL=39
        ELSE
          ITITLE='Lower-Tailed Test: Exact - Small Sample, No Ties'
          NCTITL=48
        ENDIF
        ITITL9='H0: F(x) = G(x); Ha: F(x) < G(x)  for some x'
        NCTIT9=44
C
        IF(ITAB.EQ.0)THEN
          ITITL2(2,3)='Critical'
          NCTIT2(2,3)=8
          ITITL2(3,3)='Value (<)'
          NCTIT2(3,3)=9
          NUMCOL=4
        ELSE
          NUMCOL=4
          ITITL2(1,3)='Lower'
          NCTIT2(1,3)=5
          ITITL2(2,3)='Critical'
          NCTIT2(2,3)=8
          ITITL2(3,3)='Value (<)'
          NCTIT2(3,3)=9
C
          ITITL2(1,4)='Null'
          NCTIT2(1,4)=4
          ITITL2(2,4)='Hypothesis'
          NCTIT2(2,4)=10
          ITITL2(3,4)='Conclusion'
          NCTIT2(3,4)=10
        ENDIF
C
        NMAX=0
        DO2250I=1,NUMCOL
          NTOT(I)=15
          NMAX=NMAX+NTOT(I)
 2250   CONTINUE
C
        IF(ITAB.EQ.0)THEN
          ICNT=NUMALP
          DO2260J=1,NUMALP
C
            AMAT(J,2)=STATVA
            ALPHAT=ALPHA(J)
            ATEMP=(1.0 - ALPHAT)
            CALL NORPPF(ATEMP,CUTTMP)
            AMAT(J,3)=CUTTMP
            IVALUE(J,4)(1:6)='ACCEPT'
            IF(ABS(STATVA).LT.AMAT(J,3))THEN
              IVALUE(J,4)(1:6)='REJECT'
            ENDIF
            NCVALU(J,4)=6
            WRITE(IVALUE(J,1)(1:4),'(F4.1)')100.0*ALPHAT
            IVALUE(J,1)(5:5)='%'
            NCVALU(J,1)=5
 2260     CONTINUE
        ELSE
          ICNT=NUMALP
          DO3260J=1,ICNT
C
            AMAT(J,2)=STATVA
            ALPHAT=ALPHA(J)
            IF(J.EQ.1)THEN
              AMAT(J,3)=CVL100
            ELSEIF(J.EQ.2)THEN
              AMAT(J,3)=CVL050
            ELSEIF(J.EQ.3)THEN
              AMAT(J,3)=CVL025
            ELSEIF(J.EQ.4)THEN
              AMAT(J,3)=CVL010
            ELSEIF(J.EQ.5)THEN
              AMAT(J,3)=CVL005
            ELSEIF(J.EQ.6)THEN
              AMAT(J,3)=CVL001
            ENDIF
            IVALUE(J,4)(1:6)='ACCEPT'
            IF(STATVA.LT.AMAT(J,3))THEN
              IVALUE(J,5)(1:6)='REJECT'
            ENDIF
            NCVALU(J,4)=6
C
            WRITE(IVALUE(J,1)(1:4),'(F4.1)')100.0*ALPHAT
            IVALUE(J,1)(5:5)='%'
            NCVALU(J,1)=5
 3260     CONTINUE
        ENDIF
C
        NUMLIN=3
        IFRST=.TRUE.
        ILAST=.TRUE.
        IFLAGS=.TRUE.
        IFLAGE=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C
      IF(ICASAN.EQ.'UPPE')THEN
C
        IF(ITAB.EQ.0)THEN
          ITITLE='Upper-Tailed Test: Normal Approximation'
          NCTITL=39
        ELSE
          ITITLE='Upper-Tailed Test: Exact - Small Sample, No Ties'
          NCTITL=48
        ENDIF
        ITITL9='H0: F(x) = G(x); Ha: F(x) > G(x)  for some x'
        NCTIT9=44
C
        IF(ITAB.EQ.0)THEN
          ITITL2(2,3)='Critical'
          NCTIT2(2,3)=8
          ITITL2(3,3)='Value (>)'
          NCTIT2(3,3)=9
          NUMCOL=4
        ELSE
          NUMCOL=4
          ITITL2(1,3)='Upper'
          NCTIT2(1,3)=5
          ITITL2(2,3)='Critical'
          NCTIT2(2,3)=8
          ITITL2(3,3)='Value (>)'
          NCTIT2(3,3)=9
C
          ITITL2(1,4)='Null'
          NCTIT2(1,4)=4
          ITITL2(2,4)='Hypothesis'
          NCTIT2(2,4)=10
          ITITL2(3,4)='Conclusion'
          NCTIT2(3,4)=10
        ENDIF
C
        NMAX=0
        DO2350I=1,NUMCOL
          NTOT(I)=15
          NMAX=NMAX+NTOT(I)
 2350   CONTINUE
C
        IF(ITAB.EQ.0)THEN
          ICNT=NUMALP
          DO2360J=1,NUMALP
C
            AMAT(J,2)=STATVA
            ALPHAT=ALPHA(J)
            ATEMP=ALPHAT
            CALL NORPPF(ATEMP,CUTTMP)
            AMAT(J,3)=CUTTMP
            IVALUE(J,4)(1:6)='ACCEPT'
            IF(ABS(STATVA).GT.AMAT(J,3))THEN
              IVALUE(J,4)(1:6)='REJECT'
            ENDIF
            NCVALU(J,4)=6
            WRITE(IVALUE(J,1)(1:4),'(F4.1)')100.0*ALPHAT
            IVALUE(J,1)(5:5)='%'
            NCVALU(J,1)=5
 2360     CONTINUE
        ELSE
          ICNT=NUMALP
          DO3360J=1,ICNT
C
            AMAT(J,2)=STATVA
            ALPHAT=ALPHA(J)
            IF(J.EQ.1)THEN
              AMAT(J,3)=CVL900
            ELSEIF(J.EQ.2)THEN
              AMAT(J,3)=CVL950
            ELSEIF(J.EQ.3)THEN
              AMAT(J,3)=CVL975
            ELSEIF(J.EQ.4)THEN
              AMAT(J,3)=CVL990
            ELSEIF(J.EQ.5)THEN
              AMAT(J,3)=CVL995
            ELSEIF(J.EQ.6)THEN
              AMAT(J,3)=CVL999
            ENDIF
            IVALUE(J,4)(1:6)='ACCEPT'
            IF(STATVA.GT.AMAT(J,3))THEN
              IVALUE(J,5)(1:6)='REJECT'
            ENDIF
            NCVALU(J,4)=6
C
            WRITE(IVALUE(J,1)(1:4),'(F4.1)')100.0*ALPHAT
            IVALUE(J,1)(5:5)='%'
            NCVALU(J,1)=5
 3360     CONTINUE
        ENDIF
C
        NUMLIN=3
        IFRST=.TRUE.
        ILAST=.TRUE.
        IFLAGS=.TRUE.
        IFLAGE=.TRUE.
        CALL DPDTA5(ITITLE,NCTITL,
     1              ITITL9,NCTIT9,ITITL2,NCTIT2,
     1              MAXLIN,NUMLIN,NUMCLI,NUMCOL,
     1              IVALUE,NCVALU,AMAT,ITYPCO,MAXROW,ICNT,
     1              IDIGIT,NTOT,IWHTML,IWRTF,VALIGN,ALIGN,NMAX,
     1              ICAPSW,ICAPTY,IFRST,ILAST,
     1              IFLAGS,IFLAGE,
     1              ISUBRO,IBUGA3,IERROR)
      ENDIF
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON' .OR. ISUBRO.EQ.'MNN2')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPMNN2--')
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,9013)STATVA,STATV2,STATCD,PVAL2T,PVALLT,PVALUT
 9013   FORMAT('STATVA,STATV2,STATCD,PVAL2T,PVALLT,PVALUT = ',6G15.7)
        CALL DPWRST('XXX','WRIT')
      ENDIF
C
      RETURN
      END
      SUBROUTINE DPMNN3(Y1,N1,Y2,N2,
     1                  TEMP1,TEMP2,YRANK,MAXNXT,
     1                  STATVA,STATV1,STATV2,STATV3,STATCD,NTIES,
     1                  PVAL2T,PVALLT,PVALUT,
     1                  IBUGA3,ISUBRO,IERROR)
C
C     PURPOSE--THIS ROUTINE COMPUTES THE MANN-WHITNEY 2-SAMPLE RANK
C              SUM TEST STATISTIC AND ASSOCIATED CDF AND P-VALUES.
C
C              THIS PART IS EXTRACTED FROM DPMNN2 IN ORDER TO
C              ALLOW IT TO BE COMPUTED FROM THE "STATISTICS" ROUTINES
C              (E.G., STATISTIC PLOT, BOOTSTRAP).
C
C     EXAMPLE--MANN WHITNEY RANK SUM TEST Y1 Y2
C              SAMPLE 1 IS IN INPUT VECTOR Y1 (WITH N1 OBSERVATIONS)
C              SAMPLE 2 IS IN INPUT VECTOR Y2 (WITH N2 OBSERVATIONS).
C     REFERENCE--CONOVER (1999), "PRACTICAL NONPARAMETRIC STATISTICS",
C                THIRD EDITION, WILEY, PP. 272 - 281.
C              --WALPOLE AND MEYERS (19xx), "ENGINEERING STATISTICS",
C                XX, PP. XX.
C     WRITTEN BY--ALAN HECKERT
C                 STATISTICAL ENGINEERING DIVISION
C                 INFORMATION TECHNOLOGY LABORATORY
C                 NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY
C                 GAITHERSBURG, MD 20899-8980
C                 PHONE--301-975-2855
C     NOTE--DATAPLOT IS A REGISTERED TRADEMARK
C           OF THE NATIONAL INSTITUTE OF STANDARDS AND TECHNOLOGY.
C     LANGUAGE--ANSI FORTRAN (1977)
C     VERSION NUMBER--2011/5
C     ORIGINAL VERSION--MAY       2011. EXTRACTED FROM DPMNN2
C
C-----CHARACTER STATEMENTS FOR NON-COMMON VARIABLES-------------------
C
      CHARACTER*4 IBUGA3
      CHARACTER*4 ISUBRO
      CHARACTER*4 IERROR
C
      CHARACTER*4 IWRITE
      CHARACTER*4 ISUBN1
      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
C---------------------------------------------------------------------
C
      DIMENSION Y1(*)
      DIMENSION Y2(*)
      DIMENSION TEMP1(*)
      DIMENSION TEMP2(*)
      DIMENSION YRANK(*)
C
C---------------------------------------------------------------------
C
      INCLUDE 'DPCOP2.INC'
C
C-----START POINT-----------------------------------------------------
C
      ISUBN1='DPMN'
      ISUBN2='N3  '
      IERROR='NO'
      IWRITE='OFF'
C
      STATVA=CPUMIN
      STATV1=CPUMIN
      STATV2=CPUMIN
      STATV3=CPUMIN
      STATCD=CPUMIN
      PVAL2T=CPUMIN
      PVALLT=CPUMIN
      PVALUT=CPUMIN
C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'MNN3')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,51)
   51   FORMAT('**** AT THE BEGINNING OF DPMNN3--')
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,52)IBUGA3,ISUBRO,N1,N2
   52   FORMAT('IBUGA3,ISUBRO,N1,N2 = ',2(A4,2X),2I8)
        CALL DPWRST('XXX','WRIT')
        DO56I=1,MAX(N1,N2)
          WRITE(ICOUT,57)I,Y1(I),Y2(I)
   57     FORMAT('I,Y1(I),Y2(I) = ',I8,2G15.7)
          CALL DPWRST('XXX','WRIT')
   56   CONTINUE
      ENDIF
C
C               ********************************************
C               **  STEP 01--                             **
C               **  CHECK THE INPUT ARGUMENTS FOR ERRORS  **
C               ********************************************
C
      ISTEPN='01'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'MNN3')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      IF(N1.LE.1)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,101)
  101   FORMAT('***** ERROR IN MANN-WHITNEY RANK SUM TEST--')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,112)
  112   FORMAT('      THE INPUT NUMBER OF OBSERVATIONS FOR THE ',
     1         'FIRST RESPONSE')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,113)
  113   FORMAT('      VARIABLE MUST BE 2 OR LARGER.  SUCH WAS NOT THE ',
     1         'CASE HERE.')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,117)N1
  117   FORMAT('      THE INPUT NUMBER OF OBSERVATIONS   = ',I8,'.')
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      IF(N2.LE.1)THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,101)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,122)
  122   FORMAT('      THE INPUT NUMBER OF OBSERVATIONS FOR THE ',
     1         'SECOND RESPONSE')
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,113)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,117)N2
        CALL DPWRST('XXX','BUG ')
        IERROR='YES'
        GOTO9000
      ENDIF
C
      HOLD=Y1(1)
      DO135I=2,N1
        IF(Y1(I).NE.HOLD)GOTO139
  135 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','WRIT')
      WRITE(ICOUT,101)
      CALL DPWRST('XXX','WRIT')
      WRITE(ICOUT,131)HOLD
  131 FORMAT('      THE FIRST RESPONSE VARIABLE HAS ALL ELEMENTS = ',
     1       G15.7)
      CALL DPWRST('XXX','WRIT')
      IERROR='YES'
      GOTO9000
  139 CONTINUE
C
      HOLD=Y2(1)
      DO145I=2,N1
        IF(Y2(I).NE.HOLD)GOTO149
  145 CONTINUE
      WRITE(ICOUT,999)
      CALL DPWRST('XXX','WRIT')
      WRITE(ICOUT,101)
      CALL DPWRST('XXX','WRIT')
      WRITE(ICOUT,141)HOLD
  141 FORMAT('      THE SECOND RESPONSE VARIABLE HAS ALL ELEMENTS = ',
     1       G15.7)
      CALL DPWRST('XXX','WRIT')
      IERROR='YES'
      GOTO9000
  149 CONTINUE
C
C               ************************************
C               **   STEP 11--                    **
C               **   COMPUTE RANK SUM TEST.       **
C               ************************************
C
      ISTEPN='11'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'MNN3')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      DO1100I=1,N1
        TEMP1(I)=Y1(I)
 1100 CONTINUE
      NTOT=N1
      DO1110I=1,N2
        NTOT=NTOT+1
        TEMP1(NTOT)=Y2(I)
 1110 CONTINUE
      CALL RANK(TEMP1,NTOT,IWRITE,YRANK,TEMP2,MAXNXT,IBUGA3,IERROR)
C
C     THE COMPUTED TEST STATISTIC DEPENDS ON WHETHER OR NOT THERE ARE
C     NO TIES IN THE RANKS.
C
C        1) CASE WHERE THERE ARE NO TIES:
C
C               T = SUM[i=1 to N1][R(Y1(i))]
C
C            WHERE R() IS THE RANK FROM THE COMBINED SAMPLE
C
C        2) CASE WHERE THERE ARE MANY TIES IN THE DATA:
C
C               T1 = (T - N1*(N1+N2+1)/2)/
C                    SQRT((N1*N2/((N1+N2)*N1+N2-1))*SUM[i=1 to N1+N2][R(i)**2] -
C                    (N1*N2*(N1+N2+1)**2/(4*(N1+N2-1))
C
C     SO CHECK FOR NUMBER OF TIED RANKS.  BASICALLY, IF THE RANK IS
C     A NON-INTEGER VALUE, THIS IMPLIES THAT IT IS A TIED RANK.
C
      ISTEPN='12'
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'MNN3')
     1CALL TRACE2(ISTEPN,ISUBN1,ISUBN2)
C
      NTIES=0
      RSUM=0.0
      DO1210I=1,NTOT
        ARANK=YRANK(I)
        RSUM=RSUM + ARANK**2
        ITEMP=INT(ARANK)
        ATEMP=ARANK - REAL(ITEMP)
        IF(ABS(ATEMP).GE.0.1)THEN
          NTIES=NTIES+1
        ENDIF
 1210 CONTINUE
C
      T=0.0
      DO1230I=1,N1
        T=T+YRANK(I)
 1230 CONTINUE
      STATV1=T
      AN1=REAL(N1)
      AN2=REAL(N2)
      AN=REAL(N1 + N2)
      C1=AN1*(AN+1.0)/2.0
      C2=AN1*AN2/(AN*(AN-1.0))
      C3=AN1*AN2*(AN+1.0)**2/(4.0*(AN-1.0))
      T1=(T - C1)/SQRT(C2*RSUM - C3)
      STATV2=T1
      STATV3=AN1*AN2 + 0.5*AN1*(AN1 + 1.0) - T
C
      IF(NTIES.EQ.0 .AND. N1.LE.20 .AND. N2.LE.20)THEN
        STATVA=STATV1
        TP=AN1*(AN + 1.0)*T
        TVAL=MIN(T,TP)
        ANUM=TVAL + 0.5 -AN1*(AN+1.0)/2.0
        ADEN=SQRT(AN1*AN2*(AN+1.0)/12.0)
        AVAL=ANUM/ADEN
        CALL NORCDF(AVAL,CDF)
        PVAL2T=2.0*CDF
        ANUM=T + 0.5 -AN1*(AN+1.0)/2.0
        ADEN=SQRT(AN1*AN2*(AN+1.0)/12.0)
        AVAL=ANUM/ADEN
        CALL NORCDF(AVAL,CDF)
        PVALLT=CDF
        ANUM=TP + 0.5 -AN1*(AN+1.0)/2.0
        ADEN=SQRT(AN1*AN2*(AN+1.0)/12.0)
        AVAL=ANUM/ADEN
        CALL NORCDF(AVAL,CDF)
        PVALUT=CDF
        STATCD=PVALLT
      ELSE
        STATVA=STATV2
        CALL NORCDF(T1,VAL1)
        VAL2=1.0 - VAL1
        VAL=MIN(VAL1,VAL2)
        PVAL2T=2.0*VAL
        PVALLT=VAL1
        PVALUT=VAL2
        STATCD=VAL1
      ENDIF
C
C               *****************
C               **  STEP 90--  **
C               **  EXIT       **
C               *****************
C
 9000 CONTINUE
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'MNN3')THEN
        WRITE(ICOUT,999)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,9011)
 9011   FORMAT('***** AT THE END       OF DPMNN3--')
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,9013)STATVA,STATV1,STATV2,STATV3,STATCD
 9013   FORMAT('STATVA,STATV1,STATV2,STATV3,STATCD = ',5G15.7)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,9014)PVALLT,PVALUT,PVAL2T
 9014   FORMAT('PVALLT,PVALUT,PVAL2T = ',3G15.7)
        CALL DPWRST('XXX','WRIT')
        WRITE(ICOUT,9015)NTIES,RSUM,C1,C2,C3
 9015   FORMAT('NTIES,RSUM,C1,C2,C3 = ',I8,4G15.7)
        CALL DPWRST('XXX','WRIT')
      ENDIF
C
      RETURN
      END
