      IF(ICOLL(1).LE.MAXCOL)V(IJ)=TEMPM1((JLOOP-1)*MAXROM+NSX)
      IF(NSX.EQ.1)IROW1=I
      IROWN=I
12350 CONTINUE
12340 CONTINUE
      GOTO12390
C
C     -----END MATRIX COPY FOR SUBSET CASE-----
C
12390 CONTINUE
C
