15083 FORMAT('THE WORKSHEET COLUMNS   ASSIGNED TO MATRIX ',A4,A4,
     1'ARE ',I8,' TO',I8)
      CALL DPWRST('XXX','BUG ')
C
15090 CONTINUE
      IF(ICASL7.EQ.'MASD' .OR. ICASL7.EQ.'MASF')GOTO25000
C
      GOTO19000
