19025 CONTINUE
      WRITE(ICOUT,19026)TEMPS(1),TEMPS(2),TEMPS(3),TEMPS(4)
19026 FORMAT('TEMPS(1),TEMPS(2),TEMPS(3),TEMPS(4) = ',4E15.7)
      CALL DPWRST('XXX','BUG ')
      WRITE(ICOUT,19031)IMATSW,NUMVAR
19031 FORMAT('IMATSW,NUMVAR = ',A4,I8)
