     1'ILISR(1),IN(ILISR(1)),IVALUE(ILISR(1)),IVALU2(ILISR(1)) = ',4I8)
        CALL DPWRST('XXX','BUG ')
      ENDIF
      WRITE(ICOUT,19034)(ILOCR(J),J=3,7)
19034 FORMAT('ILOCR(3),...,ILOCR(7) = ',5I8)
