      SUBROUTINE DPMLIG(Y,XLOW,XHIGH,N,NUMVAR,ICASAN,
     1                  XTEMP,XTEMP2,XTEMP3,DTEMP,ITEMP,MAXNXT,
     1                  ALOCML,SIGMML,AMUML,GAMMML,
