      CHARACTER*4 ISUBN2
      CHARACTER*4 ISTEPN
C
C---------------------------------------------------------------------
C
CCCCC DOUBLE PRECISION DSUM
CCCCC DOUBLE PRECISION DTERM1
C
      DIMENSION Y(*)
      DIMENSION XTEMP(*)
C
      INCLUDE 'DPCOST.INC'
C
      PARAMETER (NUMALP=8)
CCCCC REAL ALPHA(NUMALP)
      REAL QP(1)
C
      PARAMETER (MAXROW=50)
      CHARACTER*60 ITITLE
