      AVALUE(1)=0.0
      IDIGIT(1)=0
      ITEXT(2)='Number of Observations:'
      NCTEXT(2)=23
      AVALUE(2)=REAL(N)
      IDIGIT(2)=0
      ITEXT(3)='Sample Mean:'
      NCTEXT(3)=12
      AVALUE(3)=XMEAN
      IDIGIT(3)=NUMDIG
      ITEXT(4)='Sample Standard Deviation:'
      NCTEXT(4)=26
      AVALUE(4)=XSD
      IDIGIT(4)=NUMDIG
      ITEXT(5)='Sample Minimum:'
      NCTEXT(5)=15
      AVALUE(5)=XMIN
      IDIGIT(5)=NUMDIG
      ITEXT(6)='Sample Maximum:'
      NCTEXT(6)=15
      AVALUE(6)=XMAX
      IDIGIT(6)=NUMDIG
      ITEXT(7)='Sample Range:'
      NCTEXT(7)=13
      AVALUE(7)=XRANG
      IDIGIT(7)=NUMDIG
      NUMROW=7
      DO2310I=1,NUMROW
        NTOT(I)=15
 2310 CONTINUE
      NTOT(2)=8
C
      IFRST=.TRUE.
      ILAST=.FALSE.
      NCTITZ=0
      CALL DPDTA1(ITITLE,NCTITL,ITITLZ,NCTITZ,ITEXT,
     1            NCTEXT,AVALUE,IDIGIT,
     1            NTOT,NUMROW,
     1            ICAPSW,ICAPTY,ILAST,IFRST,
     1            ISUBRO,IBUGA3,IERROR)
C
      ITITLE=' '
      NCTITL=0
C
      ITEXT(1)='Method of Moments:'
      NCTEXT(1)=18
      AVALUE(1)=0.0
      IDIGIT(1)=0
      ITEXT(2)='Estimate of Lower Limit:'
      NCTEXT(2)=24
      AVALUE(2)=ALO2LI
      IDIGIT(2)=NUMDIG
      ITEXT(3)='Estimate of Upper Limit:'
      NCTEXT(3)=24
      AVALUE(3)=AUP2LI
      IDIGIT(3)=NUMDIG
      ITEXT(4)='Estimate of Location:'
      NCTEXT(4)=20
      AVALUE(4)=ALOCMO
      IDIGIT(4)=NUMDIG
      ITEXT(5)='Estimate of Scale:'
      NCTEXT(5)=18
      AVALUE(5)=ASCAMO
      IDIGIT(5)=NUMDIG
      ITEXT(6)=' '
      NCTEXT(6)=1
      AVALUE(6)=0.0
      IDIGIT(6)=-1
C
      ITEXT(7)='Maximum Likelihood:'
      NCTEXT(7)=19
      AVALUE(7)=0.0
      IDIGIT(7)=-1
      ITEXT(8)='Estimate of A (Midrange):'
      NCTEXT(8)=25
      AVALUE(8)=AHAT
      IDIGIT(8)=NUMDIG
      ITEXT(9)='Estimate of H (Range/2):'
      NCTEXT(9)=25
      AVALUE(9)=HHAT
      IDIGIT(9)=NUMDIG
      ITEXT(10)='Estimate of Lower Limit:'
      NCTEXT(10)=24
      AVALUE(10)=ALOWLI
      IDIGIT(10)=NUMDIG
      ITEXT(11)='Estimate of Upper Limit:'
      NCTEXT(11)=24
      AVALUE(11)=AUPPLI
      IDIGIT(11)=NUMDIG
      ITEXT(12)='Estimate of Location:'
      NCTEXT(12)=21
      AVALUE(12)=ALOCML
      IDIGIT(12)=NUMDIG
      ITEXT(13)='Estimate of Scale:'
      NCTEXT(13)=18
      AVALUE(13)=ASCAML
      IDIGIT(13)=NUMDIG
      ICNT=13
      ICNT=ICNT+1
      ITEXT(ICNT)='Log-likelihood:'
      NCTEXT(ICNT)=15
      AVALUE(ICNT)=ALIK
      IDIGIT(ICNT)=NUMDIG
      ICNT=ICNT+1
      ITEXT(ICNT)='AIC:'
      NCTEXT(ICNT)=4
      AVALUE(ICNT)=AIC
      IDIGIT(ICNT)=NUMDIG
      ICNT=ICNT+1
      ITEXT(ICNT)='AICc:'
      NCTEXT(ICNT)=5
      AVALUE(ICNT)=AICC
      IDIGIT(ICNT)=NUMDIG
      ICNT=ICNT+1
      ITEXT(ICNT)='BIC:'
      NCTEXT(ICNT)=4
      AVALUE(ICNT)=BIC
      IDIGIT(ICNT)=NUMDIG
      NUMROW=ICNT
      DO2320I=1,NUMROW
        NTOT(I)=15
 2320 CONTINUE
C
      IFRST=.FALSE.
      ILAST=.FALSE.
      NCTITZ=0
      CALL DPDTA1(ITITLE,NCTITL,ITITLZ,NCTITZ,ITEXT,NCTEXT,
     1            AVALUE,IDIGIT,
     1            NTOT,NUMROW,
     1            ICAPSW,ICAPTY,ILAST,IFRST,
     1            ISUBRO,IBUGA3,IERROR)
C
C               *****************
C               **  STEP 90--  **
