C
      IF(IBUGA3.EQ.'ON'.OR.ISUBRO.EQ.'MLVM')THEN
        WRITE(ICOUT,999)
  999   FORMAT(1X)
        CALL DPWRST('XXX','BUG ')
        WRITE(ICOUT,51)
   51   FORMAT('**** AT THE BEGINNING OF DPMLVM--')
