      CHARACTER*4 INAME2(60)
      REAL AVAL(60)
C
      LOGICAL MLGEV
C
C---------------------------------------------------------------------
C
      DIMENSION XTEMP1(*)
C
C-----COMMON----------------------------------------------------------
C
      INCLUDE 'DPCOPA.INC'
