      EQUIVALENCE (DGARBG(IDGAR2),DTEMP2(1))
      EQUIVALENCE (DGARBG(IDGAR3),DTEMP3(1))
      EQUIVALENCE (DGARBG(IDGAR4),DTEMP4(1))
      EQUIVALENCE (DGARBG(IDGAR5),DTEMP5(1))
      EQUIVALENCE (DGARBG(IDGAR6),DTEMP6(1))
