C
        IDIST2='NEGATIVE BINOMIAL'
        CALL DPMLNB(Y,TEMP1,NS1,NUMV,
     1              AK,AKSV,PSV,
     1              TEMP2,TEMP3,TEMP4,DTEMP1,ITEMP1,MAXNXT,
     1              PMOM,AKMOM,PML,PMLBC,PMLBCV,AKML,
     1              PSE,AKSE,COV,
     1              AIC,AICC,BIC,ALIKE,
     1              ICAPSW,ICAPTY,IFORSW,
     1              IDIST2,
     1              ISUBRO,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        INAME1(1)='PMOM'
        INAME2(1)='    '
        AVAL(1)=PMOM
        INAME1(2)='PML '
        INAME2(2)='    '
        AVAL(2)=PML
        INAME1(3)='PMLV'
        INAME2(3)='AR  '
        AVAL(3)=PMLBCV
        INAME1(4)='PMLS'
        INAME2(4)='E   '
        AVAL(4)=PSE
        INAME1(5)='KMOM'
        INAME2(5)='    '
        AVAL(5)=AKMOM
        INAME1(6)='PMLB'
        INAME2(6)='C   '
        AVAL(6)=PMLBC
        INAME1(7)='KML '
        INAME2(7)='    '
        AVAL(7)=AKML
        INAME1(8)='KMLS'
        INAME2(8)='E   '
        AVAL(8)=AKSE
        INAME1(9)='COVP'
        INAME2(9)='K   '
        AVAL(9)=COV
        NPAR=9
        GOTO8100
C
      ELSEIF(ICASAN.EQ.'HYPG')THEN
C
        IOP='OPEN'
        IFLAG1=1
        IFLAG2=1
        IFLAG3=0
        IFLAG4=0
        IFLAG5=0
        CALL DPAUFI(IOP,IFLAG1,IFLAG2,IFLAG3,IFLAG4,IFLAG5,
     1              IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1              IBUGA3,ISUBRO,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        IDIST='HYPERGEOMETRIC'
        CALL DPMLHY(Y,NS1,ITEMP2,ITEMP1,
     1              XTEMP1,MAXNXT,
     1              ICAPSW,ICAPTY,IHYPTY,IOUNI1,
     1              ISUBRO,IBUGA3,IERROR)
C
        IOP='CLOS'
        CALL DPAUFI(IOP,IFLAG1,IFLAG2,IFLAG3,IFLAG4,IFLAG5,
     1              IOUNI1,IOUNI2,IOUNI3,IOUNI4,IOUNI5,
     1              IBUGA3,ISUBRO,IERROR)
C
      ELSEIF(ICASAN.EQ.'JOHN' .OR. ICASAN.EQ.'JOSB' .OR.
     1       ICASAN.EQ.'JOSU')THEN
C
        IHP='Z   '
        IHP2='    '
        IHWUSE='P'
        MESSAG='NO'
        CALL CHECKN(IHP,IHP2,IHWUSE,
     1  IHNAME,IHNAM2,IUSE,IN,IVALUE,VALUE,NUMNAM,MAXNAM,
     1  ISUBN1,ISUBN2,MESSAG,IANS,IWIDTH,ILOCP,IERROR)
        Z=0.524
        IF(IERROR.EQ.'NO')Z=VALUE(ILOCP)
        IF(Z.LT.0.1)Z=0.1
        IF(Z.GT.1.0)Z=1.0
C
        IF(ICASAN.EQ.'JOSB')THEN
          ICASE3='SB'
        ELSEIF(ICASAN.EQ.'JOSU')THEN
          ICASE3='SU'
        ELSE
          ICASE3='SU'
        ENDIF
C
        CALL DPMLJO(Y,TEMP1,TEMP2,NS1,NUMV,ICASE3,
     1              TEMP3,TEMP4,TEMP5,TEMP6,DTEMP1,MAXNXT,
     1              ALPHP1,ALPHP2,ALOCPE,SCALPE,IJOHN,Z,
     1              ALPHM1,ALPHM2,ALOCMO,SCALMO,
     1              ICAPSW,ICAPTY,IFORSW,IQUAME,
     1              ISUBRO,IBUGA3,IERROR)
C
        INAME1(1)='ALPH'
        INAME2(1)='A1PE'
        AVAL(1)=ALPHP1
        INAME1(2)='ALPH'
        INAME2(2)='A2PE'
        AVAL(2)=ALPHP2
        INAME1(3)='LOCP'
        INAME1(3)='ERC '
        AVAL(3)=ALOCPE
        INAME1(4)='SCAL'
        INAME2(4)='EPER'
        AVAL(4)=SCALPE
        INAME1(5)='ALPH'
        INAME2(5)='A1MO'
        AVAL(5)=ALPHM1
        INAME1(6)='ALPH'
        INAME2(6)='A2MO'
        AVAL(6)=ALPHM2
        INAME1(7)='LOCM'
        INAME1(7)='OM  '
        AVAL(7)=ALOCMO
        INAME1(8)='SCAL'
        INAME2(8)='EMOM'
        AVAL(8)=SCALMO
        INAME1(9)='JOHN'
        INAME2(9)='DIST'
        AVAL(9)=REAL(IJOHN)
        NPAR=9
        GOTO8900
C
      ELSEIF(ICASAN.EQ.'HERM')THEN
C
        IDIST='HERMITE'
        ALPHAM=0.0
        BETAMO=0.0
        ALPHML=0.0
        BETAML=0.0
        CALL DPMLHE(Y,TEMP1,NS1,NUMV,
     1              TEMP2,TEMP3,TEMP4,ITEMP1,MAXNXT,
     1              ALPHAM,BETAMO,ALPHML,BETAML,
     1              ALPHEP,BETAEP,ALPHZF,BETAZF,
     1              ICAPSW,ICAPTY,IFORSW,
     1              ISUBRO,IBUGA3,IERROR)
