        INAME1(1)='ALPH'
        INAME2(1)='AMOM'
        AVAL(1)=ALPHAM
        INAME1(2)='BETA'
        INAME2(2)='MOM '
        AVAL(2)=BETAMO
        INAME1(3)='ALPH'
        INAME2(3)='AML '
        AVAL(3)=ALPHML
        INAME1(4)='BETA'
        INAME2(4)='ML  '
        AVAL(4)=BETAML
        INAME1(5)='ALPH'
        INAME2(5)='AEP '
        AVAL(5)=ALPHEP
        INAME1(6)='BETA'
        INAME2(6)='EP  '
        AVAL(6)=BETAEP
        INAME1(7)='ALPH'
        INAME2(7)='AZF '
        AVAL(7)=ALPHZF
        INAME1(8)='BETA'
        INAME2(8)='ZF  '
        AVAL(8)=BETAZF
        NPAR=8
        GOTO8900
C
      ELSEIF(ICASAN.EQ.'YULE')THEN
C
        IDIST='YULE'
        PMOM=0.0
        PML=0.0
        CALL DPMLYU(Y,TEMP1,NS1,NUMV,
     1              TEMP2,TEMP3,TEMP4,MAXNXT,
     1              PMOM,PFREQ,PML,
CCCCC1              AICMO,AICCMO,BICMO,
CCCCC1              AICFR,AICCFR,BICFR,
CCCCC1              AICML,AICCML,BICML,
     1              ICAPSW,ICAPTY,IFORSW,
     1              ISUBRO,IBUGA3,IERROR)
C
        INAME1(1)='PMOM'
        INAME2(1)='    '
        AVAL(1)=PMOM
        INAME1(2)='PML '
        INAME2(2)='    '
        AVAL(2)=PML
CCCCC   INAME1(3)='PFRE'
CCCCC   INAME2(3)='Q   '
CCCCC   AVAL(3)=PFREQ
        NPAR=2
        GOTO8900
C
      ELSEIF(ICASAN.EQ.'ZETA')THEN
C
        IDIST='ZETA'
        ALPHMO=0.0
        ALPHML=0.0
        ALPHFR=0.0
        CALL DPMLZE(Y,TEMP1,NS1,NUMV,
     1              TEMP2,TEMP3,TEMP4,
     1              ALPHML,ALPHFR,ALPHMO,AFRVAR,AMLVAR,
     1              ICAPSW,ICAPTY,IFORSW,MAXNXT,
     1              ISUBRO,IBUGA3,IERROR)
C
        INAME1(1)='ALPH'
        INAME2(1)='AMOM'
        AVAL(1)=ALPHMO
        INAME1(2)='ALPH'
        INAME2(2)='AFR '
        AVAL(2)=ALPHFR
        INAME1(3)='AFRE'
        INAME2(3)='QVAR'
        AVAL(3)=AFRVAR
        INAME1(4)='ALPH'
        INAME2(4)='AML '
        AVAL(4)=ALPHML
        INAME1(5)='AMLV'
        INAME2(5)='AR  '
        AVAL(5)=AMLVAR
        NPAR=5
        GOTO8900
C
      ELSEIF(ICASAN.EQ.'WARI')THEN
C
        IDIST='WARING'
        AMOM=0.0
        AML=0.0
        CMOM=0.0
        CML=0.0
        CALL DPMLWA(Y,TEMP1,NS1,NUMV,
     1              DTEMP1,TEMP2,TEMP3,TEMP4,MAXNXT,
     1              AMOM,AFREQ,AML,CMOM,CFREQ,CML,
     1              ICAPSW,ICAPTY,IFORSW,
     1              ISUBRO,IBUGA3,IERROR)
C
        INAME1(1)='AMOM'
        INAME2(1)='    '
        AVAL(1)=AMOM
        INAME1(2)='AFRE'
        INAME2(2)='Q   '
        AVAL(2)=AFREQ
        INAME1(3)='AML '
        INAME2(3)='    '
        AVAL(3)=AML
        INAME1(4)='CMOM'
        INAME2(4)='    '
        AVAL(4)=CMOM
        INAME1(5)='CFRE'
        INAME2(5)='Q   '
        AVAL(5)=CFREQ
        INAME1(6)='CML '
        INAME2(6)='    '
        AVAL(6)=CML
        NPAR=6
        GOTO8900
C
      ELSEIF(ICASAN.EQ.'G5LO')THEN
