        CALL DPMLGL(Y,NS1,
     1              DTEMP1,DTEMP2,MAXNXT,
     1              ASHALM,ASCALM,ALOCLM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              ISUBRO,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
C
        INAME1(1)='GAMM'
        INAME2(1)='ALMO'
        AVAL(1)=ASHALM
        INAME1(2)='SCAL'
        INAME2(2)='ELMO'
        AVAL(2)=ASCALM
        INAME1(3)='LOCL'
        INAME2(3)='MOM '
        AVAL(3)=ALOCLM
        NPAR=3
        GOTO8900
C
      ELSEIF(ICASAN.EQ.'WAKE')THEN
C
        CALL DPMLWK(Y,NS1,
     1              DTEMP1,DTEMP2,MAXNXT,
     1              BETALM,GAMMLM,DELTLM,ALPHLM,CHILM,
     1              ICAPSW,ICAPTY,IFORSW,
     1              ISUBRO,IBUGA3,IERROR)
        IF(IERROR.EQ.'YES')GOTO9000
