        AVAL(2)=GAMMLM
        INAME1(3)='DELT'
        INAME2(3)='ALMO'
        AVAL(3)=DELTLM
        INAME1(4)='ALPH'
        INAME2(4)='ALMO'
        AVAL(4)=ALPHLM
        INAME1(5)='CHIL'
        INAME2(5)='MOM '
        AVAL(5)=CHILM
        NPAR=5
        GOTO8900
C
      ELSEIF(ICASAN.EQ.'PEA3')THEN
