        NUMPAT=NUMARG-1
        IF(NUMPAT.GT.MAXPAT)NUMPAT=MAXPAT
        DO1225I=1,NUMPAT
          J=I+1
          IHOLD1=IHARG(J)
          IHOLD2=IHOLD1
          IF(IHOLD1.EQ.'ON')IHOLD2='SOLI'
          IF(IHOLD1.EQ.'OFF')IHOLD2='    '
