        WRITE(ICOUT,999)
        CALL DPWRST('XXX','BUG ')
        DO1278I=1,NUMPAT
          WRITE(ICOUT,1276)I,IPATLI(I)
 1276     FORMAT('PATTERN LINE ',I6,' HAS JUST BEEN SET TO ',A4)
          CALL DPWRST('XXX','BUG ')
 1278   CONTINUE
